            }
        }

        function drawAxes() {
            stroke(0);
            line(0, height / 2, width, height / 2);
            line(150, 0, 150, height);
            textAlign(CENTER, CENTER);
            textSize(12);
            for (let i = 0; i <= 4; i++) {
                text(i, i * 150, height / 2 + 15);
            }
            for (let j = -1; j <= 1; j += 0.2) {
                text(j.toFixed(1), 135, map(j, -1, 1, height, 0));
            }
        }

        function drawString() {
            stroke(0);
            strokeWeight(2);
            line(0, height / 2, 150, map(currentDisplacements[0], -1, 1, height, 0));
            line(150, map(currentDisplacements[0], -1, 1, height, 0), 300, map(currentDisplacements[1], -1, 1, height, 0));
            line(300, map(currentDisplacements[1], -1, 1, height, 0), 450, map(currentDisplacements[2], -1, 1, height, 0));
            line(450, map(currentDisplacements[2], -1, 1, height, 0), 600, height / 2);
        }

        function drawMasses() {
            fill(255, 0, 0);
            noStroke();
            ellipse(150, map(currentDisplacements[0], -1, 1, height, 0), sqrt(masses[0]) * 30, sqrt(masses[0]) * 30);
            ellipse(300, map(currentDisplacements[1], -1, 1, height, 0), sqrt(masses[1]) * 30, sqrt(masses[1]) * 30);
            ellipse(450, map(currentDisplacements[2], -1, 1, height, 0), sqrt(masses[2]) * 30, sqrt(masses[2]) * 30);
        }

        function updateSimulation() {
            masses = [parseFloat(document.getElementById('slider-m1').value),
                      parseFloat(document.getElementById('slider-m2').value),
                      parseFloat(document.getElementById('slider-m3').value)];
            initialDisplacements = [parseFloat(document.getElementById('slider-x1').value),
                                    parseFloat(document.getElementById('slider-x2').value),
                                    parseFloat(document.getElementById('slider-x3').value)];
            tension = parseFloat(document.getElementById('slider-tension').value);

            const M = numeric.diag(masses);
            const K = numeric.mul(tension, [[2, -1, 0], [-1, 2, -1], [0, -1, 2]]);
            const Minv = numeric.inv(M);
            const C = numeric.dot(numeric.dot(numeric.sqrt(Minv), K), numeric.sqrt(Minv));
            const eigResult = numeric.eig(C);
            omega = numeric.sqrt(eigResult.lambda.x);
            eigenvectors = eigResult.E.x;

            coefficients = numeric.dot(eigenvectors, numeric.inv(eigenvectors)).map((col, i) => {
                return col.reduce((sum, val, j) => sum + val * initialDisplacements[j], 0);
            });

            t = 0;
            updatePlots();
            if (!isAnimating) {
                redraw();
            }
        }

        function updatePlots() {
            const timeSteps = Array.from({ length: 250 }, (_, i) => i * dt);
            const displacements = timeSteps.map(t => {
                return eigenvectors.map((vec, i) => coefficients[i] * vec.reduce((sum, val, j) => sum + val * Math.cos(omega[j] * t), 0));
            });
            const velocities = timeSteps.map(t => {
                return eigenvectors.map((vec, i) => -coefficients[i] * omega[i] * vec.reduce((sum, val, j) => sum + val * Math.sin(omega[j] * t), 0));
            });

            const displacementTraces = [
                { x: timeSteps, y: displacements.map(d => d[0]), mode: 'lines', name: 'm₁', line: { color: 'blue' } },
                { x: timeSteps, y: displacements.map(d => d[1]), mode: 'lines', name: 'm₂', line: { color: 'orange' } },
                { x: timeSteps, y: displacements.map(d => d[2]), mode: 'lines', name: 'm₃', line: { color: 'green' } }
            ];
            const velocityTraces = [
                { x: timeSteps, y: velocities.map(v => v[0]), mode: 'lines', name: 'm₁', line: { color: 'blue' } },
                { x: timeSteps, y: velocities.map(v => v[1]), mode: 'lines', name: 'm₂', line: { color: 'orange' } },
                { x: timeSteps, y: velocities.map(v => v[2]), mode: 'lines', name: 'm₃', line: { color: 'green' } }
            ];

            const layout = {
                xaxis: { title: 'time t' },
                yaxis: { title: 'displacement' },
                shapes: [{
                    type: 'line',
                    x0: t,
                    y0: -1.0,
                    x1: t,
                    y1: 1.0,
                    line: { color: 'red', width: 2, dash: 'dashdot' }
                }]
            };

            Plotly.newPlot('displacement-plot-container', displacementTraces, layout);
            Plotly.newPlot('velocity-plot-container', velocityTraces, { ...layout, yaxis: { title: 'velocity' } });
        }

        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', () => {
                updateSimulation();
            });
        });

        document.getElementById('btn-play-pause').addEventListener('click', () => {
            isAnimating = !isAnimating;
            if (isAnimating) {
                loop();
                document.getElementById('btn-play-pause').textContent = '❚❚';
            } else {
                noLoop();
                document.getElementById('btn-play-pause').textContent = '▶';
            }
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            updateSimulation();
        });

        Object.defineProperty(this, 'currentDisplacements', {
            get: function() {
                return eigenvectors.map((vec, i) => coefficients[i] * vec.reduce((sum, val, j) => sum + val * Math.cos(omega[j] * t), 0));
            }
        });
    </script>
</body>
</html>
