    <script>
        let mode = 'move';
        let points = [{x: 150, y: 350}, {x: 250, y: 150}, {x: 350, y: 250}, {x: 550, y: 180}, {x: 480, y: 320}, {x: 420, y: 400}].map(p => createVector(p.x, p.y));
