        document.getElementById('btn-step2-explain').addEventListener('click', () => {
            algorithmState.explanationSteps.push("Step 2. Find the point clockwise furthest to the right relative to the leftmost point.");
            document.getElementById('explanation-text').innerText = algorithmState.explanationSteps.join('\n');
        });

        document.getElementById('btn-step2-do').addEventListener('click', () => {
            algorithmState.candidateLines = algorithmState.hullPointsIndices.map(i => [i, ...points.map((_, idx) => idx).filter(idx => !algorithmState.hullPointsIndices.includes(idx))]);
            algorithmState.currentPointIndex = algorithmState.fullHull[1];
            algorithmState.hullPointsIndices.push(algorithmState.currentPointIndex);
            draw();
            algorithmState.candidateLines = [];
            document.getElementById('step2-controls-container').querySelectorAll('button').forEach(btn => btn.disabled = true);
            document.getElementById('step3-controls-container').querySelectorAll('button').forEach(btn => btn.disabled = false);
        });

        document.getElementById('btn-step3-explain').addEventListener('click', () => {
            algorithmState.explanationSteps.push("Step 3. Repeat finding the point clockwise furthest to the right relative to the previously found point, until the leftmost point is reached again.");
            document.getElementById('explanation-text').innerText = algorithmState.explanationSteps.join('\n');
        });

        function step3DoHandler() {
            const nextIdx = algorithmState.fullHull[algorithmState.hullPointsIndices.length];
            if (nextIdx !== undefined) {
                algorithmState.candidateLines = algorithmState.hullPointsIndices.map(i => [i, ...points.map((_, idx) => idx).filter(idx => !algorithmState.hullPointsIndices.includes(idx))]);
                draw();
                setTimeout(() => {
                    algorithmState.currentPointIndex = nextIdx;
                    algorithmState.hullPointsIndices.push(algorithmState.currentPointIndex);
                    algorithmState.candidateLines = [];
                    draw();
                    document.getElementById(`btn-step3-do`).disabled = true;
                    if (document.getElementById(`btn-step3-repeat-${algorithmState.hullPointsIndices.length - 2}`)) {
                        document.getElementById(`btn-step3-repeat-${algorithmState.hullPointsIndices.length - 2}`).disabled = false;
                    }
                }, 500);
            }
        }

        function jarvisMarch(points) {
            let hull = [];
            let leftmost = points.reduce((minIdx, p, idx) => p.x < points[minIdx].x ? idx : minIdx, 0);
            let current = leftmost;
            do {
                hull.push(current);
                let next = (current + 1) % points.length;
                for (let i = 0; i < points.length; i++) {
                    if (orientation(points[current], points[i], points[next]) < 0) {
                        next = i;
                    }
                }
                current = next;
            } while (current !== leftmost);
            return hull;
        }

        function orientation(p1, p2, p3) {
            return (p2.x - p1.x) * (p3.y - p1.y) - (p2.y - p1.y) * (p3.x - p1.x);
        }
    </script>
