    <div id="visualization">
        <div id="plot-top"></div>
        <div id="plot-bottom"></div>
    </div>

    <script>
        const initialConditions = {
            X: 0.1,
            Glu: 60,
            Eta: 0,
            E0: 1.0,
            E1: 0.0,
            E2: 0.0,
            V: 1.0
        };

        const params = {
            alphaMax: 0.3,
            tMin: 0,
            tMax: 60,
            enzymeControl: 1,
            feedRate: 0.2,
            substrateFeedConc: 100,
            feedTime: 35
        };

        const n = 4;
        const kI = 10;

        function hillFunction(alphaMax, kI, n, Glu) {
            return alphaMax * Math.pow(kI, n) / (Math.pow(kI, n) + Math.pow(Glu, n));
        }

        function rk4Step(f, y, t, h) {
            const k1 = f(y, t);
            const k2 = f([y[0] + h * k1[0] / 2, y[1] + h * k1[1] / 2, y[2] + h * k1[2] / 2, y[3] + h * k1[3] / 2, y[4] + h * k1[4] / 2, y[5] + h * k1[5] / 2], t + h / 2);
            const k3 = f([y[0] + h * k2[0] / 2, y[1] + h * k2[1] / 2, y[2] + h * k2[2] / 2, y[3] + h * k2[3] / 2, y[4] + h * k2[4] / 2, y[5] + h * k2[5] / 2], t + h / 2);
            const k4 = f([y[0] + h * k3[0], y[1] + h * k3[1], y[2] + h * k3[2], y[3] + h * k3[3], y[4] + h * k3[4], y[5] + h * k3[5]], t + h);
            return [
                y[0] + h * (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]) / 6,
                y[1] + h * (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1]) / 6,
                y[2] + h * (k1[2] + 2 * k2[2] + 2 * k3[2] + k4[2]) / 6,
                y[3] + h * (k1[3] + 2 * k2[3] + 2 * k3[3] + k4[3]) / 6,
                y[4] + h * (k1[4] + 2 * k2[4] + 2 * k3[4] + k4[4]) / 6,
                y[5] + h * (k1[5] + 2 * k2[5] + 2 * k3[5] + k4[5]) / 6
            ];
        }

        function odes(y, t) {
            const [X, Glu, Eta, E0, E1, E2] = y;
            const alpha = hillFunction(params.alphaMax, kI, n, Glu);
            const muR = 0.5; // Respiratory growth rate
            const muF = 0.3; // Fermentative growth rate
            const YXG = 0.5; // Yield coefficient for biomass from glucose
            const YXE = 0.2; // Yield coefficient for biomass from ethanol
            const YEG = 0.4; // Yield coefficient for ethanol from glucose
            const qE0 = 0.1; // Production rate of E0
            const qE1 = 0.2; // Production rate of E1
            const qE2 = 0.15; // Production rate of E2
            const dE0dt = qE0 * X - 0.01 * E0;
            const dE1dt = qE1 * X * Glu - 0.01 * E1;
            const dE2dt = qE2 * X * (1 - Glu / (kI + Glu)) - 0.01 * E2;
            const F = t <= params.feedTime ? params.feedRate : 0;
            const G_in = t <= params.feedTime ? params.substrateFeedConc : 0;
            const dVdt = F;
            const dXdt = alpha * muR * X + (1 - alpha) * muF * X - F * X / params.V;
            const dGludt = -YXG * alpha * muR * X - YXE * (1 - alpha) * muF * X + F * (G_in - Glu) / params.V;
            const dEtadt = YXE * (1 - alpha) * muF * X + F * (-Eta) / params.V;
            return [dXdt, dGludt, dEtadt, dE0dt, dE1dt, dE2dt];
        }

        function solveOde() {
            const tValues = [];
            const XValues = [];
            const GluValues = [];
            const EtaValues = [];
            const E0Values = [];
            const E1Values = [];
            const E2Values = [];
            const alphaValues = [];

            let t = params.tMin;
            let y = [initialConditions.X, initialConditions.Glu, initialConditions.Eta, initialConditions.E0, initialConditions.E1, initialConditions.E2];

            const h = 0.1;
            while (t <= params.tMax) {
                tValues.push(t);
                XValues.push(y[0]);
                GluValues.push(y[1]);
                EtaValues.push(y[2]);
                E0Values.push(y[3]);
                E1Values.push(y[4]);
                E2Values.push(y[5]);
                alphaValues.push(hillFunction(params.alphaMax, kI, n, y[1]));
                y = rk4Step(odes, y, t, h);
                t += h;
            }

            return { tValues, XValues, GluValues, EtaValues, E0Values, E1Values, E2Values, alphaValues };
        }

        function drawPlots(data) {
            const plotType = document.getElementById('select-plot').value;
            const layoutTop = {
                title: plotType === 'fed batch' ? 'diauxic behavior' : 'glucose concentration',
                xaxis: { title: 'time (h)' },
                yaxis: { title: plotType === 'fed batch' ? 'concentration [mol/m³]' : 'glucose concentration [mol/m³]' },
                showlegend: true,
                legend: { orientation: 'h', yanchor: 'bottom', y: 1.02, xanchor: 'right', x: 1 }
            };
            const layoutBottom = {
                title: plotType === 'fed batch' ? 'enzyme concentration' : 'fraction of respiring biomass',
                xaxis: { title: 'time (h)' },
                yaxis: { title: plotType === 'fed batch' ? 'enzyme concentration [mol/m³]' : 'α' },
                showlegend: true,
                legend: { orientation: 'h', yanchor: 'bottom', y: 1.02, xanchor: 'right', x: 1 }
            };

            let tracesTop, tracesBottom;
            if (plotType === 'fed batch') {
                tracesTop = [
                    { x: data.tValues, y: data.XValues, mode: 'lines', name: 'X(t)', line: { color: 'blue' } },
                    { x: data.tValues, y: data.GluValues, mode: 'lines', name: 'Glu(t)', line: { color: 'red' } },
                    { x: data.tValues, y: data.EtaValues, mode: 'lines', name: 'Eta(t)', line: { color: 'green' } }
                ];
                tracesBottom = [
                    { x: data.tValues, y: data.E2Values, mode: 'lines', name: 'E₂(t)', line: { color: 'black' } },
                    { x: data.tValues, y: data.E1Values, mode: 'lines', name: 'E₁(t)', line: { color: 'magenta' } },
                    { x: data.tValues, y: data.E0Values, mode: 'lines', name: 'E₀(t)', line: { color: 'orange' } }
                ];
            } else {
                tracesTop = [
                    { x: data.tValues, y: data.GluValues, mode: 'lines', name: 'Glu(t)', line: { color: 'red' } }
                ];
                tracesBottom = [
                    { x: data.tValues, y: data.alphaValues, mode: 'lines', name: 'α', line: { color: 'blue' } }
                ];
            }

            Plotly.newPlot('plot-top', tracesTop, layoutTop);
            Plotly.newPlot('plot-bottom', tracesBottom, layoutBottom);
        }

        function updateUI() {
            document.getElementById('value-slider-alpha-max').textContent = params.alphaMax.toFixed(2);
            document.getElementById('value-slider-t-min').textContent = params.tMin;
            document.getElementById('value-slider-t-max').textContent = params.tMax;
            document.getElementById('value-slider-enzyme-control').textContent = params.enzymeControl.toFixed(2);
            document.getElementById('value-slider-feed-rate').textContent = params.feedRate.toFixed(2);
            document.getElementById('value-slider-substrate-feed-conc').textContent = params.substrateFeedConc;
            document.getElementById('value-slider-feed-time').textContent = params.feedTime;

            const feedTimeSlider = document.getElementById('slider-feed-time');
            feedTimeSlider.max = params.tMax;
            if (params.feedTime > params.tMax) {
                params.feedTime = params.tMax;
                feedTimeSlider.value = params.tMax;
                document.getElementById('value-slider-feed-time').textContent = params.tMax;
            }
        }

        function onSliderChange(event) {
            const id = event.target.id;
            params[id.replace('slider-', '')] = parseFloat(event.target.value);
            updateUI();
            const data = solveOde();
            drawPlots(data);
        }

        function onSelectChange() {
            const data = solveOde();
            drawPlots(data);
        }

        document.addEventListener('DOMContentLoaded', () => {
            document.querySelectorAll('input[type="range"]').forEach(slider => {
                slider.addEventListener('input', onSliderChange);
            });
            document.getElementById('select-plot').addEventListener('change', onSelectChange);

            updateUI();
            const data = solveOde();
            drawPlots(data);
        });
    </script>
</body>
</html>
