    <div id="main-container">
        <h1>Unsteady-State Diffusion in a Slab</h1>
        <p>This demo visualizes the unsteady-state diffusion of a solute in a slab. Adjust the time and diffusivity to see how they affect the concentration profile.</p>
        <div id="control-panel">
            <div class="control-row">
                <label for="time-slider">time</label>
                <input type="range" id="time-slider" min="0" max="2500" step="1" value="100">
                <span id="time-value">100.0</span>
            </div>
            <div class="control-row">
                <label for="diffusivity-slider">diffusivity in m<sup>2</sup>/s</label>
                <input type="range" id="diffusivity-slider" min="1e-10" max="1e-8" step="1e-10" value="4e-9">
                <span id="diffusivity-value">4.0 x 10⁻⁹</span>
            </div>
        </div>
        <div id="plot-div"></div>
    </div>

    <script>
        const timeSlider = document.getElementById('time-slider');
        const timeValue = document.getElementById('time-value');
        const diffusivitySlider = document.getElementById('diffusivity-slider');
        const diffusivityValue = document.getElementById('diffusivity-value');
        const plotDiv = document.getElementById('plot-div');

        let dataRevision = 0;

        function formatScientific(num) {
            return num.toExponential(1).replace('e+', ' x 10<sup>').replace('e-', ' x 10⁻') + '</sup>';
        }

        function updatePlot() {
            const t = parseFloat(timeSlider.value);
            const D = parseFloat(diffusivitySlider.value);
            const L = 0.004;
            const c_s = 4.0e-3;
            const N = 100;
            const x = Array.from({ length: 200 }, (_, i) => i * L / 199);
            let c = [];

            if (t === 0) {
                c = x.map(xi => (1.0 + 250 * xi) * 1e-3);
            } else {
                c = x.map(xi => {
                    let u = 0;
                    for (let n = 0; n < N; n++) {
                        const k_n = ((n + 0.5) * Math.PI) / L;
                        const B_n = (2 / L) * 1e-3 * ((250 * Math.pow(-1, n)) / Math.pow(k_n, 2) - 3 / k_n);
                        u += B_n * Math.sin(k_n * xi) * Math.exp(-Math.pow(k_n, 2) * D * t);
                    }
                    return u + c_s;
                });
            }

            const trace = {
                x: x,
                y: c,
                type: 'scatter',
                mode: 'lines',
                name: 'Concentration Profile'
            };

            const layout = {
                xaxis: {
                    title: 'position',
                    range: [0, 0.004],
                    tickformat: '.3f',
                    dtick: 0.001,
                    showgrid: true,
                    minor: { showgrid: true }
                },
                yaxis: {
                    title: 'concentration',
                    range: [0.001, 0.0042],
                    tickformat: '.4f',
                    dtick: 0.0005,
                    showgrid: true,
                    minor: { showgrid: true }
                },
                margin: { l: 80, r: 20, t: 20, b: 50 },
                datarevision: dataRevision++
            };

            Plotly.react(plotDiv, [trace], layout);
        }

        timeSlider.addEventListener('input', () => {
            timeValue.textContent = parseFloat(timeSlider.value).toFixed(1);
            updatePlot();
        });

        diffusivitySlider.addEventListener('input', () => {
            diffusivityValue.innerHTML = formatScientific(parseFloat(diffusivitySlider.value));
            updatePlot();
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
