    <script>
        let state = { funcKey: 'x', type: 'right', a: 5, n: 3 };
        let functions = {
            x: x => x,
            x_minus_3_squared: x => Math.pow(x - 3, 2),
            x_minus_3_cubed_plus_27: x => Math.pow(x - 3, 3) + 27,
            sqrt_x: x => Math.sqrt(x),
            sin_x_plus_1: x => Math.sin(x) + 1,
            exp_x_over_3: x => Math.exp(x / 3)
        };
        let integrals = {
            x: a => a * a / 2,
            x_minus_3_squared: a => a * a * a / 3 - 3 * a * a + 9 * a - 27,
            x_minus_3_cubed_plus_27: a => (Math.pow(a, 4) / 4 - 9 * Math.pow(a, 3) / 3 + 81 * a / 2 + 27 * a),
            sqrt_x: a => (2 / 3) * Math.pow(a, 3 / 2),
            sin_x_plus_1: a => -Math.cos(a) + a,
            exp_x_over_3: a => 3 * (Math.exp(a / 3) - 1)
        };

        function setup() {
            createCanvas(windowWidth * 0.9, 600).parent('canvas-container');
            document.querySelectorAll('.function-btn').forEach(btn => btn.addEventListener('click', handleFunctionClick));
            document.querySelectorAll('.type-btn').forEach(btn => btn.addEventListener('click', handleTypeClick));
            document.getElementById('slider-a').addEventListener('input', handleSliderAChange);
            document.getElementById('slider-n').addEventListener('input', handleSliderNChange);
            draw();
        }

        function draw() {
            background(248, 248, 248);
            let xMin = -1;
            let xMax = 8.5;
            let yMin = Infinity;
            let yMax = -Infinity;

            for (let x = xMin; x <= xMax; x += 0.01) {
                let y = functions[state.funcKey](x);
                if (y < yMin) yMin = y;
                if (y > yMax) yMax = y;
            }

            yMin -= 0.5;
            yMax += 0.5;

            let scaleX = width / (xMax - xMin);
            let scaleY = height / (yMax - yMin);

            stroke('#cccccc');
            line(0, height - (0 - yMin) * scaleY, width, height - (0 - yMin) * scaleY);
            line((0 - xMin) * scaleX, 0, (0 - xMin) * scaleX, height);

            for (let x = xMin; x <= xMax; x += 1) {
                if (x !== 0) {
                    line((x - xMin) * scaleX, height - (0 - yMin) * scaleY + 5, (x - xMin) * scaleX, height - (0 - yMin) * scaleY - 5);
                    text(x, (x - xMin) * scaleX, height - (0 - yMin) * scaleY + 20);
                }
            }

            for (let y = yMin; y <= yMax; y += 1) {
                if (y !== 0) {
                    line((0 - xMin) * scaleX - 5, height - (y - yMin) * scaleY, (0 - xMin) * scaleX + 5, height - (y - yMin) * scaleY);
                    text(y.toFixed(1), (0 - xMin) * scaleX - 25, height - (y - yMin) * scaleY);
                }
            }

            stroke(0);
            strokeWeight(2);
            beginShape();
            for (let x = xMin; x <= xMax; x += 0.01) {
                let y = functions[state.funcKey](x);
                vertex((x - xMin) * scaleX, height - (y - yMin) * scaleY);
            }
            endShape();

            let deltaX = state.a / state.n;
            let estimatedArea = 0;
            let trueIntegral = integrals[state.funcKey](state.a);

            for (let i = 0; i < state.n; i++) {
                let x1 = i * deltaX;
                let x2 = (i + 1) * deltaX;
                let y1 = functions[state.funcKey](x1);
                let y2 = functions[state.funcKey](x2);
                let midY = functions[state.funcKey]((x1 + x2) / 2);

                let x1Px = (x1 - xMin) * scaleX;
                let x2Px = (x2 - xMin) * scaleX;
                let y1Px = height - (y1 - yMin) * scaleY;
                let y2Px = height - (y2 - yMin) * scaleY;
                let midYPx = height - (midY - yMin) * scaleY;

                let shapeArea;
                let color;

                switch (state.type) {
                    case 'left':
                        shapeArea = deltaX * y1;
                        color = shapeArea > trueIntegral * (x2 - x1) / state.a ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                        fill(color);
                        rect(x1Px, y1Px, deltaX * scaleX, height - y1Px);
                        break;
                    case 'right':
                        shapeArea = deltaX * y2;
                        color = shapeArea > trueIntegral * (x2 - x1) / state.a ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                        fill(color);
                        rect(x1Px, y2Px, deltaX * scaleX, height - y2Px);
                        break;
                    case 'midpoint':
                        shapeArea = deltaX * midY;
                        color = shapeArea > trueIntegral * (x2 - x1) / state.a ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                        fill(color);
                        rect(x1Px, midYPx, deltaX * scaleX, height - midYPx);
                        break;
                    case 'trapezoidal':
                        shapeArea = deltaX * (y1 + y2) / 2;
                        color = shapeArea > trueIntegral * (x2 - x1) / state.a ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                        fill(color);
                        quad(x1Px, y1Px, x2Px, y2Px, x2Px, height, x1Px, height);
                        break;
                    case 'overestimate':
                        let maxY = Math.max(y1, y2);
                        shapeArea = deltaX * maxY;
                        color = shapeArea > trueIntegral * (x2 - x1) / state.a ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                        fill(color);
                        rect(x1Px, height - maxY * scaleY, deltaX * scaleX, maxY * scaleY);
                        break;
                    case 'underestimate':
                        let minY = Math.min(y1, y2);
                        shapeArea = deltaX * minY;
                        color = shapeArea > trueIntegral * (x2 - x1) / state.a ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)';
                        fill(color);
                        rect(x1Px, height - minY * scaleY, deltaX * scaleX, minY * scaleY);
                        break;
                }

                estimatedArea += shapeArea;
            }

            stroke('red');
            strokeWeight(2);
            line(state.a * scaleX, 0, state.a * scaleX, height);

            noStroke();
            fill(0);
            textSize(16);
            textAlign(CENTER);
            text(`estimated area: ${estimatedArea.toFixed(5)}`, width / 2, 20);
            text(`integral: ${trueIntegral.toFixed(5)}`, width / 2, 40);
        }

        function handleFunctionClick(event) {
            document.querySelectorAll('.function-btn').forEach(btn => btn.classList.remove('active'));
            event.target.classList.add('active');
            state.funcKey = event.target.getAttribute('data-function');
            draw();
        }

        function handleTypeClick(event) {
            document.querySelectorAll('.type-btn').forEach(btn => btn.classList.remove('active'));
            event.target.classList.add('active');
            state.type = event.target.getAttribute('data-type');
            draw();
        }

        function handleSliderAChange(event) {
            state.a = parseFloat(event.target.value);
            document.getElementById('value-a').textContent = state.a.toFixed(2);
            draw();
        }

        function handleSliderNChange(event) {
            state.n = parseInt(event.target.value, 10);
            document.getElementById('value-n').textContent = state.n;
            draw();
        }
    </script>
</body>
</html>
