            cursor: pointer;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-current">current (A)</label>
                <input type="range" id="slider-current" min="0" max="1" step="0.001" value="0.431">
                <span id="value-current">0.431</span>
            </div>
            <div class="slider-group">
                <label for="slider-earth-field">horizontal Earth field (T)</label>
                <input type="range" id="slider-earth-field" min="0" max="0.0001" step="0.0000001" value="0.0000194">
                <span id="value-earth-field">1.94e-5</span>
            </div>
            <div class="slider-group">
                <label for="slider-radius">radius (m)</label>
                <input type="range" id="slider-radius" min="0.1" max="1.0" step="0.01" value="0.5">
                <span id="value-radius">0.5</span>
            </div>
            <div class="slider-group">
                <label for="slider-turns">number of turns</label>
                <input type="range" id="slider-turns" min="1" max="100" step="1" value="25">
                <span id="value-turns">25</span>
            </div>
            <button id="btn-reset">↻</button>
        </div>
        <div id="viz-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        const mu0 = 4 * Math.PI * Math.pow(10, -7); // permeability of free space

        let current = 0.431;
        let earthField = 0.0000194;
        let radius = 0.5;
        let turns = 25;

        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('viz-container');
            updateValues();
            draw();
        }

        function draw() {
            background(255);
            translate(width / 2, height / 2);
            scale(100);

            drawAxes();

            const gridSpacing = 0.1;
            let minMag = Infinity;
            let maxMag = -Infinity;

            for (let y = -0.5; y <= 0.5; y += gridSpacing) {
                for (let z = -0.5; z <= 0.5; z += gridSpacing) {
                    const bTotal = calculateTotalField(y, z);
                    const mag = mag(bTotal.x, bTotal.y);
                    minMag = Math.min(minMag, mag);
                    maxMag = Math.max(maxMag, mag);
                }
            }

            for (let y = -0.5; y <= 0.5; y += gridSpacing) {
                for (let z = -0.5; z <= 0.5; z += gridSpacing) {
                    drawStreamline(y, z, minMag, maxMag);
                }
            }
        }

        function drawAxes() {
            stroke(255, 0, 0);
            strokeWeight(0.5);
            line(-5, 0, 5, 0); // Y-axis
            line(0, -5, 0, 5); // Z-axis

            for (let i = -0.5; i <= 0.5; i += 0.2) {
                line(i, -0.02, i, 0.02); // Y-axis ticks
                line(-0.02, i, 0.02, i); // Z-axis ticks
                fill(255, 0, 0);
                textSize(0.1);
                textAlign(CENTER, CENTER);
                text(i.toFixed(1), i, -0.05); // Y-axis labels
                text(i.toFixed(1), -0.05, i); // Z-axis labels
            }

            fill(255, 0, 0);
            textSize(0.2);
            text("Y", 5.2, 0);
            text("Z", 0, 5.2);
        }

        function drawStreamline(y, z, minMag, maxMag) {
            const bTotal = calculateTotalField(y, z);
            const mag = mag(bTotal.x, bTotal.y);
            if (mag === 0) return;

            const hue = map(mag, minMag, maxMag, 270, 60);
            stroke(hue, 255, 255);
            strokeWeight(0.01);

            let p = createVector(y, z);
            let steps = 100;
            let stepSize = 0.01;
            let arrowInterval = 10;

            for (let i = 0; i < steps; i++) {
                const bTotal = calculateTotalField(p.x, p.y);
                const mag = mag(bTotal.x, bTotal.y);
                if (mag === 0) break;

                const dir = p5.Vector.div(bTotal, mag);
                const pNext = p.copy().add(dir.mult(stepSize));

                if (!isInBounds(pNext)) break;

                line(p.x, p.y, pNext.x, pNext.y);

                if (i % arrowInterval === 0) {
                    drawArrow(p, dir);
                }

                p = pNext;
            }

            p = createVector(y, z);
            for (let i = 0; i < steps; i++) {
                const bTotal = calculateTotalField(p.x, p.y);
                const mag = mag(bTotal.x, bTotal.y);
                if (mag === 0) break;

                const dir = p5.Vector.div(bTotal, mag).mult(-1);
                const pNext = p.copy().add(dir.mult(stepSize));

                if (!isInBounds(pNext)) break;

                line(p.x, p.y, pNext.x, pNext.y);

                if (i % arrowInterval === 0) {
                    drawArrow(p, dir);
                }

                p = pNext;
            }
        }

        function drawArrow(p, dir) {
            push();
            translate(p.x, p.y);
            rotate(dir.heading());
            line(0, 0, 0.05, 0);
            line(0.05, 0, 0.04, -0.01);
            line(0.05, 0, 0.04, 0.01);
            pop();
        }

        function isInBounds(p) {
            return p.x >= -0.5 && p.x <= 0.5 && p.y >= -0.5 && p.y <= 0.5;
        }

        function calculateTotalField(y, z) {
            const bEarth = createVector(0, earthField);
            const bCoils = calculateCoilField(y, z);
            return p5.Vector.add(bEarth, bCoils);
        }

        function calculateCoilField(y, z) {
            const b1 = calculateFieldFromLoop(y, z, -radius / 2);
            const b2 = calculateFieldFromLoop(y, z, radius / 2);
            return p5.Vector.add(b1, b2);
        }

        function calculateFieldByLoop(y, z, zOffset) {
            const zp = z - zOffset;
            const m = (4 * radius * y) / ((radius + y) ** 2 + zp ** 2);
            const commonFactor1 = (mu0 * current * turns) / (2 * Math.PI);
            const commonFactor2 = 1 / Math.sqrt((radius + y) ** 2 + zp ** 2);
            const k = ellipticK(m);
            const e = ellipticE(m);
            const by = (y !== 0) ? commonFactor1 * (zp / y) * commonFactor2 * (((radius ** 2 + y ** 2 + zp ** 2) / ((radius - y) ** 2 + zp ** 2)) * e - k) : 0;
            const bz = commonFactor1 * commonFactor2 * (k - ((radius ** 2 - y ** 2 - zp ** 2) / ((radius - y) ** 2 + zp ** 2)) * e);
            return createVector(by, bz);
        }

        function ellipticK(m) {
            if (m === 1) return Infinity;
            let a = 1;
            let b = Math.sqrt(1 - m);
            while (Math.abs(a - b) > 1e-15) {
                const aNext = (a + b) / 2;
                const bNext = Math.sqrt(a * b);
                a = aNext;
                b = bNext;
            }
            return Math.PI / (2 * a);
        }

        function ellipticE(m) {
            if (m === 1) return 1;
            let a = 1;
            let b = Math.sqrt(1 - m);
            let c = Math.sqrt(m);
            let s = 1;
            let e = 1;
            while (Math.abs(c) > 1e-15) {
                const aNext = (a + b) / 2;
                const bNext = Math.sqrt(a * b);
                const cNext = c / 2;
                s -= cNext * cNext;
                a = aNext;
                b = bNext;
                c = cNext;
                e *= 2;
            }
            return Math.PI * (a + b * b / (4 * s)) / (2 * e);
        }

        function updateValues() {
            document.getElementById('value-current').textContent = current.toFixed(3);
            document.getElementById('value-earth-field').textContent = earthField.toExponential(3);
            document.getElementById('value-radius').textContent = radius.toFixed(2);
            document.getElementById('value-turns').textContent = turns;
        }

        document.getElementById('slider-current').addEventListener('input', (event) => {
            current = parseFloat(event.target.value);
            updateValues();
            draw();
        });

        document.getElementById('slider-earth-field').addEventListener('input', (event) => {
            earthField = parseFloat(event.target.value);
            updateValues();
            draw();
        });

        document.getElementById('slider-radius').addEventListener('input', (event) => {
            radius = parseFloat(event.target.value);
            updateValues();
            draw();
        });

        document.getElementById('slider-turns').addEventListener('input', (event) => {
            turns = parseInt(event.target.value);
            updateValues();
            draw();
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            document.getElementById('slider-current').value = 0.431;
            document.getElementById('slider-earth-field').value = 0.0000194;
            document.getElementById('slider-radius').value = 0.5;
            document.getElementById('slider-turns').value = 25;
            current = 0.431;
            earthField = 0.0000194;
            radius = 0.5;
            turns = 25;
            updateValues();
            draw();
        });
    </script>
</body>
</html>
