    <script>
        const WORD_LIST = ['THE', 'IDES', 'OF', 'MARCH', 'ARE', 'COME', 'EVEN', 'YOU', 'BRUTUS', 'CAESAR', 'CIPHER', 'HELLO', 'WORLD', 'SECRET', 'MESSAGE', 'ATTACK', 'DAWN', 'I', 'A'].sort((a, b) => b.length - a.length);
        let currentMessage = "";
        let currentCiphertext = "";

        document.getElementById('btn-toggle-encoding').addEventListener('click', () => {
            document.getElementById('panel-encoding').style.display = 'block';
            document.getElementById('panel-decoding').style.display = 'none';
