<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Applying Graphics Filters Using Convolution</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 20px;
        }
        #control-panel {
            border: 1px solid #ccc;
            padding: 10px;
            margin-bottom: 20px;
        }
        #control-panel > div {
            margin-bottom: 10px;
        }
        #visualization-area {
            border: 1px solid #ccc;
            padding: 10px;
            display: flex;
            justify-content: space-between;
        }
        #canvas-before-container, #canvas-after-container {
            width: 200px;
            height: 200px;
            display: inline-block;
            vertical-align: top;
        }
        #canvas-before-container canvas, #canvas-after-container canvas {
            display: block;
        }
        .radio-group label {
            display: inline-block;
            padding: 5px 10px;
            cursor: pointer;
            border: 1px solid #ccc;
            border-radius: 4px;
            margin-right: 5px;
        }
        .radio-group input[type="radio"]:checked + label {
            background-color: #007bff;
            color: white;
            border-color: #007bff;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <h1>Applying Graphics Filters Using Convolution</h1>
    <p>This demo allows you to apply different convolution filters to a graphical representation of a selected letter.</p>

    <div id="control-panel">
        <div>
            <label for="slider-convolver">convolver</label>
            <button onclick="document.getElementById('slider-convolver').stepDown(); updateVisualization();">-</button>
            <input type="range" id="slider-convolver" min="1" max="5" step="1" value="2">
            <button id="convolver-plus-btn" onclick="document.getElementById('slider-convolver').stepUp(); updateVisualization();">+</button>
        </div>
        <div>
            <label for="slider-image-size">image size</label>
            <button onclick="document.getElementById('slider-image-size').stepDown(); updateVisualization();">-</button>
            <input type="range" id="slider-image-size" min="10" max="40" step="1" value="24">
            <button id="image-size-plus-btn" onclick="document.getElementById('slider-image-size').stepUp(); updateVisualization();">+</button>
        </div>
        <div>
            <label for="input-letter">pick a letter:</label>
            <input type="text" id="input-letter" maxlength="1" value="a" onkeydown="handleLetterKey(event)">
        </div>
        <div class="radio-group">
            <label>filter</label>
            <input type="radio" name="filter" id="radio-shadow" value="shadow" checked>
            <label for="radio-shadow">shadow</label>
            <input type="radio" name="filter" id="radio-blur" value="blur">
            <label for="radio-blur">blur</label>
        </div>
    </div>

    <div id="visualization-area">
        <div>
            <p>before</p>
            <div id="canvas-before-container"></div>
        </div>
        <div>
            <p>after</p>
            <div id="canvas-after-container"></div>
        </div>
    </div>

    <script>
        let beforeCanvas;
        let afterCanvas;
        let beforeGraphics;
        let afterGraphics;

        function setup() {
            beforeCanvas = createCanvas(200, 200);
            beforeCanvas.parent('canvas-before-container');
            afterCanvas = createCanvas(200, 200);
            afterCanvas.parent('canvas-after-container');

            beforeGraphics = createGraphics(200, 200);
            afterGraphics = createGraphics(200, 200);

            document.getElementById('slider-convolver').addEventListener('input', updateVisualization);
            document.getElementById('slider-image-size').addEventListener('input', updateVisualization);
            document.getElementById('input-letter').addEventListener('input', updateVisualization);
            document.querySelectorAll('input[name="filter"]').forEach(radio => radio.addEventListener('change', updateVisualization));

            updateVisualization();
        }

        function handleLetterKey(event) {
            const letterInput = document.getElementById('input-letter');
            const currentChar = letterInput.value;
            const keyCode = event.keyCode || event.which;

            if (keyCode === 38) { // Up arrow
                letterInput.value = String.fromCharCode(currentChar.charCodeAt(0) === 90 ? 65 : currentChar.charCodeAt(0) + 1);
            } else if (keyCode === 40) { // Down arrow
                letterInput.value = String.fromCharCode(currentChar.charCodeAt(0) === 65 ? 90 : currentChar.charCodeAt(0) - 1);
            }
            updateVisualization();
        }

        function updateVisualization() {
            const convolverValue = parseInt(document.getElementById('slider-convolver').value);
            const imageSize = parseInt(document.getElementById('slider-image-size').value);
            const letter = document.getElementById('input-letter').value;
            const filterType = document.querySelector('input[name="filter"]:checked').value;

            const gridSize = imageSize;
            const cellSize = 200 / gridSize;

            beforeGraphics.clear();
            beforeGraphics.background(255);
            beforeGraphics.fill(0);
            beforeGraphics.textSize(gridSize * 0.8);
            beforeGraphics.textAlign(CENTER, CENTER);
            beforeGraphics.text(letter, gridSize / 2 * cellSize, gridSize / 2 * cellSize);

            const beforeGrid = [];
            beforeGraphics.loadPixels();
            for (let i = 0; i < gridSize; i++) {
                beforeGrid[i] = [];
                for (let j = 0; j < gridSize; j++) {
                    const index = (j * beforeGraphics.width + i) * 4;
                    beforeGrid[i][j] = beforeGraphics.pixels[index] < 128 ? 1 : 0;
                }
            }
            beforeGraphics.updatePixels();

            const kernelSize = 2 * convolverValue + 1;
            const kernel = Array.from({ length: kernelSize }, () => Array(kernelSize).fill(0));
            if (filterType === 'blur') {
                for (let i = 0; i < kernelSize; i++) {
                    for (let j = 0; j < kernelSize; j++) {
                        kernel[i][j] = 1.0 / (kernelSize * kernelSize);
                    }
                }
            } else if (filterType === 'shadow') {
                for (let i = 1; i <= convolverValue; i++) {
                    for (let j = 1; j <= convolverValue; j++) {
                        kernel[i][j] = 1.0 / (convolverValue * convolverValue);
                    }
                }
            }

            const afterGrid = Array.from({ length: gridSize }, () => Array(gridSize).fill(0));
            for (let i = 0; i < gridSize; i++) {
                for (let j = 0; j < gridSize; j++) {
                    let sum = 0;
                    for (let ki = 0; ki < kernelSize; ki++) {
                        for (let kj = 0; kj < kernelSize; kj++) {
                            const ni = i + ki - convolverValue;
                            const nj = j + kj - convolverValue;
                            const value = (ni >= 0 && ni < gridSize && nj >= 0 && nj < gridSize) ? beforeGrid[ni][nj] : 0;
                            sum += value * kernel[ki][kj];
                        }
                    }
                    afterGrid[i][j] = sum;
                }
            }

            afterGraphics.clear();
            for (let i = 0; i < gridSize; i++) {
                for (let j = 0; j < gridSize; j++) {
                    const colorValue = 255 * (1 - afterGrid[i][j]);
                    afterGraphics.noStroke();
                    afterGraphics.fill(colorValue);
                    afterGraphics.rect(i * cellSize, j * cellSize, cellSize, cellSize);
                }
            }
            afterGraphics.updatePixels();

            beforeCanvas.image(beforeGraphics, 0, 0);
            afterCanvas.image(afterGraphics, 0, 0);
        }
    </script>
</body>
</html>
