</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="view-switcher">
                <button id="btn-crystallography" class="active">crystallography</button>
                <button id="btn-contact-map" class="inactive">contact map</button>
            </div>
            <div id="crystallography-controls">
                <label for="checkbox-lateral-chains">lateral chains</label>
                <input type="checkbox" id="checkbox-lateral-chains" checked>
                <label for="slider-gray-level">gray level</label>
                <input type="range" id="slider-gray-level" min="0" max="1" step="0.01" value="0.5">
                <span id="gray-level-value">0.5</span>
                <label for="slider-browse-sequence">browse the sequence</label>
                <input type="range" id="slider-browse-sequence" min="1" max="400" step="1" value="1">
                <span id="browse-sequence-value">1</span>
                <label for="select-residue">jump to a residue</label>
                <select id="select-residue">
                    <option value="A">A</option>
                    <option value="C">C</option>
                    <option value="D">D</option>
                    <option value="E">E</option>
                    <option value="F">F</option>
                    <option value="G">G</option>
                    <option value="H">H</option>
                    <option value="I">I</option>
                    <option value="K">K</option>
                    <option value="L">L</option>
                    <option value="M">M</option>
                    <option value="N">N</option>
                    <option value="P">P</option>
                    <option value="Q">Q</option>
                    <option value="R">R</option>
                    <option value="S">S</option>
                    <option value="T">T</option>
                    <option value="V">V</option>
                    <option value="W">W</option>
                    <option value="Y">Y</option>
                </select>
            </div>
            <div id="shared-controls">
                <label for="slider-residue-x">jump to atom x</label>
                <input type="range" id="slider-residue-x" min="1" max="400" step="1" value="50">
                <span id="residue-x-value">50</span>
                <label for="slider-residue-y">jump to atom y</label>
                <input type="range" id="slider-residue-y" min="1" max="400" step="1" value="52">
                <span id="residue-y-value">52</span>
                <label for="slider-rmsd">RMSD</label>
                <input type="range" id="slider-rmsd" min="0" max="15" step="0.1" value="4.9">
                <span id="rmsd-value">4.9</span>
                <label for="slider-plot-size">plot size</label>
                <input type="range" id="slider-plot-size" min="200" max="800" step="1" value="400">
                <span id="plot-size-value">400</span>
            </div>
        </div>
        <div id="visualization-area">
            <button id="btn-reset" style="position: absolute; top: 10px; right: 10px;">+</button>
            <p id="info-display"></p>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/examples/js/controls/OrbitControls.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        const numResidues = 400;
        const aaCodes = ['A', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'K', 'L', 'M', 'N', 'P', 'Q', 'R', 'S', 'T', 'V', 'W', 'Y'];
        let proteinData = generateProteinData(numResidues);
        let selectedResidueA = 1;
        let selectedResidueX = 50;
        let selectedResidueY = 52;
        let rmsdThreshold = 4.9;
        let plotSize = 400;
        let lateralChainsVisible = true;
        let grayLevel = 0.5;

        let scene, camera, renderer, orbitControls;
        let p5Instance;
        let canvasElement;

        function generateProteinData(numResidues) {
            const data = [];
            let currentPos = new THREE.Vector3(0, 0, 0);
            const directions = [
                new THREE.Vector3(1, 0, 0),
                new THREE.Vector3(-1, 0, 0),
                new THREE.Vector3(0, 1, 0),
                new THREE.Vector3(0, -1, 0),
                new THREE.Vector3(0, 0, 1),
                new THREE.Vector3(0, 0, -1)
            ];

            for (let i = 1; i <= numResidues; i++) {
                const direction = directions[Math.floor(Math.random() * directions.length)];
                currentPos.add(direction.multiplyScalar(2));

                const atoms = [
                    { atomName: 'CA', x: currentPos.x, y: currentPos.y, z: currentPos.z },
                    { atomName: 'N', x: currentPos.x + 1, y: currentPos.y, z: currentPos.z },
                    { atomName: 'C', x: currentPos.x - 1, y: currentPos.y, z: currentPos.z },
                    { atomName: 'O', x: currentPos.x - 1, y: currentPos.y + 1, z: currentPos.z },
                    { atomName: 'CB', x: currentPos.x, y: currentPos.y + 1, z: currentPos.z }
                ];

                data.push({
                    resName: aaCodes[Math.floor(Math.random() * aaCodes.length)],
                    resSeq: i,
                    atoms: atoms
                });
            }

            return data;
        }

        function initThreeJS() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            camera = new THREE.PerspectiveCamera(75, plotSize / plotSize, 0.1, 1000);
            camera.position.z = 50;

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(plotSize, plotSize);
            canvasElement.appendChild(renderer.domElement);

            orbitControls = new THREE.OrbitControls(camera, renderer.domElement);

            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight.position.set(1, 1, 1).normalize();
            scene.add(directionalLight);

            render3D();
        }

        function render3D() {
            scene.clear();

            const mainChainColor = new THREE.Color().lerpColors(new THREE.Color(0x000000), new THREE.Color(0x0000ff), grayLevel);
            const materialMainChain = new THREE.MeshLambertMaterial({ color: mainChainColor });
            const materialResidueA = new THREE.MeshLambertMaterial({ color: 0xffff00 });
            const materialResidueXY = new THREE.MeshLambertMaterial({ color: 0xff0000 });

            const geometry = new THREE.SphereGeometry(0.5, 32, 32);

            proteinData.forEach((residue, index) => {
                residue.atoms.forEach(atom => {
                    const sphere = new THREE.Mesh(geometry, materialMainChain);
                    sphere.position.set(atom.x, atom.y, atom.z);
                    if (index + 1 === selectedResidueA) {
                        sphere.material = materialResidueA;
                    } else if (atom.atomName === 'CA' && (index + 1 === selectedResidueX || index + 1 === selectedResidueY)) {
                        sphere.material = materialResidueXY;
                    }
                    if (!lateralChainsVisible && atom.atomName !== 'CA') {
                        return;
                    }
                    scene.add(sphere);
                });
            });

            const boundingBox = new THREE.Box3().setFromObject(scene);
            const boxHelper = new THREE.BoxHelper(scene, 0x808080);
            scene.add(boxHelper);

            renderer.render(scene, camera);
        }

        function initP5() {
            p5Instance = new p5(sketch);
        }

        function sketch(p) {
            p.setup = () => {
                canvasElement = p.createCanvas(plotSize, plotSize).parent('canvas-container').elt;
                p.background(255);
            };

            p.draw = () => {
                p.background(255);
                p.stroke(128);
                p.strokeWeight(1);
                p.line(0, p.height, p.width, p.height);
                p.line(p.width, 0, p.width, p.height);
                p.strokeWeight(2);
                p.textAlign(p.LEFT, p.TOP);
                p.textSize(12);
                [1, 100, 200, 300, 400].forEach(num => {
                    const pos = map(num, 1, numResidues, 0, p.width);
                    p.text(num, pos, p.height - 10);
                    p.text(num, p.width - 10, pos);
                    p.line(pos, p.height - 5, pos, p.height);
                    p.line(p.width - 5, pos, p.width, pos);
                });

                p.stroke(0);
                proteinData.forEach((residue, i) => {
                    const caAtom = residue.atoms.find(atom => atom.atomName === 'CA');
                    proteinData.forEach((otherResidue, j) => {
                        const otherCaAtom = otherResidue.atoms.find(atom => atom.atomName === 'CA');
                        const distance = caAtom ? new THREE.Vector3(caAtom.x, caAtom.y, caAtom.z).distanceTo(new THREE.Vector3(otherCaAtom.x, otherCaAtom.y, otherCaAtom.z)) : Infinity;
                        if (distance <= rmsdThreshold) {
                            p.point(map(i + 1, 1, numResidues, 0, p.width), map(j + 1, 1, numResidues, 0, p.height));
                        }
                    });
                });

                p.stroke(255, 0, 0);
                p.line(map(selectedResidueX, 1, numResidues, 0, p.width), 0, map(selectedResidueX, 1, numResidues, 0, p.width), p.height);
                p.line(0, map(selectedResidueY, 1, numResidues, 0, p.height), p.width, map(selectedResidueY, 1, numResidues, 0, p.height));
            };
        }

        function map(value, start1, stop1, start2, stop2) {
            return ((value - start1) / (stop1 - start1)) * (stop2 - start2) + start2;
        }

        function updateInfoDisplay() {
            const resA = proteinData[selectedResidueA - 1];
            const resX = proteinData[selectedResidueX - 1];
            const resY = proteinData[selectedResidueY - 1];
            document.getElementById('info-display').innerText = `Res A: ${resA.resName}${resA.resSeq} | Res X: ${resX.resName}${resX.resSeq} | Res Y: ${resY.resName}${resY.resSeq}`;
        }

        function resizeCanvas() {
            if (renderer) {
                renderer.setSize(plotSize, plotSize);
                camera.aspect = plotSize / plotSize;
                camera.updateProjectionMatrix();
            }
            if (p5Instance) {
                p5Instance.resizeCanvas(plotSize, plotSize);
            }
        }

        document.getElementById('btn-crystallography').addEventListener('click', () => {
            document.getElementById('btn-crystallography').classList.add('active');
            document.getElementById('btn-contact-map').classList.remove('active');
            if (p5Instance) {
                p5Instance.remove();
                p5Instance = null;
            }
            if (!renderer) {
                initThreeJS();
            }
        });

        document.getElementById('btn-contact-map').addEventListener('click', () => {
            document.getElementById('btn-contact-map').classList.add('active');
            document.getElementById('btn-crystallography').classList.remove('active');
            if (renderer) {
                renderer.dispose();
                renderer = null;
            }
            if (!p5Instance) {
                initP5();
            }
        });

        document.getElementById('checkbox-lateral-chains').addEventListener('change', () => {
            lateralChainsVisible = document.getElementById('checkbox-lateral-chains').checked;
            render3D();
        });

        document.getElementById('slider-gray-level').addEventListener('input', () => {
            grayLevel = parseFloat(document.getElementById('slider-gray-level').value);
            document.getElementById('gray-level-value').innerText = grayLevel.toFixed(2);
            render3D();
        });

        document.getElementById('slider-browse-sequence').addEventListener('input', () => {
            selectedResidueA = parseInt(document.getElementById('slider-browse-sequence').value);
            document.getElementById('browse-sequence-value').innerText = selectedResidueA;
            updateInfoDisplay();
            render3D();
        });

        document.getElementById('select-residue').addEventListener('change', () => {
            const selectedAA = document.getElementById('select-residue').value;
            const residueIndex = proteinData.findIndex(residue => residue.resName === selectedAA && residue.resSeq > selectedResidueA);
            if (residueIndex !== -1) {
                selectedResidueA = residueIndex + 1;
                document.getElementById('slider-browse-sequence').value = selectedResidueA;
                document.getElementById('browse-sequence-value').innerText = selectedResidueA;
                updateInfoDisplay();
                render3D();
            }
        });

        document.getElementById('slider-residue-x').addEventListener('input', () => {
            selectedResidueX = parseInt(document.getElementById('slider-residue-x').value);
            document.getElementById('residue-x-value').innerText = selectedResidueX;
            updateInfoDisplay();
            if (renderer) {
                render3D();
            } else if (p5Instance) {
                p5Instance.redraw();
            }
        });

        document.getElementById('slider-residue-y').addEventListener('input', () => {
            selectedResidueY = parseInt(document.getElementById('slider-residue-y').value);
