            background-color: lightgreen;
        }
        #canvas-container {
            width: 100%;
            max-width: 800px;
            height: 500px;
            position: relative;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div>
                <input type="radio" id="radio-rivers" name="quiz-type" value="rivers" checked>
                <label for="radio-rivers">rivers</label>
                <input type="radio" id="radio-lakes" name="quiz-type" value="lakes">
                <label for="radio-lakes">lakes</label>
            </div>
            <div>
                <button id="btn-reset">reset</button>
            </div>
            <div>
                <input type="checkbox" id="check-show-locations" checked>
                <label for="check-show-locations">show locations</label>
            </div>
            <div>
                <input type="checkbox" id="check-show-some">
                <label for="check-show-some">show some rivers and lakes</label>
            </div>
        </div>
        <p id="p-position">Current position {-172, -19}</p>
        <div id="table-container">
            <table id="table-quiz"></table>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        const rivers = [
            {name: 'Nile', lon: 31.2, lat: 30.2, found: false},
            {name: 'Saint Lawrence', lon: -64.5, lat: 49.5, found: false},
            {name: 'Nelson', lon: -122.8, lat: 59.3, found: false},
            {name: 'Mekong', lon: 104.5, lat: 19.8, found: false},
            {name: 'Araguaia', lon: -52.8, lat: -12.5, found: false},
            {name: 'Yangtze', lon: 107.8, lat: 29.6, found: false},
            {name: 'Brahmaputra', lon: 91.7, lat: 26.8, found: false},
            {name: 'Yukon', lon: -140.5, lat: 63.8, found: false},
            {name: 'Mackenzie', lon: -122.3, lat: 63.6, found: false},
            // Add remaining 31 rivers here...
        ];

        const lakes = [
            {name: 'Onega', lon: 35.5, lat: 61.7, found: false},
            {name: 'Erie', lon: -81.2, lat: 42.2, found: false},
            {name: 'Huron', lon: -82.3, lat: 44.8, found: false},
            {name: 'Issyk-Kul', lon: 76.6, lat: 42.8, found: false},
            {name: 'Michigan', lon: -84.7, lat: 44.8, found: false},
            {name: 'Ladoga', lon: 30.8, lat: 61.6, found: false},
            {name: 'Winnipeg', lon: -96.8, lat: 53.2, found: false},
            {name: 'Baikal', lon: 105.5, lat: 51.7, found: false},
            {name: 'Tanganyika', lon: 30.5, lat: -7.8, found: false},
            // Add remaining 18 lakes here...
        ];

        let quizType = 'rivers';
        let data = rivers;
        let mapImage;

        function preload() {
            mapImage = loadImage('https://upload.wikimedia.org/wikipedia/commons/c/c5/Blue_Marble_Next_Generation_%2B_topography_%2B_bathymetry.jpg');
        }

        const sketch = (p) => {
            p.setup = () => {
                p.createCanvas(p.windowWidth * 0.8, p.windowHeight * 0.6);
                populateTable();
            };

            p.draw = () => {
                p.background(255);
                p.image(mapImage, 0, 0, p.width, p.height);

                const [lon, lat] = mapToGeo(p.mouseX, p.mouseY);
                document.getElementById('p-position').innerText = `Current position {${Math.round(lon)}, ${Math.round(lat)}}`;

                p.fill(173, 216, 230, 150);
                p.noStroke();
                p.circle(p.mouseX, p.mouseY, 30);

                data.forEach(item => {
                    if (!item.found && document.getElementById('check-show-locations').checked) {
                        const [x, y] = mapToPixels(item.lon, item.lat);
                        p.fill('red');
                        p.noStroke();
                        p.circle(x, y, 10);
                    }
                });

                if (document.getElementById('check-show-some').checked) {
                    const unfoundItems = data.filter(item => !item.found);
                    const randomSubset = p.shuffle(unfoundItems).slice(0, 3);
                    randomSubset.forEach(item => {
                        const [x, y] = mapToPixels(item.lon, item.lat);
                        p.fill('white');
                        p.textSize(12);
                        p.text(item.name, x + 10, y);
                    });
                }
            };

            p.mouseClicked = () => {
                data.forEach(item => {
                    if (!item.found) {
                        const [x, y] = mapToPixels(item.lon, item.lat);
                        const distance = p.dist(p.mouseX, p.mouseY, x, y);
                        if (distance <= 15) {
                            item.found = true;
                            updateTable(item.name);
                            return;
                        }
                    }
                });
            };
        };

        const myp5 = new p5(sketch, 'canvas-container');

        function mapToPixels(lon, lat) {
            return [myp5.map(lon, -180, 180, 0, myp5.width), myp5.map(lat, 90, -90, 0, myp5.height)];
        }

        function mapToGeo(x, y) {
            return [myp5.map(x, 0, myp5.width, -180, 180), myp5.map(y, 0, myp5.height, 90, -90)];
        }

        function populateTable() {
            const table = document.getElementById('table-quiz');
            table.innerHTML = '';

            const headerRow = table.insertRow();
            const statusRow = table.insertRow();

            data.forEach(item => {
                const headerCell = headerRow.insertCell();
                headerCell.innerText = item.name;

                const statusCell = statusRow.insertCell();
                statusCell.innerText = item.found ? 'True' : 'False';
                statusCell.className = item.found ? 'found' : '';
            });
        }

        function updateTable(name) {
            const table = document.getElementById('table-quiz');
            const rows = table.rows;
            for (let i = 0; i < rows[1].cells.length; i++) {
                if (rows[0].cells[i].innerText === name) {
                    rows[1].cells[i].innerText = 'True';
                    rows[1].cells[i].className = 'found';
                    break;
                }
            }
        }

        document.getElementById('radio-rivers').addEventListener('change', () => {
            if (document.getElementById('radio-rivers').checked) {
                quizType = 'rivers';
                data = rivers;
                resetQuiz();
            }
        });

        document.getElementById('radio-lakes').addEventListener('change', () => {
            if (document.getElementById('radio-lakes').checked) {
                quizType = 'lakes';
                data = lakes;
                resetQuiz();
            }
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            resetQuiz();
        });

        document.getElementById('check-show-locations').addEventListener('change', () => {
            myp5.redraw();
        });

        document.getElementById('check-show-some').addEventListener('change', () => {
            myp5.redraw();
        });

        function resetQuiz() {
            data.forEach(item => {
                item.found = false;
            });
            populateTable();
            myp5.redraw();
        }

        populateTable();
    </script>
</body>
</html>
