        #viz-container {
            display: flex;
            flex-direction: column;
            flex: 1;
            border: 1px solid #ccc;
            padding: 10px;
        }
        #header-section {
            display: flex;
            justify-content: space-between;
            margin-bottom: 10px;
        }
        #main-content {
            display: flex;
