        }
        #canvas-area {
            flex: 2;
            position: relative;
        }
        #canvas-area canvas {
            width: 100%;
            height: 100%;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label>tiers</label>
            <div id="tiers-container">
                <button id="btn-tier-1">1</button>
                <button id="btn-tier-2">2</button>
                <button id="btn-tier-3">3</button>
                <button id="btn-tier-4">4</button>
                <button id="btn-tier-5">5</button>
            </div>
            <label>show binary</label><input type="checkbox" id="checkbox-binary">
            <label>characters</label>
            <input type="radio" id="radio-chars-no" name="chars-toggle" value="no"><label for="radio-chars-no">no</label>
            <input type="radio" id="radio-chars-yes" name="chars-toggle" value="yes" checked><label for="radio-chars-yes">yes</label>
        </div>
        <div id="viz-container">
            <div id="header-section">
                <p id="original-text">MISSISSIPPI_RIVER</p>
                <p id="encoded-text">1100000101000101001001000011111010011011110101</p>
            </div>
            <div id="main-content">
                <div id="info-panel">
                    <p id="explanation-text">Once at the top, the total frequency becomes 17, which is the total number of characters.</p>
                    <div id="encoding-table">
                        <p>I = 00</p>
                        <p>S = 01</p>
                        <p>P = 100</p>
                        <p>R = 101</p>
                        <p>M = 1100</p>
                        <p>V = 1101</p>
                        <p>E = 1110</p>
                        <p>_ = 1111</p>
                    </div>
                </div>
                <div id="canvas-area"></div>
            </div>
        </div>
    </div>

    <script>
        const treeData = [
            { id: 'I',  label: 'I', freq: 5, tier: 1, pos: {x: 100, y: 450}, children: [] },
            { id: 'S',  label: 'S', freq: 4, tier: 1, pos: {x: 180, y: 450}, children: [] },
            { id: 'P',  label: 'P', freq: 2, tier: 1, pos: {x: 260, y: 450}, children: [] },
            { id: 'R',  label: 'R', freq: 2, tier: 1, pos: {x: 340, y: 450}, children: [] },
            { id: 'M',  label: 'M', freq: 1, tier: 1, pos: {x: 420, y: 450}, children: [] },
            { id: 'V',  label: 'V', freq: 1, tier: 1, pos: {x: 500, y: 450}, children: [] },
            { id: 'E',  label: 'E', freq: 1, tier: 1, pos: {x: 580, y: 450}, children: [] },
            { id: '_',  label: '_', freq: 1, tier: 1, pos: {x: 660, y: 450}, children: [] },
            { id: 'IS', label: 'IS', freq: 9, tier: 2, pos: {x: 140, y: 350}, children: ['I', 'S'] },
            { id: 'PR', label: 'PR', freq: 4, tier: 2, pos: {x: 300, y: 350}, children: ['P', 'R'] },
            { id: 'MV', label: 'MV', freq: 2, tier: 2, pos: {x: 460, y: 350}, children: ['M', 'V'] },
            { id: 'E_', label: 'E_', freq: 2, tier: 2, pos: {x: 620, y: 350}, children: ['E', '_'] },
            { id: 'MVE_', label: 'MVE_', freq: 4, tier: 3, pos: {x: 540, y: 250}, children: ['MV', 'E_'] },
            { id: 'PRMVE_', label: 'PRMVE_', freq: 8, tier: 4, pos: {x: 420, y: 150}, children: ['PR', 'MVE_'] },
            { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: {x: 280, y: 50}, children: ['IS', 'PRMVE_'] }
        ];

        let currentTier = 5;
        let showBinary = false;
        let showNodeChars = true;

        const explanationTexts = [
            "After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.",
            "We continue grouping until we get to the highest tier.",
            "We continue grouping until we get to the highest tier.",
            "We continue grouping until we get to the highest tier.",
            "Once at the top, the total frequency becomes 17, which is the total number of characters."
        ];

        function setup() {
            createCanvas(800, 500);
            updateUI();
        }

        function draw() {
            background(255);
            stroke('#C70000');
            strokeWeight(2);

            for (let node of treeData) {
                let parent = treeData.find(n => n.children.includes(node.id));
                if (parent && parent.tier <= currentTier && node.tier <= currentTier) {
                    line(parent.pos.x, parent.pos.y, node.pos.x, node.pos.y);
                    if (showBinary) {
                        textSize(12);
                        fill(0);
                        let midX = (parent.pos.x + node.pos.x) / 2;
                        let midY = (parent.pos.y + node.pos.y) / 2;
                        text(parent.children.indexOf(node.id) === 0 ? '0' : '1', midX, midY);
                    }
                }
            }

            for (let node of treeData) {
                if (node.tier <= currentTier) {
                    fill('#FEFDD5');
                    stroke('#C70000');
                    rectMode(CENTER);
                    rect(node.pos.x, node.pos.y, 50, 30, 10);
                    fill(0);
                    textSize(12);
                    textAlign(CENTER, CENTER);
                    let label = node.children.length === 0 ? `${node.label}(${node.freq})` : 
                                showNodeChars ? `${node.label}(${node.freq})` : `(${node.freq})`;
                    text(label, node.pos.x, node.pos.y);
                }
            }
        }

        function updateUI() {
            document.getElementById('explanation-text').textContent = explanationTexts[currentTier - 1];
            document.getElementById('encoding-table').style.display = currentTier >= 2 ? 'block' : 'none';
            document.getElementById('encoded-text').style.display = currentTier === 5 ? 'block' : 'none';
        }

        function selectTier(tier) {
            document.querySelectorAll('#tiers-container button').forEach(btn => btn.classList.remove('active'));
            document.querySelector(`#btn-tier-${tier}`).classList.add('active');
            currentTier = tier;
            updateUI();
            redraw();
