<body>
    <div id="control-panel">
        <label for="slider-n">n</label>
        <input type="range" id="slider-n" min="1" max="20" value="1" step="1">
        <input type="number" id="input-n" min="1" max="20" value="1" step="1">
    </div>
    <div id="visualization-area">
        <div id="function-plot-container">
            <canvas id="canvas-function"></canvas>
            <div id="formula-label">$$y = \frac{2}{x}$$</div>
        </div>
        <div id="sum-plot-container">
            <canvas id="canvas-sum"></canvas>
        </div>
    </div>

    <script>
        const colorArray = ['#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa', '#6495ed', '#9370db', '#8a2be2'];

        function setupFunctionPlot() {
            const functionPlot = new p5((sketch) => {
                let n = 1;

                sketch.setup = () => {
                    sketch.createCanvas(sketch.select('#function-plot-container').width(), sketch.select('#function-plot-container').height());
                    sketch.pixelDensity(1);
                };

                sketch.draw = () => {
                    sketch.background(255);
                    sketch.translate(50, sketch.height - 50);
                    sketch.scale(1, -1);

                    // Draw axes
                    sketch.stroke(0);
                    sketch.line(0, 0, sketch.width - 100, 0); // x-axis
                    sketch.line(0, 0, 0, sketch.height - 100); // y-axis

                    // Draw ticks and labels
                    for (let i = 1; i <= 10; i++) {
                        sketch.line(i * 50, 0, i * 50, 5);
                        sketch.text(i, i * 50 - 5, -10);
                    }
                    for (let j = 1; j <= 2; j++) {
                        sketch.line(0, j * 100, -5, j * 100);
                        sketch.text(j, -20, j * 100 + 5);
                    }

                    // Draw function y = 2/x
                    sketch.stroke('blue');
                    sketch.beginShape();
                    for (let x = 0.5; x <= 10; x += 0.01) {
                        sketch.vertex(x * 50, 2 / x * 100);
                    }
                    sketch.endShape();

                    // Draw rectangles
                    for (let k = 1; k <= n; k++) {
                        sketch.fill(colorArray[(k - 1) % colorArray.length]);
                        sketch.rect(k * 50, 2 / (k + 1) * 100, 50, (2 / k - 2 / (k + 1)) * 100);
                        if (k <= 3) {
                            sketch.stroke('gray');
                            sketch.drawingContext.setLineDash([5, 5]);
                            sketch.line(k * 50, 0, k * 50, 2 / k * 100);
                            sketch.drawingContext.setLineDash([]);
                        }
                    }
                };

                sketch.updateN = (newN) => {
                    n = newN;
                    sketch.redraw();
                };
            }, 'canvas-function');

            return functionPlot;
        }

        function setupSumPlot() {
            const sumPlot = new p5((sketch) => {
                let n = 1;

                sketch.setup = () => {
                    sketch.createCanvas(sketch.select('#sum-plot-container').width(), sketch.select('#sum-plot-container').height());
                    sketch.pixelDensity(1);
                };

                sketch.draw = () => {
                    sketch.background(255);
                    sketch.translate(50, sketch.height - 50);
                    sketch.scale(1, -1);

                    // Draw axes
                    sketch.stroke(0);
                    sketch.line(0, 0, sketch.width - 100, 0); // x-axis
                    sketch.line(0, 0, 0, sketch.height - 100); // y-axis

                    // Draw ticks and labels
                    sketch.line(100, 0, 105, 0);
                    sketch.text(1, 95, -10);
                    for (let j = 1; j <= 2; j++) {
                        sketch.line(0, j * 100, -5, j * 100);
                        sketch.text(j, -20, j * 100 + 5);
                    }

                    // Draw stacked rectangles
                    let yStack = 0;
                    for (let k = n; k >= 1; k--) {
                        const h = (2 / k - 2 / (k + 1)) * 100;
                        sketch.fill(colorArray[(k - 1) % colorArray.length]);
                        sketch.rect(0, yStack, 100, h);
                        yStack += h;
                    }
                };

                sketch.updateN = (newN) => {
                    n = newN;
                    sketch.redraw();
                };
            }, 'canvas-sum');

            return sumPlot;
        }

        const functionPlot = setupFunctionPlot();
        const sumPlot = setupSumPlot();

        const sliderN = document.getElementById('slider-n');
        const inputN = document.getElementById('input-n');

        sliderN.addEventListener('input', () => {
            const newValue = parseInt(sliderN.value, 10);
            inputN.value = newValue;
            functionPlot.updateN(newValue);
            sumPlot.updateN(newValue);
        });

        inputN.addEventListener('input', () => {
            const newValue = parseInt(inputN.value, 10);
            if (newValue >= 1 && newValue <= 20) {
                sliderN.value = newValue;
                functionPlot.updateN(newValue);
                sumPlot.updateN(newValue);
            }
        });
    </script>
</body>
</html>
