            width: 75%;
            padding: 20px;
            background-color: #fff;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="button-group">
                <button id="btn-monod" class="active">Monod</button>
                <button id="btn-si">substrate inhibition</button>
            </div>
            <div class="button-group">
                <button id="btn-bifurcation" class="active">bifurcation diagram</button>
                <button id="btn-nontrivial">nontrivial steady state</button>
            </div>
            <div class="slider-container">
                <label for="slider-x2f">x<sub>2f</sub></label>
                <input type="range" id="slider-x2f" min="1" max="10" step="0.1" value="4.0">
                <span id="value-x2f">4.0</span>
            </div>
            <div class="slider-container">
                <label for="slider-umax">μ<sub>max</sub></label>
                <input type="range" id="slider-umax" min="0.1" max="2" step="0.01" value="0.6">
                <span id="value-umax">0.60</span>
            </div>
            <div class="slider-container">
                <label for="slider-km">k<sub>m</sub></label>
                <input type="range" id="slider-km" min="0.01" max="1" step="0.01" value="0.12">
                <span id="value-km">0.12</span>
            </div>
            <div class="slider-container" id="slider-k1-container" style="display: none;">
                <label for="slider-k1">k<sub>1</sub></label>
                <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.4545">
                <span id="value-k1">0.4545</span>
            </div>
            <div class="slider-container">
                <label for="slider-ds">D<sub>s</sub></label>
                <input type="range" id="slider-ds" min="0.01" max="1.0" step="0.01" value="0.6">
                <span id="value-ds">0.60</span>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        let currentModel = 'monod';
        let currentView = 'bifurcation';
        const params = {
            x2f: 4.0,
            umax: 0.6,
            km: 0.12,
            k1: 0.4545,
            ds: 0.6
        };

        function updatePlot() {
            if (currentView === 'bifurcation') {
                drawBifurcationDiagram();
            } else {
                drawNontrivialStatePlot();
            }
        }

        function drawBifurcationDiagram() {
            const x2Values = Array.from({ length: 100 }, (_, i) => i * params.x2f / 99);
            const dValues = x2Values.map(x2 => params.umax * x2 / (params.km + x2 + (currentModel === 'si' ? params.k1 * x2 * x2 : 0)));
            const dbif = params.umax * params.x2f / (params.km + params.x2f + (currentModel === 'si' ? params.k1 * params.x2f * params.x2f : 0));

            const stableTrace = {
                x: x2Values.filter((_, i) => i < x2Values.findIndex(x2 => dValues[i] > params.ds) || currentModel === 'monod'),
                y: dValues.filter((_, i) => i < x2Values.findIndex(x2 => dValues[i] > params.ds) || currentModel === 'monod'),
                mode: 'lines',
                line: { color: 'blue' },
                name: 'Stable'
            };

            const unstableTrace = {
                x: x2Values.filter((_, i) => i >= x2Values.findIndex(x2 => dValues[i] > params.ds) && currentModel === 'si'),
                y: dValues.filter((_, i) => i >= x2Values.findIndex(x2 => dValues[i] > params.ds) && currentModel === 'si'),
                mode: 'lines',
                line: { color: 'red', dash: 'dash' },
                name: 'Unstable'
            };

            const trivialStableTrace = {
                x: [0, dbif],
                y: [params.x2f, params.x2f],
                mode: 'lines',
                line: { color: 'blue' },
                name: 'Trivial Stable'
            };

            const trivialUnstableTrace = {
                x: [dbif, 1.0],
                y: [params.x2f, params.x2f],
                mode: 'lines',
                line: { color: 'red', dash: 'dash' },
                name: 'Trivial Unstable'
            };

            const intersectionPoints = [];
            for (let i = 0; i < dValues.length - 1; i++) {
                if ((dValues[i] - params.ds) * (dValues[i + 1] - params.ds) < 0) {
                    const x2 = x2Values[i] + (x2Values[i + 1] - x2Values[i]) * (params.ds - dValues[i]) / (dValues[i + 1] - dValues[i]);
                    const ddx = (dValues[i + 1] - dValues[i]) / (x2Values[i + 1] - x2Values[i]);
                    intersectionPoints.push({
                        x: params.ds,
                        y: x2,
                        mode: 'markers',
                        marker: { color: ddx > 0 ? 'magenta' : 'cyan' },
                        name: 'Intersection'
                    });
                }
            }

            const trivialPoint = {
                x: [params.ds],
                y: [params.x2f],
                mode: 'markers',
                marker: { color: 'green' },
                name: 'Trivial Intersection'
            };

            const layout = {
                title: 'Bifurcation Diagram',
                xaxis: { title: 'Dilution Rate (D)', range: [0, 1.0] },
                yaxis: { title: 'X₂', range: [0, 6.0] },
                shapes: [
                    {
                        type: 'line',
                        x0: params.ds,
                        y0: 0,
                        x1: params.ds,
                        y1: 6.0,
                        line: { color: 'gray', dash: 'dash' }
                    }
                ]
            };

            Plotly.newPlot('plot-container', [...stableTrace, ...unstableTrace, ...trivialStableTrace, ...trivialUnstableTrace, ...intersectionPoints, ...trivialPoint], layout);
        }

        function drawNontrivialStatePlot() {
            const x2Values = Array.from({ length: 100 }, (_, i) => i * 7 / 99);
            const muValues = x2Values.map(x2 => params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2));

            const muTrace = {
                x: x2Values,
                y: muValues,
                mode: 'lines',
                line: { color: 'blue' },
                name: 'μ(x₂)'
            };

            const dUpper = params.umax / (1 + 2 * Math.sqrt(params.k1 * params.km));
            const dLower = params.umax * params.x2f / (params.km + params.x2f + params.k1 * params.x2f * params.x2f);

            const upperLine = {
                x: [0, 7],
                y: [dUpper, dUpper],
                mode: 'lines',
                line: { color: 'red', dash: 'dash' },
                name: 'Upper Bound'
            };

            const lowerLine = {
                x: [0, 7],
                y: [dLower, dLower],
                mode: 'lines',
                line: { color: 'red', dash: 'dash' },
                name: 'Lower Bound'
            };

            const dsLine = {
                x: [0, 7],
                y: [params.ds, params.ds],
                mode: 'lines',
                line: { color: 'gray' },
                name: 'Dₛ'
            };

            const bistabilityRegion = {
                x: [0, 7],
                y: [dLower, dLower],
                fill: 'tozeroy',
                fillcolor: 'rgba(255, 165, 0, 0.3)',
                line: { color: 'transparent' },
                name: 'Bistability Region'
            };

            const intersectionPoints = [];
            for (let i = 0; i < muValues.length - 1; i++) {
                if ((muValues[i] - params.ds) * (muValues[i + 1] - params.ds) < 0) {
                    const x2 = x2Values[i] + (x2Values[i + 1] - x2Values[i]) * (params.ds - muValues[i]) / (muValues[i + 1] - muValues[i]);
                    const dmu = (muValues[i + 1] - muValues[i]) / (x2Values[i + 1] - x2Values[i]);
                    intersectionPoints.push({
                        x: [x2],
                        y: [params.ds],
                        mode: 'markers',
                        marker: { color: dmu > 0 ? 'magenta' : 'cyan' },
                        name: 'Intersection'
                    });
                }
            }

            const layout = {
                title: 'Nontrivial Steady State Plot',
                xaxis: { title: 'Substrate Concentration (x₂)', range: [0, 7] },
                yaxis: { title: 'Specific Growth Rate Coefficient (μ)', range: [0, 1] }
            };

            Plotly.newPlot('plot-container', [...muTrace, ...dsLine, ...upperLine, ...lowerLine, ...bistabilityRegion, ...intersectionPoints], layout);
        }

        document.getElementById('btn-monod').addEventListener('click', () => {
            currentModel = 'monod';
            document.getElementById('btn-monod').classList.add('active');
            document.getElementById('btn-si').classList.remove('active');
            document.getElementById('slider-k1-container').style.display = 'none';
            updatePlot();
        });

        document.getElementById('btn-si').addEventListener('click', () => {
            currentModel = 'si';
            document.getElementById('btn-si').classList.add('active');
            document.getElementById('btn-monod').classList.remove('active');
            document.getElementById('slider-k1-container').style.display = 'block';
            updatePlot();
        });

        document.getElementById('btn-bifurcation').addEventListener('click', () => {
            currentView = 'bifurcation';
            document.getElementById('btn-bifurcation').classList.add('active');
            document.getElementById('btn-nontrivial').classList.remove('active');
            updatePlot();
        });

        document.getElementById('btn-nontrivial').addEventListener('click', () => {
            currentView = 'nontrivial';
            document.getElementById('btn-nontrivial').classList.add('active');
            document.getElementById('btn-bifurcation').classList.remove('active');
            updatePlot();
        });

        const sliders = ['x2f', 'umax', 'km', 'k1', 'ds'];
        sliders.forEach(param => {
            const slider = document.getElementById(`slider-${param}`);
            const valueDisplay = document.getElementById(`value-${param}`);
            slider.addEventListener('input', () => {
                params[param] = parseFloat(slider.value);
                valueDisplay.textContent = param === 'k1' ? params[param].toFixed(4) : params[param].toFixed(2);
                updatePlot();
            });
        });

        updatePlot();
    </script>
</body>
</html>
