            context.fillStyle = '#00f';
            context.fillRect(0, 0, canvas.width, canvas.height);
            context.fillStyle = '#008000';
            context.beginPath();
            context.arc(canvas.width / 2, canvas.height / 2, canvas.width / 4, 0, Math.PI * 2);
            context.fill();
            return new THREE.CanvasTexture(canvas);
        }

        function createLatitudeLine(latitude) {
            const radians = latitude * (Math.PI / 180);
            const circleGeometry = new THREE.CircleGeometry(1, 64, 0, Math.PI * 2);
            const circleVertices = circleGeometry.attributes.position.array;
            for (let i = 0; i < circleVertices.length; i += 3) {
                const x = circleVertices[i];
                const z = circleVertices[i + 2];
                const distanceFromCenter = Math.sqrt(x * x + z * z);
                const newY = Math.sin(radians) * distanceFromCenter;
                circleVertices[i + 1] = newY;
            }
            const lineMaterial = new THREE.LineBasicMaterial({ color: 0x0000ff });
            if (latitude === 51.4789) {
                lineMaterial = new THREE.LineDashedMaterial({ color: 0x0000ff, dashSize: 0.1, gapSize: 0.1 });
            } else if (latitude === 90) {
                lineMaterial = new THREE.LineBasicMaterial({ color: 0xff0000 });
            }
            const line = new THREE.Line(circleGeometry, lineMaterial);
            line.geometry.computeLineDistances();
            return line;
        }

        function update() {
            const season = parseFloat(sliderSeason.value);
            const latitude = parseFloat(sliderLatitude.value);
            const timeOfDay = parseFloat(sliderTime.value);

            document.getElementById('value-season').textContent = season.toFixed(3);
            document.getElementById('value-latitude').textContent = latitude.toFixed(2);
            document.getElementById('value-time').textContent = timeOfDay.toFixed(2);

            const solarDeclination = 23.5 * Math.cos(2 * Math.PI * season);
            const solarDeclinationRad = solarDeclination * (Math.PI / 180);

            earth.rotation.y = (timeOfDay / 24) * 2 * Math.PI;

            directionalLight.position.set(0, Math.sin(solarDeclinationRad), -Math.cos(solarDeclinationRad));
            directionalLight.updateMatrixWorld();

            const latitudeRad = latitude * (Math.PI / 180);
            const cosLat = Math.cos(latitudeRad);
            const sinLat = Math.sin(latitudeRad);
            const cosDec = Math.cos(solarDeclinationRad);
            const sinDec = Math.sin(solarDeclinationRad);

            const surfaceNormalDir = new THREE.Vector3(cosLat, sinLat, 0);
            surfaceNormal.position.set(cosLat, sinLat, 0);
            surfaceNormal.setDirection(surfaceNormalDir);

            const sunDir = new THREE.Vector3(0, sinDec, -cosDec);
            sunVector.position.set(cosLat, sinLat, 0);
            sunVector.setDirection(sunDir);

            selectedLatitudeLine.geometry.dispose();
            selectedLatitudeLine.geometry = createLatitudeLine(latitude).geometry;
            selectedLatitudeLine.geometry.computeLineDistances();

            updatePlots(season, latitude);
        }

        function updatePlots(season, latitude) {
            const solarDeclination = 23.5 * Math.cos(2 * Math.PI * season);
            const solarDeclinationRad = solarDeclination * (Math.PI / 180);

            const latitudes = [0, latitude, 90];
            const instantaneousData = { equator: [], latitude: [], northPole: [] };
            const cumulativeData = { equator: 0, latitude: 0, northPole: 0 };

            for (let hour = 0; hour <= 24; hour++) {
                const HRA = 15 * (hour - 12) * (Math.PI / 180);
                latitudes.forEach((lat, index) => {
                    const latRad = lat * (Math.PI / 180);
                    const cosLat = Math.cos(latRad);
                    const sinLat = Math.sin(latRad);
                    const cosHRA = Math.cos(HRA);
                    const sinHRA = Math.sin(HRA);
                    const cosDec = Math.cos(solarDeclinationRad);
                    const sinDec = Math.sin(solarDeclinationRad);

                    const energy = 1361 * Math.max(0, sinLat * sinDec + cosLat * cosDec * cosHRA);
                    instantaneousData[Object.keys(instantaneousData)[index]].push(energy);
                    cumulativeData[Object.keys(cumulativeData)[index]] += energy;
                });
            }

            const instantPlotData = [
                { x: Array.from({ length: 25 }, (_, i) => i), y: instantaneousData.equator, mode: 'lines', name: 'Equator', line: { color: 'blue' } },
                { x: Array.from({ length: 25 }, (_, i) => i), y: instantaneousData.latitude, mode: 'lines', name: 'Latitude', line: { color: 'black' } },
                { x: Array.from({ length: 25 }, (_, i) => i), y: instantaneousData.northPole, mode: 'lines', name: 'North Pole', line: { color: 'red' } },
                { x: [parseFloat(document.getElementById('value-time').textContent)], y: [instantaneousData.latitude[Math.round(parseFloat(document.getElementById('value-time').textContent))]], mode: 'markers', marker: { color: 'black', size: 10 } }
            ];

            const cumulativePlotData = [
                { x: Array.from({ length: 25 }, (_, i) => i), y: Object.values(cumulativeData).map(val => val / 24), mode: 'lines', name: 'Equator', line: { color: 'blue' } },
                { x: Array.from({ length: 25 }, (_, i) => i), y: Object.values(cumulativeData).map(val => val / 24), mode: 'lines', name: 'Latitude', line: { color: 'black' } },
                { x: Array.from({ length: 25 }, (_, i) => i), y: Object.values(cumulativeData).map(val => val / 24), mode: 'lines', name: 'North Pole', line: { color: 'red' } }
            ];

            Plotly.newPlot('plot-instant', instantPlotData, { title: 'Energy', yaxis: { title: 'watts/m^2', range: [0, 1400] }, xaxis: { title: 'time (hr)', range: [0, 24] } });
            Plotly.newPlot('plot-cumulative', cumulativePlotData, { title: 'Energy', yaxis: { title: 'watts/m^2', range: [0, 14000] }, xaxis: { title: 'time (hr)', range: [0, 24] } });
        }

        update();

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();
    </script>
</body>
</html>
