        #control-panel {
            margin-bottom: 20px;
            display: flex;
            align-items: center;
        }
        #control-panel span {
            margin-right: 10px;
        }
        #canvas-container {
            border: 1px solid #ccc;
            width: 100%;
            height: 400px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <span>area</span>
            <input type="range" id="slider-area" min="1" max="101" step="1" value="13">
            <input type="number" id="input-area" min="1" max="101" step="1" value="13">
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        const congruentData = {
            5: { a: {n: 3, d: 2}, b: {n: 20, d: 3}, c: {n: 41, d: 6} },
            6: { a: {n: 3, d: 1}, b: {n: 4, d: 1}, c: {n: 5, d: 1} },
            7: { a: {n: 35, d: 12}, b: {n: 24, d: 5}, c: {n: 337, d: 60} },
            13: { a: {n: 780, d: 323}, b: {n: 323, d: 30}, c: {n: 106921, d: 9690} },
            79: { a: {n: 233126551, d: 167973000}, b: {n: 335946000, d: 2950969}, c: {n: 56434050774922081, d: 495683115837000} }
            // Add other congruent numbers here
        };

        let canvas;
        let areaValue;

        function setup() {
            canvas = createCanvas(windowWidth * 0.8, 400);
            canvas.parent('canvas-container');
            areaValue = 13; // Initial value
            drawTriangle(areaValue);

            document.getElementById('slider-area').addEventListener('input', syncInputs);
            document.getElementById('input-area').addEventListener('input', syncInputs);
        }

        function syncInputs(event) {
            const newValue = event.target.value;
            document.getElementById('slider-area').value = newValue;
            document.getElementById('input-area').value = newValue;
            areaValue = parseInt(newValue);
            drawTriangle(areaValue);
        }

        function drawTriangle(n) {
            clear();
            if (congruentData[n]) {
                const { a, b, c } = congruentData[n];
                const aVal = a.n / a.d;
                const bVal = b.n / b.d;
                const cVal = c.n / c.d;

                const padding = 50;
                const scale = min((width - padding) / aVal, (height - padding) / bVal);

                const ax = padding;
                const ay = height - padding;
                const bx = padding;
                const by = ay - bVal * scale;
                const cx = ax + aVal * scale;
                const cy = by;

                fill('#FFF2E5');
                stroke('#000000');
                strokeWeight(2);
                beginShape();
                vertex(ax, ay);
                vertex(bx, by);
                vertex(cx, cy);
                endShape(CLOSE);

                drawFraction(a, (ax + cx) / 2, ay + 10, 0, 16); // Base (a)
                drawFraction(b, bx + 10, (ay + by) / 2, 0, 16); // Height (b)
                drawFraction(c, (bx + cx) / 2, (by + cy) / 2, atan2(cy - by, cx - bx), 16); // Hypotenuse (c)
            } else {
                textAlign(CENTER, CENTER);
                textSize(20);
                fill('#FF0000');
                text(`${n} is not a congruent number`, width / 2, height / 2);
            }
        }

        function drawFraction(fractionObject, x, y, rotation, fontSize) {
            push();
            translate(x, y);
            rotate(rotation);
            textAlign(CENTER, CENTER);
            textSize(fontSize);
            fill('#8B5A2B');

            const numStr = fractionObject.n.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
            const denStr = fractionObject.d.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
            const maxWidth = max(textWidth(numStr), textWidth(denStr));

            text(numStr, 0, -fontSize / 2);
            line(-maxWidth / 2, -fontSize / 6, maxWidth / 2, -fontSize / 6);
            text(denStr, 0, fontSize / 2);

            pop();
        }
    </script>
</body>
</html>
