                </div>
                <div class="control-item">
                    <label for="slider-r">r</label>
                    <input type="range" id="slider-r" min="0.1" max="2.0" step="0.01" value="1.0">
                    <span id="label-r">1.0</span>
                </div>
            </div>
        </div>

        <!-- 2. Problem Control Panel -->
        <div id="problem-controls-panel">
            <div class="problem-column">
                <p>primal problem: minimize costs C given volume Q</p>
                <div class="control-item">
                    <label for="slider-Q">Q</label>
                    <input type="range" id="slider-Q" min="1" max="10" step="0.1" value="3.0">
                    <span id="label-Q">3.0</span>
                </div>
                <button id="btn-Q-from-dual">Q from dual</button>
            </div>
            <div class="problem-column">
                <p>dual problem: maximize volume Q given costs C</p>
                <div class="control-item">
                    <label for="slider-C">C</label>
                    <input type="range" id="slider-C" min="1" max="10" step="0.1" value="4.0">
                    <span id="label-C">4.0</span>
                </div>
                <button id="btn-C-from-primal">C from primal</button>
            </div>
        </div>

        <!-- 3. Visualization Area -->
        <div id="visualization-area">
            <div id="plot-primal" class="plot-container"></div>
            <div id="plot-dual" class="plot-container"></div>
        </div>

        <!-- 4. Output Display -->
        <div id="output-area">
            <div id="output-primal" class="output-box">x = 3.00, y = 3.00, C = 6.00</div>
            <div id="output-dual" class="output-box">x = 2.00, y = 2.00, Q = 2.00</div>
        </div>

    </div>

    <!-- Three.js Library -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Initialize variables
        let scenePrimal, cameraPrimal, rendererPrimal, controlsPrimal;
        let sceneDual, cameraDual, rendererDual, controlsDual;

        let alpha = 0.5, beta = 0.5, w = 1.0, r = 1.0;
        let Q = 3.0, C = 4.0;

        // Initialize scenes
        function initScenes() {
            // Primal Scene
            scenePrimal = new THREE.Scene();
            cameraPrimal = new THREE.PerspectiveCamera(75, 0.5, 0.1, 1000);
            rendererPrimal = new THREE.WebGLRenderer({ antialias: true });
            rendererPrimal.setSize(document.getElementById('plot-primal').clientWidth, document.getElementById('plot-primal').clientHeight);
            document.getElementById('plot-primal').appendChild(rendererPrimal.domElement);
            controlsPrimal = new THREE.OrbitControls(cameraPrimal, rendererPrimal.domElement);

            // Dual Scene
            sceneDual = new THREE.Scene();
            cameraDual = new THREE.PerspectiveCamera(75, 0.5, 0.1, 1000);
            rendererDual = new THREE.WebGLRenderer({ antialias: true });
            rendererDual.setSize(document.getElementById('plot-dual').clientWidth, document.getElementById('plot-dual').clientHeight);
            document.getElementById('plot-dual').appendChild(rendererDual.domElement);
            controlsDual = new THREE.OrbitControls(cameraDual, rendererDual.domElement);

            // Common setup
            [scenePrimal, sceneDual].forEach(scene => {
                const light = new THREE.AmbientLight(0x404040); // soft white light
                scene.add(light);

                const axesHelper = new THREE.AxesHelper(10);
                scene.add(axesHelper);

                const boxHelper = new THREE.BoxHelper(new THREE.Mesh(new THREE.BoxGeometry(10, 10, 10)), 0xaaaaaa);
                scene.add(boxHelper);
            });

            cameraPrimal.position.z = 20;
            cameraDual.position.z = 20;
        }

        // Update function
        function update() {
            // Update labels
            document.getElementById('label-alpha').textContent = alpha.toFixed(2);
            document.getElementById('label-beta').textContent = beta.toFixed(2);
            document.getElementById('label-w').textContent = w.toFixed(2);
            document.getElementById('label-r').textContent = r.toFixed(2);
            document.getElementById('label-Q').textContent = Q.toFixed(1);
            document.getElementById('label-C').textContent = C.toFixed(1);

            // Primal Problem Solver
            const k = (w * beta) / (r * alpha);
            const x_p = Math.pow(Q / Math.pow(k, beta), 1 / (alpha + beta));
            const y_p = x_p * k;
            const C_out = w * x_p + r * y_p;
            document.getElementById('output-primal').textContent = `x = ${x_p.toFixed(2)}, y = ${y_p.toFixed(2)}, C = ${C_out.toFixed(2)}`;

            // Dual Problem Solver
            const x_d = (C * alpha) / (w * (alpha + beta));
            const y_d = (C * beta) / (r * (alpha + beta));
            const Q_out = Math.pow(x_d, alpha) * Math.pow(y_d, beta);
            document.getElementById('output-dual').textContent = `x = ${x_d.toFixed(2)}, y = ${y_d.toFixed(2)}, Q = ${Q_out.toFixed(2)}`;

            // Redraw plots
            drawPrimalPlot(x_p, y_p, C_out);
            drawDualPlot(x_d, y_d, Q_out);
        }

        // Draw Primal Plot
        function drawPrimalPlot(x_p, y_p, C_out) {
            scenePrimal.clear();
            const light = new THREE.AmbientLight(0x404040);
            scenePrimal.add(light);
            const axesHelper = new THREE.AxesHelper(10);
            scenePrimal.add(axesHelper);
            const boxHelper = new THREE.BoxHelper(new THREE.Mesh(new THREE.BoxGeometry(10, 10, 10)), 0xaaaaaa);
            scenePrimal.add(boxHelper);

            // Isoquant Curtain
            const isoquantPoints = [];
            for (let x = 0.1; x <= 10; x += 0.1) {
                const y = Math.pow(Q / Math.pow(x, alpha), 1 / beta);
                isoquantPoints.push(new THREE.Vector3(x, y, 0));
            }
            const isoquantGeometry = new THREE.BufferGeometry().setFromPoints(isoquantPoints);
            const isoquantMaterial = new THREE.MeshBasicMaterial({ color: 0xffa500, side: THREE.DoubleSide });
            const isoquantMesh = new THREE.Mesh(isoquantGeometry, isoquantMaterial);
            isoquantMesh.geometry.attributes.position.array.forEach((v, i) => {
                if (i % 3 === 2) isoquantMesh.geometry.attributes.position.array[i] = 10;
            });
            isoquantMesh.geometry.attributes.position.needsUpdate = true;
            scenePrimal.add(isoquantMesh);

            // Isocost Plane
            const isocostGeometry = new THREE.PlaneGeometry(10, 10);
            const isocostMaterial = new THREE.MeshBasicMaterial({ color: 0x808080, transparent: true, opacity: 0.5 });
            const isocostMesh = new THREE.Mesh(isocostGeometry, isocostMaterial);
            isocostMesh.rotation.x = -Math.PI / 2;
            isocostMesh.position.set(x_p, y_p, C_out);
            scenePrimal.add(isocostMesh);

            // Solution Vector
            const solutionGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(x_p, y_p, C_out)
            ]);
            const solutionMaterial = new THREE.LineBasicMaterial({ color: 0x0000ff, linewidth: 5 });
            const solutionLine = new THREE.Line(solutionGeometry, solutionMaterial);
            scenePrimal.add(solutionLine);

            rendererPrimal.render(scenePrimal, cameraPrimal);
        }

        // Draw Dual Plot
        function drawDualPlot(x_d, y_d, Q_out) {
            sceneDual.clear();
            const light = new THREE.AmbientLight(0x404040);
            sceneDual.add(light);
            const axesHelper = new THREE.AxesHelper(10);
            sceneDual.add(axesHelper);
            const boxHelper = new THREE.BoxHelper(new THREE.Mesh(new THREE.BoxGeometry(10, 10, 10)), 0xaaaaaa);
            sceneDual.add(boxHelper);

            // Production Surface
            const productionSurfaceGeometry = new THREE.ParametricGeometry((u, v, target) => {
                const x = u * 10;
                const y = v * 10;
                const z = Math.pow(x, alpha) * Math.pow(y, beta);
                target.set(x, y, z);
            }, 50, 50);
            const productionSurfaceMaterial = new THREE.MeshBasicMaterial({ color: 0xffa500, wireframe: false });
            const productionSurfaceMesh = new THREE.Mesh(productionSurfaceGeometry, productionSurfaceMaterial);
            sceneDual.add(productionSurfaceMesh);

            // Budget Curtain
            const budgetPoints = [];
            for (let x = 0.1; x <= 10; x += 0.1) {
                const y = (C - w * x) / r;
                budgetPoints.push(new THREE.Vector3(x, y, 0));
            }
            const budgetGeometry = new THREE.BufferGeometry().setFromPoints(budgetPoints);
            const budgetMaterial = new THREE.MeshBasicMaterial({ color: 0x808080, transparent: true, opacity: 0.5 });
            const budgetMesh = new THREE.Mesh(budgetGeometry, budgetMaterial);
            budgetMesh.geometry.attributes.position.array.forEach((v, i) => {
                if (i % 3 === 2) budgetMesh.geometry.attributes.position.array[i] = 10;
            });
            budgetMesh.geometry.attributes.position.needsUpdate = true;
            sceneDual.add(budgetMesh);

            // Intersection Curve
            const intersectionPoints = [];
            for (let x = 0.1; x <= 10; x += 0.1) {
                const y = (C - w * x) / r;
                const q = Math.pow(x, alpha) * Math.pow(y, beta);
                intersectionPoints.push(new THREE.Vector3(x, y, q));
            }
            const intersectionGeometry = new THREE.BufferGeometry().setFromPoints(intersectionPoints);
            const intersectionMaterial = new THREE.LineBasicMaterial({ color: 0xff0000, linewidth: 5 });
            const intersectionLine = new THREE.Line(intersectionGeometry, intersectionMaterial);
            sceneDual.add(intersectionLine);

            // Optimal Point Marker
            const optimalPointGeometry = new THREE.SphereGeometry(0.2, 32, 32);
            const optimalPointMaterial = new THREE.MeshBasicMaterial({ color: 0x808080 });
            const optimalPointMesh = new THREE.Mesh(optimalPointGeometry, optimalPointMaterial);
            optimalPointMesh.position.set(x_d, y_d, Q_out);
            sceneDual.add(optimalPointMesh);

            rendererDual.render(sceneDual, cameraDual);
        }

        // Event listeners
        document.getElementById('slider-alpha').addEventListener('input', () => {
            alpha = parseFloat(document.getElementById('slider-alpha').value);
            update();
        });
        document.getElementById('slider-beta').addEventListener('input', () => {
            beta = parseFloat(document.getElementById('slider-beta').value);
            update();
        });
        document.getElementById('slider-w').addEventListener('input', () => {
            w = parseFloat(document.getElementById('slider-w').value);
            update();
        });
        document.getElementById('slider-r').addEventListener('input', () => {
            r = parseFloat(document.getElementById('slider-r').value);
            update();
        });
        document.getElementById('slider-Q').addEventListener('input', () => {
            Q = parseFloat(document.getElementById('slider-Q').value);
            update();
        });
        document.getElementById('slider-C').addEventListener('input', () => {
            C = parseFloat(document.getElementById('slider-C').value);
            update();
        });
        document.getElementById('btn-reset').addEventListener('click', () => {
            document.getElementById('slider-alpha').value = 0.5;
            document.getElementById('slider-beta').value = 0.5;
            document.getElementById('slider-w').value = 1.0;
            document.getElementById('slider-r').value = 1.0;
            document.getElementById('slider-Q').value = 3.0;
            document.getElementById('slider-C').value = 4.0;
            alpha = 0.5; beta = 0.5; w = 1.0; r = 1.0; Q = 3.0; C = 4.0;
            update();
        });
        document.getElementById('btn-Q-from-dual').addEventListener('click', () => {
            Q = parseFloat(document.getElementById('output-dual').textContent.split(',')[2].split('=')[1]);
            document.getElementById('slider-Q').value = Q;
            update();
        });
        document.getElementById('btn-C-from-primal').addEventListener('click', () => {
            C = parseFloat(document.getElementById('output-primal').textContent.split(',')[2].split('=')[1]);
            document.getElementById('slider-C').value = C;
            update();
        });

        // Initialize and render
        initScenes();
        update();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            rendererPrimal.render(scenePrimal, cameraPrimal);
            rendererDual.render(sceneDual, cameraDual);
        }
        animate();
    </script>
</body>
