<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Flash Distillation Unit Simulation</title>
    <style>
        #demo-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
        }
        #control-panel {
            width: 100%;
            max-width: 600px;
            margin-bottom: 20px;
        }
        .control-row {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 10px;
        }
        #visualization-area {
            width: 800px;
            height: 500px;
            background-color: #F0F0F0;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <!-- Flash Pressure Slider -->
            <div class="control-row">
                <label for="slider-pressure">flash pressure (kPa)</label>
                <input type="range" id="slider-pressure" min="100" max="1000" step="1" value="180">
                <span id="pressure-value">180</span>
            </div>
            <!-- Heat Load Slider -->
            <div class="control-row">
                <label for="slider-heat">heat load (kJ/hr)</label>
                <input type="range" id="slider-heat" min="0" max="10000" step="100" value="0">
                <span id="heat-value">0</span>
            </div>
        </div>

        <!-- Section 2: Visualization Area -->
        <div id="visualization-area">
            <!-- p5.js canvas will be inserted here by the script -->
        </div>
    </div>

    <!-- CDN for p5.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        // Constants
        const F = 10; // Feed Flow (kmol/hr)
        const T_F = 323.2; // Feed Temperature (K)
        const P_F = 1000; // Feed Pressure (kPa)
        const z = [0.25, 0.25, 0.25, 0.25]; // Feed Mole Fractions
        const antoineConstants = [
            { A: 6.80165, B: 1005.157, C: -27.159 }, // n-butane
            { A: 6.86177, B: 1184.249, C: -35.223 }, // n-pentane
            { A: 6.90237, B: 1358.59, C: -51.303 }, // n-heptane
            { A: 6.92383, B: 1431.149, C: -59.501 }  // n-octane
        ];
        const physicalProperties = [
            { Cp_L: 132, ΔH_vap: 22400 }, // n-butane
            { Cp_L: 161, ΔH_vap: 25800 }, // n-pentane
            { Cp_L: 225, ΔH_vap: 31800 }, // n-heptane
            { Cp_L: 255, ΔH_vap: 34400 }  // n-octane
        ];

        let results = {};

        function calculateFlash(P_flash, Q_hr) {
            function Psat(T, i) {
                const { A, B, C } = antoineConstants[i];
                return Math.pow(10, A - B / (T + C));
            }

            function K(T, i) {
                return Psat(T, i) / P_flash;
            }

            function rachfordRice(V, T) {
                let sum = 0;
                for (let i = 0; i < 4; i++) {
                    sum += z[i] * (K(T, i) - 1) / (1 + V * (K(T, i) - 1));
                }
                return sum;
            }

            function energyBalance(T, V) {
                let sumCp = 0, sumHvap = 0;
                for (let i = 0; i < 4; i++) {
                    sumCp += z[i] * physicalProperties[i].Cp_L * (T_F - T);
                    sumHvap += results.y[i] * physicalProperties[i].ΔH_vap;
                }
                return Q_hr / F + sumCp - V * sumHvap;
            }

            function secant(f, x0, x1, tolerance = 1e-6, maxIterations = 100) {
                let f0 = f(x0), f1 = f(x1);
                for (let i = 0; i < maxIterations; i++) {
                    let x2 = x1 - f1 * (x1 - x0) / (f1 - f0);
                    if (Math.abs(x2 - x1) < tolerance) return x2;
                    x0 = x1; f0 = f1;
                    x1 = x2; f1 = f(x1);
                }
                throw new Error("Secant method did not converge");
            }

            let T_min = 273, T_max = 500;
            let T_flash = secant(energyBalance, T_min, T_max);

            let V_frac = secant(rachfordRice, 0, 1, 1e-6, 100);

            results.x = [];
            results.y = [];
            for (let i = 0; i < 4; i++) {
                results.x.push(z[i] / (1 + V_frac * (K(T_flash, i) - 1)));
                results.y.push(results.x[i] * K(T_flash, i));
            }

            return { T_flash, V_frac, ...results };
        }

        function updateUI() {
            const P_flash = parseFloat(document.getElementById('slider-pressure').value);
            const Q_hr = parseFloat(document.getElementById('slider-heat').value);
            document.getElementById('pressure-value').textContent = P_flash;
            document.getElementById('heat-value').textContent = Q_hr;
            results = calculateFlash(P_flash, Q_hr);
            redraw();
        }

        let myCanvas;

        function setup() {
            myCanvas = createCanvas(800, 500);
            myCanvas.parent('visualization-area');
            textAlign(CENTER, CENTER);
            textSize(14);
            updateUI();
        }

        function draw() {
            background('#F0F0F0');

            // Flash Vessel
            fill('#E6E6FA');
            stroke(0);
            rect(350, 200, 100, 100);
            fill(0);
            text('Flash', 400, 250);

            // Feed Stream
            stroke('#9ACD32');
            line(100, 250, 350, 250);
            triangle(350, 250, 360, 245, 360, 255);

            // Vapor Stream
            stroke('#9370DB');
            line(400, 200, 400, 100);
            line(400, 100, 550, 100);
            triangle(550, 100, 545, 110, 545, 90);

            // Liquid Stream
            stroke('#FFA500');
            line(400, 300, 400, 400);
            line(400, 400, 550, 400);
            triangle(550, 400, 545, 390, 545, 410);

            // Heat Input
            stroke('#FF0000');
            strokeWeight(2);
            for (let i = 0; i < 20; i++) {
                line(350 + i * 5, 200 - i * 5, 350 + (i + 1) * 5, 200 - (i + 1) * 5);
            }
            triangle(350, 200, 345, 210, 355, 210);

            // Text Labels
            noStroke();
            fill(0);

            // Feed Info (left)
            text(`Feed: F = ${F} kmol/hr\nT_F = ${T_F} K\nP_F = ${P_F} kPa\nz = [${z.join(', ')}]`, 200, 100);

            // Vapor Info (top right)
            text(`Vapor:\ny = [${results.y.map(v => v.toFixed(2)).join(', ')}]`, 600, 100);

            // Liquid Info (bottom right)
            text(`Liquid:\nx = [${results.x.map(v => v.toFixed(2)).join(', ')}]`, 600, 400);

            // Process Info (middle right)
            text(`Vapor Fraction: ${(results.V_frac * 100).toFixed(2)}%\nFlash Temp: ${results.T_flash.toFixed(2)} K\nFlash Press: ${parseFloat(document.getElementById('slider-pressure').value)} kPa`, 600, 250);

            // Heat Info (Q)
            text(`Q = ${parseFloat(document.getElementById('slider-heat').value)} kJ/hr`, 350, 190);
        }

        function redraw() {
            myCanvas.redraw();
        }

        document.getElementById('slider-pressure').addEventListener('input', updateUI);
        document.getElementById('slider-heat').addEventListener('input', updateUI);
    </script>
</body>
</html>
