        const moonSurface = new THREE.Mesh(moonSurfaceGeometry, moonSurfaceMaterial);
        moonSurface.rotation.x = -Math.PI / 2;
        moonSurface.position.y = -0.5;

        // Create moon for space view
        const moonGeometry = new THREE.SphereGeometry(0.5, 32, 32);
        const moonMaterial = new THREE.MeshStandardMaterial({ color: 0x8B4513 });
        const moon = new THREE.Mesh(moonGeometry, moonMaterial);

        // Create earth
        const earthGeometry = new THREE.SphereGeometry(0.3, 32, 32);
        const earthMaterial = new THREE.MeshPhongMaterial({ color: 0x0077be });
        const earth = new THREE.Mesh(earthGeometry, earthMaterial);
        earth.castShadow = true;
        earth.receiveShadow = true;

        // Create sun
        const sunGeometry = new THREE.SphereGeometry(0.2, 32, 32);
        const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00, emissive: 0xffff00 });
        const sun = new THREE.Mesh(sunGeometry, sunMaterial);
        sun.castShadow = true;

        // Add atmosphere to earth
        const atmosphereGeometry = new THREE.SphereGeometry(0.32, 32, 32);
        const atmosphereMaterial = new THREE.MeshBasicMaterial({
            color: 0x0077be,
            blending: THREE.AdditiveBlending,
            transparent: true,
            opacity: 0.5
        });
        const atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);

        // Create constellations
        const constellationGroup = new THREE.Group();
        const starGeometry = new THREE.BufferGeometry();
        const starVertices = [];
        const starColors = [];

        // Example constellation: Orion
        const orionStars = [
            { position: [7.15, 6.20, 1.56], color: [1, 1, 1] }, // Betelgeuse
            { position: [6.81, 7.78, 2.36], color: [1, 1, 1] }, // Bellatrix
            { position: [5.96, 7.24, 2.04], color: [1, 1, 1] }, // Mintaka
            { position: [5.96, 6.68, 1.80], color: [1, 1, 1] }, // Alnilam
            { position: [5.96, 6.12, 1.56], color: [1, 1, 1] }, // Alnitak
            { position: [6.20, 6.12, 1.56], color: [1, 1, 1] }, // Saiph
            { position: [6.54, 6.68, 1.80], color: [1, 1, 1] }  // Rigel
        ];

        orionStars.forEach(star => {
            starVertices.push(...star.position);
            starColors.push(...star.color);
        });

        starGeometry.setAttribute('position', new THREE.Float32BufferAttribute(starVertices, 3));
        starGeometry.setAttribute('color', new THREE.Float32BufferAttribute(starColors, 3));

        const starMaterial = new THREE.PointsMaterial({ size: 0.1, vertexColors: true });
        const stars = new THREE.Points(starGeometry, starMaterial);
        constellationGroup.add(stars);

        // Add constellation lines
        const lineGeometry = new THREE.BufferGeometry();
        const lineVertices = [];
        const orionLines = [
            [0, 1], [1, 2], [2, 3], [3, 4], [4, 5], [4, 6]
        ];

        orionLines.forEach(line => {
            lineVertices.push(...orionStars[line[0]].position);
            lineVertices.push(...orionStars[line[1]].position);
        });

        lineGeometry.setAttribute('position', new THREE.Float32BufferAttribute(lineVertices, 3));
        const lineMaterial = new THREE.LineBasicMaterial({ color: 0xffffff });
        const lines = new THREE.LineSegments(lineGeometry, lineMaterial);
        constellationGroup.add(lines);

        // Add ambient light
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        // Add directional light (sun)
        const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
        directionalLight.position.set(5, 5, 5);
        directionalLight.castShadow = true;
        directionalLight.shadow.mapSize.width = 1024;
        directionalLight.shadow.mapSize.height = 1024;
        directionalLight.shadow.camera.near = 0.1;
        directionalLight.shadow.camera.far = 50;
        scene.add(directionalLight);

        // Add objects to scene
        scene.add(moonSurface);
        scene.add(moon);
        scene.add(earth);
        scene.add(atmosphere);
        scene.add(constellationGroup);
        scene.add(sun);

        // Camera setup
        let viewMode = 'land';
        function setViewMode(mode) {
            viewMode = mode;
            if (mode === 'land') {
                camera.position.set(0, 0, 2);
                camera.lookAt(0, 0, 0);
                moonSurface.visible = true;
                moon.visible = false;
                earth.visible = false;
                sun.visible = false;
            } else if (mode === 'space') {
                camera.position.set(0, 5, 10);
                camera.lookAt(0, 0, 0);
                moonSurface.visible = false;
                moon.visible = true;
                earth.visible = true;
                sun.visible = true;
            }
        }
        setViewMode('land');

        // Controls
        const params = {
            time: 0,
            nodalAngle: 0,
            sunEarthAngle: Math.PI / 2,
            zodiac: 0
        };

        document.getElementById('btn-land').addEventListener('click', () => {
            setViewMode('land');
            document.getElementById('btn-land').classList.add('active');
            document.getElementById('btn-space').classList.remove('active');
        });

        document.getElementById('btn-space').addEventListener('click', () => {
            setViewMode('space');
            document.getElementById('btn-space').classList.add('active');
            document.getElementById('btn-land').classList.remove('active');
        });

        const updateSliderValue = (sliderId, valueId, value) => {
            document.getElementById(valueId).textContent = value.toFixed(2);
            params[sliderId.replace('slider-', '')] = parseFloat(value);
        };

        document.getElementById('slider-time').addEventListener('input', (e) => {
            updateSliderValue('slider-time', 'slider-time-value', e.target.value);
        });

        document.getElementById('slider-nodal-angle').addEventListener('input', (e) => {
            updateSliderValue('slider-nodal-angle', 'slider-nodal-angle-value', e.target.value);
        });

        document.getElementById('slider-sun-earth-angle').addEventListener('input', (e) => {
            updateSliderValue('slider-sun-earth-angle', 'slider-sun-earth-angle-value', e.target.value);
        });

        document.getElementById('slider-zodiac').addEventListener('input', (e) => {
            updateSliderValue('slider-zodiac', 'slider-zodiac-value', e.target.value);
        });

        const isAnimating = {
            time: false,
            nodalAngle: false,
            sunEarthAngle: false,
            zodiac: false
        };

        const toggleAnimation = (sliderId, btnId) => {
            isAnimating[sliderId.replace('slider-', '')] = !isAnimating[sliderId.replace('slider-', '')];
            const button = document.getElementById(btnId);
            button.classList.toggle('paused');
        };

        document.getElementById('btn-time-play').addEventListener('click', () => {
            toggleAnimation('slider-time', 'btn-time-play');
        });

        document.getElementById('btn-nodal-angle-play').addEventListener('click', () => {
            toggleAnimation('slider-nodal-angle', 'btn-nodal-angle-play');
        });

        document.getElementById('btn-sun-earth-angle-play').addEventListener('click', () => {
            toggleAnimation('slider-sun-earth-angle', 'btn-sun-earth-angle-play');
        });

        document.getElementById('btn-zodiac-play').addEventListener('click', () => {
            toggleAnimation('slider-zodiac', 'btn-zodiac-play');
        });

        document.getElementById('checkbox-constellations').addEventListener('change', (e) => {
            constellationGroup.visible = e.target.checked;
        });

        document.getElementById('checkbox-realistic-size').addEventListener('change', (e) => {
            const scaleFactor = e.target.checked ? 0.1 : 1;
            earth.scale.set(scaleFactor, scaleFactor, scaleFactor);
        });

        // Animation loop
        let lastFrameTime = performance.now();

        const animate = (currentTime) => {
            requestAnimationFrame(animate);

            const deltaTime = (currentTime - lastFrameTime) / 1000;
            lastFrameTime = currentTime;

            if (isAnimating.time) {
                params.time += deltaTime * 0.5;
                document.getElementById('slider-time').value = params.time;
                updateSliderValue('slider-time', 'slider-time-value', params.time);
            }

            if (isAnimating.nodalAngle) {
                params.nodalAngle += deltaTime * 0.5;
                document.getElementById('slider-nodal-angle').value = params.nodalAngle;
                updateSliderValue('slider-nodal-angle', 'slider-nodal-angle-value', params.nodalAngle);
            }

            if (isAnimating.sunEarthAngle) {
                params.sunEarthAngle += deltaTime * 0.5;
                document.getElementById('slider-sun-earth-angle').value = params.sunEarthAngle;
                updateSliderValue('slider-sun-earth-angle', 'slider-sun-earth-angle-value', params.sunEarthAngle);
            }

            if (isAnimating.zodiac) {
                params.zodiac += deltaTime * 0.5;
                document.getElementById('slider-zodiac').value = params.zodiac;
                updateSliderValue('slider-zodiac', 'slider-zodiac-value', params.zodiac);
            }

            // Update positions and rotations based on parameters
            const earthOrbitRadius = 2;
            const earthAngle = params.time * Math.PI * 2;
            earth.position.set(
                earthOrbitRadius * Math.cos(earthAngle + params.sunEarthAngle),
                0,
                earthOrbitRadius * Math.sin(earthAngle + params.sunEarthAngle)
            );

            const sunOrbitRadius = 4;
            const sunAngle = params.time * Math.PI * 2;
            sun.position.set(
                sunOrbitRadius * Math.cos(sunAngle),
                0,
                sunOrbitRadius * Math.sin(sunAngle)
            );

            moon.rotation.y = params.nodalAngle;
            constellationGroup.rotation.y = params.zodiac;

            // Check for eclipse
            const earthToMoon = new THREE.Vector3().subVectors(new THREE.Vector3(), earth.position);
            const earthToSun = new THREE.Vector3().subVectors(sun.position, earth.position);
            const earthToSunDist = earthToSun.length();
            const earthToMoonDist = earthToMoon.length();

            if (earthToSunDist < earthToMoonDist && earthToSunDist < earth.geometry.parameters.radius + sun.geometry.parameters.radius) {
                earth.material.color.set(0xff0000);
            } else {
                earth.material.color.set(0x0077be);
            }

            renderer.render(scene, camera);
        };

        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });

        // Render MathJax
        MathJax.startup.promise.then(() => {
            MathJax.typesetPromise([document.getElementById('slider-sun-earth-angle-value')]);
        });
    </script>
</body>
</html>
