        <div>
            <label for="slider-transition-pace">Transition to constant reduction pace (years)</label>
            <input type="range" id="slider-transition-pace" min="0" max="100" step="1" value="25">
            <span id="value-transition-pace">25</span>
        </div>
        <div>
            <label for="slider-wedges">Number of constant pace wedges (above emission stabilization at t=0)</label>
            <input type="range" id="slider-wedges" min="0" max="10" step="0.01" value="4">
            <span id="value-wedges">4.00</span>
        </div>
    </div>
    <div id="plot-div"></div>
    <script>
        const C0 = 850;
        const E0 = 8.5;
        const C_eq = 586;
        const E_floor = 1.5;
        const rise_rate = 0.02;
        const time_step = 0.5;
        const max_time = 300;

        const sliderResidenceTime = document.getElementById('slider-residence-time');
        const sliderTransitionPace = document.getElementById('slider-transition-pace');
        const sliderWedges = document.getElementById('slider-wedges');

        const valueResidenceTime = document.getElementById('value-residence-time');
        const valueTransitionPace = document.getElementById('value-transition-pace');
        const valueWedges = document.getElementById('value-wedges');

        function updateValueDisplay() {
            valueResidenceTime.textContent = sliderResidenceTime.value;
            valueTransitionPace.textContent = sliderTransitionPace.value;
            valueWedges.textContent = parseFloat(sliderWedges.value).toFixed(2);
            updatePlot();
        }

        sliderResidenceTime.addEventListener('input', updateValueDisplay);
        sliderTransitionPace.addEventListener('input', updateValueDisplay);
        sliderWedges.addEventListener('input', updateValueDisplay);

        function simulateModel(t_res, t_trans, N_wedges) {
            const times = [];
            const emissions = [];
            const carbonConcentration = [];

            let E_t = E0;
            let C_t = C0;

            for (let t = 0; t <= max_time; t += time_step) {
                times.push(t);

                if (t < t_trans) {
                    E_t = E0 + rise_rate * t;
                } else {
                    const reduction_rate = N_wedges / 50;
                    const E_linear = E_t - reduction_rate * (t - t_trans);
                    E_t = Math.max(E_floor, E_linear);
                }

                emissions.push(100 * E_t);

                const dC = (E_t - (C_t - C_eq) / t_res) * time_step;
                C_t += dC;
                carbonConcentration.push(C_t);
            }

            return { times, emissions, carbonConcentration };
        }

        function updatePlot() {
            const t_res = parseFloat(sliderResidenceTime.value);
            const t_trans = parseFloat(sliderTransitionPace.value);
            const N_wedges = parseFloat(sliderWedges.value);

            const { times, emissions, carbonConcentration } = simulateModel(t_res, t_trans, N_wedges);

            const traceEmissions = {
                x: times,
                y: emissions,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'purple', width: 2 },
                name: 'Annual Emissions (GtC/yr × 100)'
            };

            const traceCarbonConcentration = {
                x: times,
                y: carbonConcentration,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue', width: 2 },
                name: 'Atmospheric Carbon (GtC)'
            };

            const traceTargetCeiling = {
                x: [0, max_time],
                y: [1200, 1200],
                type: 'scatter',
                mode: 'lines',
                line: { color: 'black', width: 2, dash: 'dash' },
                name: 'Target Ceiling'
            };

            const data = [traceEmissions, traceCarbonConcentration, traceTargetCeiling];

            const layout = {
                title: 'Atmospheric carbon (GtC; blue) and annual emissions (GtC/yr × 100; purple) versus time',
                xaxis: { title: 'years', range: [0, max_time], zeroline: true },
                yaxis: { title: 'GtC or GtC/yr × 100', range: [0, 1600], zeroline: true },
                showlegend: false,
                margin: { l: 60, r: 20, b: 40, t: 80 },
                paper_bgcolor: '#f9f9f9',
                plot_bgcolor: 'white',
                grid: { color: '#ddd' }
            };

            Plotly.newPlot('plot-div', data, layout);
        }

        updatePlot();
    </script>
</body>
</html>
