</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-theta0">θ<sub>₀</sub></label>
                <input type="range" id="slider-theta0" min="-3.14159" max="3.14159" step="0.01" value="0.5236">
                <span id="value-theta0">π/6</span>
            </div>
            <div class="control-group">
                <label for="slider-v0">v<sub>₀</sub></label>
                <input type="range" id="slider-v0" min="-5" max="5" step="0.1" value="0">
                <span id="value-v0">0</span>
            </div>
            <div class="control-group">
                <label for="slider-length">connector length</label>
                <input type="range" id="slider-length" min="0.5" max="4" step="0.1" value="2">
                <span id="value-length">2</span>
            </div>
            <div class="control-group">
                <label for="btn-play-pause">animate</label>
                <div>
                    <button id="btn-play-pause">▶</button>
                    <button id="btn-reset">|<</button>
                </div>
            </div>
        </div>
        <div id="visualization"></div>
    </div>

    <script>
        let isPlaying = false;
        let theta, theta_dot, x, x_dot;
        const m = 1; // mass of the bob
        const M = 1; // mass of the support
        const g = 9.8; // gravity
        const dt = 0.01; // time step
        const scale = 50; // pixels per meter

        function setup() {
            createCanvas(500, 500);
            background('#F0F0F0');
            resetSimulation();
        }

        function draw() {
            if (isPlaying) {
                updatePhysics();
            }
            renderPendulum();
        }

        function updatePhysics() {
            const l = parseFloat(document.getElementById('slider-length').value);
            const theta_ddot = (-m * l * sin(theta) * cos(theta) * theta_dot ** 2 - g * (M + m) * sin(theta)) / (l * (M + m * sin(theta) ** 2));
            theta_dot += theta_ddot * dt;
            theta += theta_dot * dt;
            x_dot = parseFloat(document.getElementById('slider-v0').value) - (m * l * cos(theta) * theta_dot) / (M + m);
            x += x_dot * dt;
        }

        function renderPendulum() {
            background('#F0F0F0');
            const px = width / 2 + x * scale;
            const py = height / 2;
            const bx = px + (parseFloat(document.getElementById('slider-length').value) * scale) * sin(theta);
            const by = py + (parseFloat(document.getElementById('slider-length').value) * scale) * cos(theta);

            fill('red');
            noStroke();
            rect(px - 5, py - 5, 10, 10);

            stroke('black');
            strokeWeight(2);
            line(px, py, bx, by);

            fill('orange');
            stroke('black');
            strokeWeight(2);
            ellipse(bx, by, 20, 20);
        }

        function resetSimulation() {
            isPlaying = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            theta = parseFloat(document.getElementById('slider-theta0').value);
            theta_dot = 0;
            x = 0;
            x_dot = parseFloat(document.getElementById('slider-v0').value);
            renderPendulum();
        }

        document.getElementById('slider-theta0').addEventListener('input', function() {
            const value = parseFloat(this.value);
            let displayValue;
            if (Math.abs(value - Math.PI / 6) < 0.01) displayValue = 'π/6';
            else if (Math.abs(value - Math.PI / 3) < 0.01) displayValue = 'π/3';
            else if (Math.abs(value - Math.PI / 2) < 0.01) displayValue = 'π/2';
            else if (Math.abs(value - Math.PI) < 0.01) displayValue = 'π';
            else if (Math.abs(value + Math.PI / 6) < 0.01) displayValue = '-π/6';
            else if (Math.abs(value + Math.PI / 3) < 0.01) displayValue = '-π/3';
            else if (Math.abs(value + Math.PI / 2) < 0.01) displayValue = '-π/2';
            else displayValue = value.toFixed(2);
            document.getElementById('value-theta0').textContent = displayValue;
            resetSimulation();
        });

        document.getElementById('slider-v0').addEventListener('input', function() {
            document.getElementById('value-v0').textContent = this.value;
            resetSimulation();
        });

        document.getElementById('slider-length').addEventListener('input', function() {
            document.getElementById('value-length').textContent = this.value;
            resetSimulation();
        });

        document.getElementById('btn-play-pause').addEventListener('click', function() {
            isPlaying = !isPlaying;
            this.textContent = isPlaying ? '||' : '▶';
        });

        document.getElementById('btn-reset').addEventListener('click', resetSimulation);
    </script>
</body>
</html>
