</head>
<body>
    <h1>Deformation Pattern in an Earthquake Source Region</h1>
    <div id="control-panel">
        <div class="slider-group">
            <label for="slider-strike">strike angle φ of fault plane clockwise from North (0° ≤ φ < 360°)</label>
            <input type="range" id="slider-strike" min="0" max="360" step="1" value="180">
            <input type="text" id="value-strike" value="180" readonly>
        </div>
        <div class="slider-group">
            <label for="slider-dip">dip angle δ of fault plane down from horizontal (0° ≤ δ ≤ 90°)</label>
            <input type="range" id="slider-dip" min="0" max="90" step="1" value="60">
            <input type="text" id="value-dip" value="60" readonly>
        </div>
        <div class="slider-group">
            <label for="slider-slip-angle">slip angle λ in fault plane between slip direction and strike (−180° ≤ λ < 180°)</label>
            <input type="range" id="slider-slip-angle" min="-180" max="180" step="1" value="90">
            <input type="text" id="value-slip-angle" value="90" readonly>
        </div>
        <div class="slider-group">
            <label for="slider-slip-amount">amount of slip of hanging wall (0 to 1)</label>
            <input type="range" id="slider-slip-amount" min="0" max="1" step="0.01" value="0.5">
            <input type="text" id="value-slip-amount" value="0.5" readonly>
        </div>
        <button id="btn-reset">↺</button>
    </div>
    <div id="visualization-container">
        <div>
            <input type="checkbox" id="checkbox-box">
            <label for="checkbox-box" id="checkbox-box-label">box</label>
        </div>
        <div id="canvas-container">
            <canvas id="webgl-canvas"></canvas>
        </div>
    </div>

    <!-- Include three.js from CDN -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Initialize scene, camera, and renderer
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('webgl-canvas') });
        renderer.setSize(window.innerWidth, window.innerHeight);

        // Set up camera position and OrbitControls
        camera.position.z = 5;
        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        // Add ambient and directional lights
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight1.position.set(1, 1, 1).normalize();
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight2.position.set(-1, -1, -1).normalize();
        scene.add(directionalLight2);

        // Create the sphere geometry and material
        const sphereGeometry = new THREE.SphereGeometry(1, 64, 32);
        const sphereMaterial = new THREE.MeshStandardMaterial({ vertexColors: THREE.VertexColors });
        const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
        scene.add(sphere);

        // Function to calculate P and T axes from fault parameters
        function calculateAxes(strike, dip, slipAngle) {
            const phi = strike * Math.PI / 180;
            const delta = dip * Math.PI / 180;
            const lambda = slipAngle * Math.PI / 180;

            const n = new THREE.Vector3(
                Math.sin(delta) * Math.cos(phi),
                Math.sin(lambda) * Math.cos(delta) + Math.cos(lambda) * Math.sin(delta) * Math.sin(phi),
                -Math.cos(lambda) * Math.cos(delta) + Math.sin(lambda) * Math.sin(delta) * Math.sin(phi)
            ).normalize();

            const u = new THREE.Vector3(
                -Math.cos(phi),
                Math.cos(delta) * Math.sin(phi),
                Math.sin(delta) * Math.sin(phi)
            ).normalize();

            const P = n.clone().add(u).normalize();
            const T = u.clone().sub(n).normalize();

            return { P, T };
        }

        // Function to color the sphere faces
        function colorSphereFaces(P, T) {
            const positions = sphereGeometry.attributes.position.array;
            const colors = [];

            for (let i = 0; i < positions.length; i += 9) {
                const centroid = new THREE.Vector3(
                    (positions[i] + positions[i + 3] + positions[i + 6]) / 3,
                    (positions[i + 1] + positions[i + 4] + positions[i + 7]) / 3,
                    (positions[i + 2] + positions[i + 5] + positions[i + 8]) / 3
                );

                const dotP = centroid.dot(P);
                const dotT = centroid.dot(T);

                if (dotP > 0 && dotT > 0) {
                    colors.push(1, 1, 1); // White for compressive quadrant
                } else {
                    colors.push(1, 0.65, 0); // Orange for dilatational quadrant
                }
            }

            sphereGeometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
        }

        // Function to deform the sphere
        function deformSphere(slipAmount, P, T) {
            const positions = sphereGeometry.attributes.position.array;
            const originalPositions = sphereGeometry.boundingBox.max.clone().multiplyScalar(2);

            for (let i = 0; i < positions.length; i += 3) {
                const vertex = new THREE.Vector3(positions[i], positions[i + 1], positions[i + 2]);
                const dotP = vertex.dot(P);
                const dotT = vertex.dot(T);

                const deformationScale = 0.1; // Adjust deformation scale as needed
                const displacement = deformationScale * slipAmount * ((dotT * dotT) - (dotP * dotP)) * vertex.length();

                positions[i] += vertex.x * displacement;
                positions[i + 1] += vertex.y * displacement;
                positions[i + 2] += vertex.z * displacement;
            }

            sphereGeometry.attributes.position.needsUpdate = true;
            sphereGeometry.computeVertexNormals();
        }

        // Function to create and add the compass rose
        function addCompassRose() {
            const curve = new THREE.EllipseCurve(
                0, 0,             // ax, aY
                1, 1,             // xRadius, yRadius
                0, 2 * Math.PI,   // aStartAngle, aEndAngle
                false,            // aClockwise
                0                 // aRotation
            );

            const points = curve.getPoints(50);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineDashedMaterial({ color: 0xffffff, dashSize: 0.1, gapSize: 0.05 });
            const line = new THREE.Line(geometry, material);
            line.rotation.x = Math.PI / 2;
            scene.add(line);

            // Add cardinal direction labels
            const directions = ['N', 'E', 'S', 'W'];
            const angles = [0, Math.PI / 2, Math.PI, 3 * Math.PI / 2];
            const fontLoader = new THREE.FontLoader();
            fontLoader.load('https://threejs.org/examples/fonts/helvetiker_regular.typeface.json', function(font) {
                directions.forEach((direction, index) => {
                    const textGeometry = new THREE.TextGeometry(direction, {
                        font: font,
                        size: 0.1,
                        height: 0.01
                    });
                    const textMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
                    const textMesh = new THREE.Mesh(textGeometry, textMaterial);
                    textMesh.position.set(Math.cos(angles[index]), 0, Math.sin(angles[index]));
                    scene.add(textMesh);
                });
            });

            // Add degree markers
            for (let i = 0; i <= 360; i += 10) {
                const angle = i * Math.PI / 180;
                const markerGeometry = new THREE.CylinderGeometry(0.005, 0.005, 0.05, 32);
                const markerMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
                const markerMesh = new THREE.Mesh(markerGeometry, markerMaterial);
                markerMesh.position.set(Math.cos(angle), 0.025, Math.sin(angle));
                markerMesh.rotation.x = Math.PI / 2;
                scene.add(markerMesh);
            }
        }

        // Function to create and add axis labels
        function addAxisLabels(P, T) {
            const fontLoader = new THREE.FontLoader();
            fontLoader.load('https://threejs.org/examples/fonts/helvetiker_regular.typeface.json', function(font) {
                const labels = ['P', 'T', 'U'];
                const vectors = [P, T, new THREE.Vector3().crossVectors(P, T)];

                labels.forEach((label, index) => {
                    const vector = vectors[index].clone().normalize().multiplyScalar(1.1);
                    const textGeometry = new THREE.TextGeometry(label, {
                        font: font,
                        size: 0.1,
                        height: 0.01
                    });
                    const textMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
                    const textMesh = new THREE.Mesh(textGeometry, textMaterial);
                    textMesh.position.set(vector.x, vector.y, vector.z);
                    scene.add(textMesh);
                });
            });
        }

        // Function to handle slider changes
        function updateVisualization() {
            const strike = parseFloat(document.getElementById('slider-strike').value);
            const dip = parseFloat(document.getElementById('slider-dip').value);
            const slipAngle = parseFloat(document.getElementById('slider-slip-angle').value);
            const slipAmount = parseFloat(document.getElementById('slider-slip-amount').value);

            const { P, T } = calculateAxes(strike, dip, slipAngle);
            colorSphereFaces(P, T);
            deformSphere(slipAmount, P, T);

            // Remove and re-add axis labels
            scene.children = scene.children.filter(child => !child.isMesh || child.name !== 'axis-label');
            addAxisLabels(P, T);

            renderer.render(scene, camera);
        }

        // Function to handle reset button click
        function resetControls() {
            document.getElementById('slider-strike').value = 180;
            document.getElementById('value-strike').value = 180;
            document.getElementById('slider-dip').value = 60;
            document.getElementById('value-dip').value = 60;
            document.getElementById('slider-slip-angle').value = 90;
            document.getElementById('value-slip-angle').value = 90;
            document.getElementById('slider-slip-amount').value = 0.5;
            document.getElementById('value-slip-amount').value = 0.5;
            document.getElementById('checkbox-box').checked = false;

            updateVisualization();
        }

        // Function to handle checkbox change
        function toggleBoundingBox() {
            if (document.getElementById('checkbox-box').checked) {
                if (!scene.getObjectByName('bounding-box')) {
                    const boundingBox = new THREE.BoxHelper(sphere, 0xffff00);
                    boundingBox.name = 'bounding-box';
                    scene.add(boundingBox);
                }
            } else {
                const boundingBox = scene.getObjectByName('bounding-box');
                if (boundingBox) {
                    scene.remove(boundingBox);
                }
            }
            renderer.render(scene, camera);
        }

        // Add event listeners to sliders
        document.getElementById('slider-strike').addEventListener('input', () => {
            document.getElementById('value-strike').value = document.getElementById('slider-strike').value;
            updateVisualization();
        });

        document.getElementById('slider-dip').addEventListener('input', () => {
            document.getElementById('value-dip').value = document.getElementById('slider-dip').value;
            updateVisualization();
        });

        document.getElementById('slider-slip-angle').addEventListener('input', () => {
            document.getElementById('value-slip-angle').value = document.getElementById('slider-slip-angle').value;
            updateVisualization();
        });

        document.getElementById('slider-slip-amount').addEventListener('input', () => {
            document.getElementById('value-slip-amount').value = document.getElementById('slider-slip-amount').value;
            updateVisualization();
        });

        // Add event listener to reset button
        document.getElementById('btn-reset').addEventListener('click', resetControls);

        // Add event listener to checkbox
        document.getElementById('checkbox-box').addEventListener('change', toggleBoundingBox);

        // Initial setup
        addCompassRose();
        updateVisualization();
    </script>
</body>
</html>
