        <div id="canvas-container"></div>
        <div id="probability-display">99.9% success probability</div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let N = 10;
        let k = 2;
        const targetIndex = 6;
        let amplitudes = [];

        function setup() {
            createCanvas(windowWidth * 0.8, 600).parent('canvas-container');
            updateAmplitudes();
            updateProbabilityDisplay();
        }

        function draw() {
            background(255);
            const sectionHeight = height / 4;
            const barWidth = width / N;

            // Stage 1: Original Amplitudes
            drawAmplitudes(0, sectionHeight, amplitudes, 'Original Amplitudes');

            // Stage 2: After First Inversion
            let invertedAmplitudes = [...amplitudes];
            invertedAmplitudes[targetIndex] *= -1;
            drawAmplitudes(sectionHeight, 2 * sectionHeight, invertedAmplitudes, 'After First Inversion');

            // Stage 3: After First Diffusion
            let diffusedAmplitudes = diffuse(invertedAmplitudes);
            drawAmplitudes(2 * sectionHeight, 3 * sectionHeight, diffusedAmplitudes, 'After First Diffusion');

            // Stage 4: After k Iterations
            let finalAmplitudes = [...amplitudes];
            for (let i = 0; i < k; i++) {
                finalAmplitudes[targetIndex] *= -1;
                finalAmplitudes = diffuse(finalAmplitudes);
            }
            drawAmplitudes(3 * sectionHeight, height, finalAmplitudes, 'After k Iterations');
        }

        function drawAmplitudes(yStart, yEnd, amps, title) {
            const midY = (yStart + yEnd) / 2;
            stroke(0);
            line(0, midY, width, midY);
            noStroke();
            textSize(16);
            textAlign(CENTER);
            text(title, width / 2, yStart + 20);

            const barWidth = width / N;
            const scaleFactor = 40;

            for (let i = 0; i < N; i++) {
                const x = i * barWidth;
                const y = midY - amps[i] * scaleFactor;
                const h = Math.abs(amps[i]) * scaleFactor;
                fill(i === targetIndex ? '#FFA500' : '#808080');
                rect(x, y, barWidth, h);
            }

            if (title === 'Original Amplitudes') {
                drawParentheses(midY);
            }
        }

        function drawParentheses(midY) {
            const margin = width * 0.05;
            const radius = width * 0.05;
            stroke(0);
            arc(margin, midY, radius * 2, radius * 2, PI, 0);
            arc(width - margin, midY, radius * 2, radius * 2, 0, PI);
        }

        function updateAmplitudes() {
            amplitudes = Array(N).fill(1 / Math.sqrt(N));
        }

        function diffuse(amps) {
            const mean = amps.reduce((acc, val) => acc + val, 0) / N;
            return amps.map(a => 2 * mean - a);
        }

        function updateProbabilityDisplay() {
            const probability = Math.pow(Math.sin((2 * k + 1) * Math.asin(1 / Math.sqrt(N))), 2);
            document.getElementById('probability-display').innerText = (probability * 100).toFixed(1) + '% success probability';
        }

        document.getElementById('slider-db-size').addEventListener('input', function() {
            N = parseInt(this.value);
            document.getElementById('display-db-size').innerText = N;
            updateAmplitudes();
            updateProbabilityDisplay();
        });

        document.getElementById('slider-iterations').addEventListener('input', function() {
            k = parseInt(this.value);
            document.getElementById('display-iterations').innerText = k;
            updateProbabilityDisplay();
        });
    </script>
</body>
</html>
