                <button id="btn-distillate-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-bottom">bottom specification</label>
                <input type="range" id="slider-bottom" min="0.01" max="0.49" step="0.01" value="0.05">
                <button id="btn-bottom-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-feed-comp">feed composition</label>
                <input type="range" id="slider-feed-comp" min="0.02" max="0.98" step="0.01" value="0.50">
                <button id="btn-feed-comp-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-feed-quality">feed quality</label>
                <input type="range" id="slider-feed-quality" min="0.0" max="2.0" step="0.05" value="1.0">
                <button id="btn-feed-quality-plus">+</button>
            </div>
        </div>

        <!-- Right column: Visualization Area -->
        <div id="visualization-area">
            <p id="stages-count-display">The number of stages is 8.</p>
            <div id="canvas-container"></div> <!-- p5.js canvas will be attached here -->
        </div>
    </div>

    <script>
        let alpha, xD, xB, xF, q;
        let stageCountDisplay;

        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            const width = canvasContainer.clientWidth;
            const height = canvasContainer.clientHeight;
            createCanvas(width, height);
            noLoop();

            stageCountDisplay = document.getElementById('stages-count-display');

            // Set up sliders and buttons
            document.querySelectorAll('.control-group input[type="range"]').forEach(slider => {
                slider.addEventListener('input', updateDiagram);
            });

            document.querySelectorAll('.control-group button').forEach(button => {
                button.addEventListener('click', function() {
                    const sliderId = this.id.replace('-plus', '');
                    const slider = document.getElementById(sliderId);
                    const step = parseFloat(slider.step);
                    slider.value = (parseFloat(slider.value) + step).toFixed(2);
                    slider.dispatchEvent(new Event('input'));
                });
            });

            updateDiagram();
        }

        function updateDiagram() {
            alpha = parseFloat(document.getElementById('slider-volatility').value);
            xD = parseFloat(document.getElementById('slider-distillate').value);
            xB = parseFloat(document.getElementById('slider-bottom').value);
            xF = parseFloat(document.getElementById('slider-feed-comp').value);
            q = parseFloat(document.getElementById('slider-feed-quality').value);

            // Enforce constraints
            if (xB >= xF) {
                xF = xB + parseFloat(document.getElementById('slider-feed-comp').step);
                document.getElementById('slider-feed-comp').value = xF.toFixed(2);
            }
            if (xD <= xF) {
                xF = xD - parseFloat(document.getElementById('slider-feed-comp').step);
                document.getElementById('slider-feed-comp').value = xF.toFixed(2);
            }

            // Update feed composition slider range
            document.getElementById('slider-feed-comp').min = parseFloat(document.getElementById('slider-bottom').value) + parseFloat(document.getElementById('slider-feed-comp').step);
            document.getElementById('slider-feed-comp').max = parseFloat(document.getElementById('slider-distillate').value) - parseFloat(document.getElementById('slider-feed-comp').step);

            drawDiagram();
        }

        function drawDiagram() {
            clear();
            background(255);
            const margin = 50;
            const plotWidth = width - 2 * margin;
            const plotHeight = height - 2 * margin;

            // Draw axes
            stroke('#808080');
            strokeWeight(2);
            line(margin, margin, margin, height - margin);
            line(margin, height - margin, width - margin, height - margin);

            // Draw axis labels and ticks
            fill('#808080');
            textSize(14);
            textAlign(CENTER, CENTER);
            text('x', width / 2, height - 10);
            text('y', 10, height / 2);
            for (let i = 0; i <= 1; i += 0.1) {
                const x = map(i, 0, 1, margin, width - margin);
                const y = map(i, 0, 1, height - margin, margin);
                line(x, height - margin, x, height - margin + 5);
                line(margin, y, margin - 5, y);
                text(i.toFixed(1), x, height - margin + 20);
                text(i.toFixed(1), margin - 20, y);
            }

            // Draw equilibrium curve
            stroke('#FF0000');
            beginShape();
            for (let x = 0; x <= 1; x += 0.01) {
                const y = (alpha * x) / (1 + (alpha - 1) * x);
                vertex(map(x, 0, 1, margin, width - margin), map(y, 0, 1, height - margin, margin));
            }
            endShape();

            // Draw diagonal line
            stroke('#228B22');
            line(margin, height - margin, width - margin, margin);

            // Calculate intersection point
            let xInt, yInt;
            if (q === 1) {
                xInt = xF;
                yInt = (alpha * xInt) / (1 + (alpha - 1) * xInt);
            } else if (q === 0) {
                yInt = xF;
                xInt = yInt;
            } else {
                const m = q / (q - 1);
                const b = xF - m * xF;
                const eqCurveFunc = x => (alpha * x) / (1 + (alpha - 1) * x);
                xInt = solveIntersection(eqCurveFunc, m, b);
                yInt = eqCurveFunc(xInt);
            }

            // Draw operating & feed lines
            stroke('#00FFFF');
            line(map(xD, 0, 1, margin, width - margin), map(xD, 0, 1, height - margin, margin), map(xInt, 0, 1, margin, width - margin), map(yInt, 0, 1, height - margin, margin));

            stroke('#FF00FF');
            line(map(xB, 0, 1, margin, width - margin), map(xB, 0, 1, height - margin, margin), map(xInt, 0, 1, margin, width - margin), map(yInt, 0, 1, height - margin, margin));

            stroke('#0000CD');
            if (q === 1) {
                line(map(xF, 0, 1, margin, width - margin), map(xF, 0, 1, height - margin, margin), map(xInt, 0, 1, margin, width - margin), map(yInt, 0, 1, height - margin, margin));
            } else if (q === 0) {
                line(map(xF, 0, 1, margin, width - margin), map(xF, 0, 1, height - margin, margin), map(xInt, 0, 1, margin, width - margin), map(yInt, 0, 1, height - margin, margin));
            } else {
                line(map(xF, 0, 1, margin, width - margin), map(xF, 0, 1, height - margin, margin), map(xInt, 0, 1, margin, width - margin), map(yInt, 0, 1, height - margin, margin));
            }

            // Calculate and draw stages
            let stageCount = 0;
            let x = xD;
            let y = xD;
            stroke('#000000');
            while (x > xB) {
                const yNew = (alpha * x) / (1 + (alpha - 1) * x);
                line(map(x, 0, 1, margin, width - margin), map(y, 0, 1, height - margin, margin), map(x, 0, 1, margin, width - margin), map(yNew, 0, 1, height - margin, margin));
                x = xNew;
                if (x <= xB) break;
                stageCount++;
                y = x > xInt ? yNew : (x * (1 - q) + q * yNew) / (1 - q);
                line(map(x, 0, 1, margin, width - margin), map(yNew, 0, 1, height - margin, margin), map(x, 0, 1, margin, width - margin), map(y, 0, 1, height - margin, margin));
            }
            stageCount++;

            // Update stage count display
            stageCountDisplay.textContent = `The number of stages is ${stageCount}.`;
        }

        function solveIntersection(eqCurveFunc, m, b) {
            let x = 0.5;
            let tolerance = 1e-6;
            let iterationLimit = 1000;
            let iteration = 0;

            while (iteration < iterationLimit) {
                const yEq = eqCurveFunc(x);
                const yLine = m * x + b;
                const diff = yEq - yLine;
                if (Math.abs(diff) < tolerance) break;

                const dydx = (eqCurveFunc(x + tolerance) - eqCurveFunc(x)) / tolerance;
                x -= diff / (dydx - m);

                iteration++;
            }

            return x;
        }
    </script>
