    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        let currentMonth = (new Date()).getMonth();
        let is2D = true;

        // Initialize p5.js sketch
        const sketch = (p) => {
            p.setup = () => {
                p.createCanvas(p.windowWidth, p.windowHeight);
            };

            p.draw = () => {
                p.background(0);
                p.translate(p.width / 2, p.height / 2);

                // Draw Solar Wind
                p.stroke('#990000');
                for (let i = 0; i < 10; i++) {
                    p.line(-p.width / 2, p.random(-p.height / 2, p.height / 2), -p.width / 2 + 50, p.random(-p.height / 2, p.height / 2));
                }

                // Calculate tilt angle
                const tiltAngle = 23.5 * Math.cos(2 * Math.PI * currentMonth / 12);

                // Rotate Earth and Magnetosphere
                p.rotate(p.radians(tiltAngle));

                // Draw Bow Shock
                p.stroke('#FFA500');
                p.beginShape();
                for (let a = 0; a <= 180; a += 1) {
                    const r = 150 + 50 * Math.sin(2 * p.radians(a));
                    p.vertex(r * Math.cos(p.radians(a)), r * Math.sin(p.radians(a)));
                }
                p.endShape();

                // Draw Magnetopause
                p.stroke('#00FFFF');
                p.beginShape();
                for (let a = 0; a <= 180; a += 1) {
                    const r = 100 + 50 * Math.sin(2 * p.radians(a));
                    p.vertex(r * Math.cos(p.radians(a)), r * Math.sin(p.radians(a)));
                }
                p.endShape();

                // Draw Magnetic Field Lines
                p.stroke('#808080');
                for (let i = 0; i < 5; i++) {
                    p.beginShape();
                    for (let a = 0; a <= 180; a += 1) {
                        const r = 70 + 20 * Math.sin(2 * p.radians(a));
                        p.vertex(r * Math.cos(p.radians(a)), r * Math.sin(p.radians(a)));
                    }
                    p.endShape();
                }

                // Draw Earth
                p.fill('#4169E1');
                p.ellipse(0, 0, 50, 50);
                p.fill('#228B22');
                p.arc(0, 0, 50, 50, p.radians(0), p.radians(180));

                // Draw Van Allen Belts
                p.fill('#006400');
                p.arc(0, 0, 60, 60, p.radians(90), p.radians(270));
                p.fill('#32CD32');
                p.arc(0, 0, 70, 70, p.radians(90), p.radians(270));

                // Draw Polar Cusps
                p.fill('#F0E68C');
                p.triangle(-150, -50, -150, 50, -100, 0);

                // Draw Magnetotail
                p.noStroke();
                p.fill('rgba(0, 0, 139, 0.4)');
                p.rect(25, -100, 200, 200);
            };
        };

        // Initialize three.js scene
        let scene, camera, renderer, controls, earth, magnetosphere, sun;

        function initThreeJS() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000000);

            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.z = 500;

            renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('main-canvas') });
            renderer.setSize(window.innerWidth, window.innerHeight);

            controls = new THREE.OrbitControls(camera, renderer.domElement);

            // Sun
            const sunGeometry = new THREE.SphereGeometry(50, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xFFFF00 });
            sun = new THREE.Mesh(sunGeometry, sunMaterial);
            scene.add(sun);

            // Earth's Orbit
            const curve = new THREE.EllipseCurve(
                0, 0,            // ax, aY
                200, 200,         // xRadius, yRadius
                0, 2 * Math.PI,  // aStartAngle, aEndAngle
                false,            // aClockwise
                0                 // aRotation
            );
            const points = curve.getPoints(50);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: 0xffffff });
            const orbit = new THREE.Line(geometry, material);
            scene.add(orbit);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(20, 32, 32);
            const earthMaterial = new THREE.MeshStandardMaterial({ color: 0x4169E1 });
            earth = new THREE.Mesh(earthGeometry, earthMaterial);
            scene.add(earth);

            // Earth's Axis
            const axis = new THREE.ArrowHelper(new THREE.Vector3(0, 1, 0), new THREE.Vector3(0, 0, 0), 30, 0x00FF00);
            earth.add(axis);

            // Solar Wind
            for (let i = 0; i < 10; i++) {
                const direction = new THREE.Vector3(1, 0, 0);
                const origin = new THREE.Vector3(0, 0, 0);
                const length = 100;
                const hex = 0xff0000;
                const arrowHelper = new THREE.ArrowHelper(direction, origin, length, hex);
                scene.add(arrowHelper);
            }

            // Magnetosphere
            const magnetospherePoints = [];
            for (let a = 0; a <= 180; a += 1) {
                const r = 100 + 50 * Math.sin(2 * Math.radians(a));
                magnetospherePoints.push(new THREE.Vector3(r * Math.cos(p.radians(a)), r * Math.sin(p.radians(a)), 0));
            }
            const latheGeometry = new THREE.LatheGeometry(magnetospherePoints.map(v => new THREE.Vector2(v.x, v.y)), 12);
            const latheMaterial = new THREE.MeshStandardMaterial({ color: 0x0000ff, transparent: true, opacity: 0.4 });
            magnetosphere = new THREE.Mesh(latheGeometry, latheMaterial);
            earth.add(magnetosphere);

            // Lights
            const ambientLight = new THREE.AmbientLight(0x404040); // soft white light
            scene.add(ambientLight);

            const pointLight = new THREE.PointLight(0xffffff, 1, 1000);
            pointLight.position.set(0, 0, 0);
            scene.add(pointLight);

            animate();
        }

        function animate() {
            requestAnimationFrame(animate);

            // Update Earth's position and orientation
            const angle = 2 * Math.PI * currentMonth / 12;
            earth.position.x = 200 * Math.cos(angle);
            earth.position.y = 200 * Math.sin(angle);
            earth.rotation.y = angle;

            // Update Magnetosphere orientation
            magnetosphere.rotation.y = angle;

            controls.update();
            renderer.render(scene, camera);
        }

        // Event listeners
        document.getElementById('slider-month').addEventListener('input', (event) => {
            currentMonth = parseInt(event.target.value);
            if (is2D) {
                sketch.draw();
            } else {
                animate();
            }
        });

        document.getElementById('btn-2d').addEventListener('click', () => {
            if (!is2D) {
                is2D = true;
                document.getElementById('btn-2d').classList.add('active');
                document.getElementById('btn-3d').classList.remove('active');
                renderer.setSize(0, 0); // Hide three.js canvas
                new p5(sketch);
            }
        });

        document.getElementById('btn-3d').addEventListener('click', () => {
            if (is2D) {
                is2D = false;
                document.getElementById('btn-3d').classList.add('active');
                document.getElementById('btn-2d').classList.remove('active');
                noLoop(); // Stop p5.js sketch
                initThreeJS();
            }
        });

        // Initial setup
        document.getElementById('slider-month').value = currentMonth;
        new p5(sketch);
    </script>
