                <label for="slider-alpha">parameter α</label>
                <input type="range" id="slider-alpha" min="0.1" max="5.0" step="0.005" value="1.825">
                <span id="display-alpha">1.825</span>
            </div>
            <div>
                <label for="slider-beta">parameter β</label>
                <input type="range" id="slider-beta" min="0.1" max="5.0" step="0.005" value="1.945">
                <span id="display-beta">1.945</span>
            </div>
        </div>
        <div class="visualization-title">
            <span>density function</span> <span>and</span> <span>distribution function</span>
        </div>
        <div id="plot-container"></div>
        <div class="statistics-display">
            <p>Mean: <span id="mean-value">3.550</span></p>
            <p>Variance: <span id="variance-value">6.904</span></p>
            <p>Moment Generating Function: <span id="mgf-formula">$$ (1 - \beta t)^{-\alpha} = \frac{1}{(1 - \beta t)^{\alpha}} $$</span></p>
        </div>
    </div>

    <script>
        // Helper functions
        function gammaFunction(z) {
            const p = [0.99999999999980993, 676.5203681218851, -1259.1392167224028, 771.32342877765313,
                       -176.61502916214059, 12.507343278686905, -0.13857109526572012, 9.9843695780195716e-6,
                       1.5056327351493116e-7];
            let x = z - 1;
            let t = x + 5.5;
            t = (x + 0.5) * Math.log(t) - t;
            let n = 7;
            let y = 1;
            while (n > 0) {
                y += p[n] / ++x;
                n--;
            }
            return Math.exp(t + Math.log(2.5066282746310005 * y));
        }

        function gammaPdf(x, alpha, beta) {
            return (Math.pow(x, alpha - 1) * Math.exp(-x / beta)) / (Math.pow(beta, alpha) * gammaFunction(alpha));
        }

        function gammaCdf(x, alpha, beta, dx = 0.01) {
            let cdf = 0;
            for (let i = 0; i <= x / dx; i++) {
                cdf += gammaPdf(i * dx, alpha, beta) * dx;
            }
            return cdf;
        }

        // Initial setup
        const sliderAlpha = document.getElementById('slider-alpha');
        const sliderBeta = document.getElementById('slider-beta');
        const displayAlpha = document.getElementById('display-alpha');
        const displayBeta = document.getElementById('display-beta');
        const meanValue = document.getElementById('mean-value');
        const varianceValue = document.getElementById('variance-value');
        const mgfFormula = document.getElementById('mgf-formula');

        function updatePlot(alpha, beta) {
            const xValues = [];
            const pdfValues = [];
            const cdfValues = [];
            for (let x = 0; x <= 25; x += 0.1) {
                xValues.push(x);
                const pdf = gammaPdf(x, alpha, beta);
                pdfValues.push(pdf);
                cdfValues.push(gammaCdf(x, alpha, beta));
            }

            const mean = alpha * beta;
            const variance = alpha * Math.pow(beta, 2);
            const yMean = gammaPdf(mean, alpha, beta);

            meanValue.textContent = mean.toFixed(3);
            varianceValue.textContent = variance.toFixed(3);
            mgfFormula.innerHTML = `$$ (1 - \\beta t)^{-\\alpha} = \\frac{1}{(1 - ${beta.toFixed(3)} t)^{${alpha.toFixed(3)}} } $$`;
            MathJax.typeset();

            Plotly.newPlot('plot-container', [
                { x: xValues, y: pdfValues, mode: 'lines', line: { color: 'blue', width: 2.5 }, name: 'PDF' },
                { x: xValues, y: cdfValues, mode: 'lines', line: { color: 'orange', width: 2.5 }, name: 'CDF' }
            ], {
                xaxis: { range: [0, 25], title: '' },
                yaxis: { range: [0, 1.1], title: '' },
                showlegend: false,
                margin: { l: 50, r: 50, b: 50, t: 50 },
                shapes: [
                    { type: 'line', x0: mean, y0: 0, x1: mean, y1: yMean, line: { color: 'red', width: 3 } }
                ]
            });
        }

        // Initial plot
        updatePlot(parseFloat(sliderAlpha.value), parseFloat(sliderBeta.value));

        // Event listeners
        sliderAlpha.addEventListener('input', () => {
            const alpha = parseFloat(sliderAlpha.value);
            const beta = parseFloat(sliderBeta.value);
            displayAlpha.textContent = alpha.toFixed(3);
            updatePlot(alpha, beta);
        });

        sliderBeta.addEventListener('input', () => {
            const alpha = parseFloat(sliderAlpha.value);
            const beta = parseFloat(sliderBeta.value);
            displayBeta.textContent = beta.toFixed(3);
            updatePlot(alpha, beta);
        });
    </script>
</body>
</html>
