<body>
    <div id="canvas-container">
        <canvas id="visualization-canvas"></canvas>
    </div>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        let locator = { x: 0.2, y: 0.22 };
        let isDragging = false;
        let margin = 60;
        let width = 650;
        let height = 650;
        let plotWidth = width - 2 * margin;
        let plotHeight = height - 2 * margin;

        const azeotropes = [
            { id: 'A1', x: 0.655, y: 0 },
            { id: 'A2', x: 0, y: 0.795 },
            { id: 'A3', x: 0.605, y: 0.345 },
            { id: 'A4', x: 0.26, y: 0.335 }
        ];

        const distillationBoundary = [
            { x: azeotropes[1].x, y: azeotropes[1].y }, // A2
            { x: azeotropes[3].x, y: azeotropes[3].y }, // A4
            { x: azeotropes[0].x, y: azeotropes[0].y }  // A1
        ];

        function setup() {
            createCanvas(width, height);
        }

        function draw() {
            background(255);
            drawAxes();
            drawHypotenuse();
            drawAzeotropes();
            drawDistillationLine();
            drawLocator();
        }

        function drawAxes() {
            stroke(0);
            strokeWeight(1);
            line(margin, margin, margin, height - margin);
            line(margin, height - margin, width - margin, margin);

            for (let i = 0; i <= 10; i++) {
                let pos = map(i, 0, 10, margin, width - margin);
                line(pos, height - margin, pos, height - margin + 5);
                line(margin, pos, margin - 5, pos);

                if (i % 2 === 0) {
                    let label = (i / 10).toFixed(1);
                    fill(0);
                    textSize(12);
                    textAlign(CENTER, CENTER);
                    text(label, pos, height - margin + 20);
                    text(label, margin - 20, pos);
                }
            }

            fill(255, 0, 0);
            textSize(14);
            textAlign(RIGHT, TOP);
            text('methanol', margin, height - margin);
            textAlign(LEFT, TOP);
            text('chloroform', width - margin, height - margin);
            textAlign(RIGHT, BOTTOM);
            text('acetone', margin, margin);
        }

        function drawHypotenuse() {
            stroke(0);
            strokeWeight(1);
            line(margin, height - margin, width - margin, margin);
        }

        function drawAzeotropes() {
            stroke(0);
            strokeWeight(1);
            fill(153, 0, 0);
            for (let azeotrope of azeotropes) {
                let px = map(azeotrope.x, 0, 1, margin, width - margin);
                let py = map(1 - azeotrope.y, 0, 1, margin, height - margin);
                ellipse(px, py, 6, 6);
                fill(0);
                textSize(12);
                textAlign(CENTER, CENTER);
                text(azeotrope.id, px, py - 15);
                fill(153, 0, 0);
            }
        }

        function drawDistillationLine() {
            noFill();
            stroke(0, 0, 255);
            strokeWeight(2);
            beginShape();
            let points = integrate(locator.x, locator.y, true);
            points.reverse();
            for (let point of points) {
                curveVertex(map(point.x, 0, 1, margin, width - margin), map(1 - point.y, 0, 1, margin, height - margin));
            }
            points = integrate(locator.x, locator.y, false);
            for (let point of points) {
                curveVertex(map(point.x, 0, 1, margin, width - margin), map(1 - point.y, 0, 1, margin, height - margin));
            }
            endShape();
        }

        function integrate(startX, startY, forward) {
            let points = [];
            let x = startX;
            let y = startY;
            let stepSize = 0.005;
            let maxSteps = 250;
            let direction = forward ? 1 : -1;

            for (let i = 0; i < maxSteps; i++) {
                let vector = getVector(x, y);
                x += vector.dx * stepSize * direction;
                y += vector.dy * stepSize * direction;

                if (x <= 0 || y <= 0 || x + y >= 1) break;

                points.push({ x, y });
            }

            return points;
        }

        function getVector(x, y) {
            const A3 = { x: 0.605, y: 0.345 }; // Unstable node (repeller)
            const M = { x: 0, y: 0 };         // Stable node (attractor)

            // Vector from repeller to current point
            let v_repel_x = x - A3.x;
            let v_repel_y = y - A3.y;
            // Vector from current point to attractor
            let v_attract_x = M.x - x;
            let v_attract_y = M.y - y;

            // Normalize influence by distance squared, add epsilon to avoid division by zero
            let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
            let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

            // Weighted sum of influences
            let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
            let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

            // Add a small rotational field to shape the curves
            dx += -0.1 * y;
            dy += 0.1 * x;

            return { dx, dy };
        }

        function drawLocator() {
            let px = map(locator.x, 0, 1, margin, width - margin);
            let py = map(1 - locator.y, 0, 1, margin, height - margin);
            fill(255);
            stroke(0);
            strokeWeight(2);
            ellipse(px, py, 8, 8);
        }

        function mousePressed() {
            let px = map(locator.x, 0, 1, margin, width - margin);
            let py = map(1 - locator.y, 0, 1, margin, height - margin);
            if (dist(mouseX, mouseY, px, py) < 15) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging) {
                let mx = map(mouseX, margin, width - margin, 0, 1);
                let my = 1 - map(mouseY, margin, height - margin, 0, 1);
                let closestPoint = findClosestPointOnPath(mx, my, distillationBoundary);
                locator.x = closestPoint.x;
                locator.y = closestPoint.y;
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function findClosestPointOnPath(mx, my, path) {
            let closestPoint = { x: 0, y: 0 };
            let minDistance = Infinity;

            for (let i = 0; i < path.length - 1; i++) {
                let p1 = path[i];
                let p2 = path[i + 1];
                let t = ((mx - p1.x) * (p2.x - p1.x) + (my - p1.y) * (p2.y - p1.y)) / ((p2.x - p1.x) ** 2 + (p2.y - p1.y) ** 2);
                t = constrain(t, 0, 1);
                let closest = { x: p1.x + t * (p2.x - p1.x), y: p1.y + t * (p2.y - p1.y) };
                let distance = dist(mx, my, closest.x, closest.y);
                if (distance < minDistance) {
                    minDistance = distance;
                    closestPoint = closest;
                }
            }

            return closestPoint;
        }
    </script>
