    <!-- Include three.js and OrbitControls.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Get references to the DOM elements
        const sliderA = document.getElementById('slider-a');
        const inputA = document.getElementById('input-a');
        const sliderB = document.getElementById('slider-b');
        const inputB = document.getElementById('input-b');
        const sliderC = document.getElementById('slider-c');
        const inputC = document.getElementById('input-c');

        // Synchronize slider and input values
        sliderA.addEventListener('input', () => {
            inputA.value = sliderA.value;
            updateParaboloid();
        });
        inputA.addEventListener('input', () => {
            sliderA.value = inputA.value;
            updateParaboloid();
        });

        sliderB.addEventListener('input', () => {
            inputB.value = sliderB.value;
            updateParaboloid();
        });
        inputB.addEventListener('input', () => {
            sliderB.value = inputB.value;
            updateParaboloid();
        });

        sliderC.addEventListener('input', () => {
            inputC.value = sliderC.value;
            updateParaboloid();
        });
        inputC.addEventListener('input', () => {
            sliderC.value = inputC.value;
            updateParaboloid();
        });

        // Scene setup
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        // Camera position
        camera.position.set(5, 5, 5);
        camera.lookAt(0, 0, 0);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(ambientLight);
        scene.add(directionalLight);

        // Orbit Controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        // Coordinate Axes
        const axesMaterial = new THREE.LineBasicMaterial({ color: 0x888888 });
        const xAxisGeometry = new THREE.BufferGeometry().setFromPoints([
            new THREE.Vector3(-5, 0, 0),
            new THREE.Vector3(5, 0, 0)
        ]);
        const yAxisGeometry = new THREE.BufferGeometry().setFromPoints([
            new THREE.Vector3(0, -5, 0),
            new THREE.Vector3(0, 5, 0)
        ]);
        const zAxisGeometry = new THREE.BufferGeometry().setFromPoints([
            new THREE.Vector3(0, 0, -5),
            new THREE.Vector3(0, 0, 5)
        ]);
        const xAxis = new THREE.Line(xAxisGeometry, axesMaterial);
        const yAxis = new THREE.Line(yAxisGeometry, axesMaterial);
        const zAxis = new THREE.Line(zAxisGeometry, axesMaterial);
        scene.add(xAxis);
        scene.add(yAxis);
        scene.add(zAxis);

        // Elliptic Paraboloid
        let paraboloidMesh;

        function createParaboloid(a, b, c) {
            const range = 2.0;
            const slices = 50;
            const stacks = 50;
            const geometry = new THREE.ParametricGeometry((u, v, target) => {
                let x = a * v * range * Math.cos(u * 2 * Math.PI);
                let y = b * v * range * Math.sin(u * 2 * Math.PI);
                let z = c * (v * range) * (v * range);
                target.set(x, y, z);
            }, slices, stacks);

            // Vertex coloring
            const colors = [];
            const zMax = c * range * range;
            for (let i = 0; i < geometry.attributes.position.count; i++) {
                const z = geometry.attributes.position.array[i * 3 + 2];
                const normalizedZ = z / zMax;
                const color = new THREE.Color().lerpColors(new THREE.Color(0xFFD700), new THREE.Color(0xFF4500), normalizedZ);
                colors.push(color.r, color.g, color.b);
            }
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            // Solid mesh
            const material = new THREE.MeshPhongMaterial({
                side: THREE.DoubleSide,
                vertexColors: true,
                shininess: 100
            });
            const solidMesh = new THREE.Mesh(geometry, material);

            // Wireframe mesh
            const wireframeMaterial = new THREE.MeshBasicMaterial({
                wireframe: true,
                color: 0xcccccc
            });
            const wireframeMesh = new THREE.Mesh(geometry, wireframeMaterial);

            return [solidMesh, wireframeMesh];
        }

        function updateParaboloid() {
            const a = parseFloat(sliderA.value);
            const b = parseFloat(sliderB.value);
            const c = parseFloat(sliderC.value);

            if (paraboloidMesh) {
                scene.remove(paraboloidMesh[0]);
                scene.remove(paraboloidMesh[1]);
            }

            paraboloidMesh = createParaboloid(a, b, c);
            scene.add(paraboloidMesh[0]);
            scene.add(paraboloidMesh[1]);
        }

        // Initial paraboloid creation
        updateParaboloid();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }
        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });
    </script>
