    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        const canvas = document.getElementById('main-canvas');
        const renderer = new THREE.WebGLRenderer({ canvas });
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xffffff);

        const camera = new THREE.PerspectiveCamera(75, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
        camera.position.z = 5;
        camera.up.set(0, 0, 1);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        const orbitControls = new THREE.OrbitControls(camera, renderer.domElement);
        orbitControls.update();

        let j1 = 1.5, m1 = 0.5, j2 = 1.0, m2 = 0.0, j = 2.5;
        let isAnimating = false;
        let phi_z = 0, phi_j = 0;

        const arrowMaterial = new THREE.MeshBasicMaterial({ color: 0x000000 });
        const coneMaterial = new THREE.MeshBasicMaterial({ color: 0x000000, transparent: true, opacity: 0.5 });

        let arrowJ, arrowJ1, arrowJ2;
        let coneJ, coneJ1, coneJ2;

        function createArrow(length, color) {
            const geometry = new THREE.ConeGeometry(0.05, length, 32);
            const material = new THREE.MeshBasicMaterial({ color });
            const cone = new THREE.Mesh(geometry, material);
            cone.position.y = length / 2;
            const cylinderGeometry = new THREE.CylinderGeometry(0.02, 0.02, length, 32);
            const cylinder = new THREE.Mesh(cylinderGeometry, material);
            const arrow = new THREE.Group();
            arrow.add(cone);
            arrow.add(cylinder);
            return arrow;
        }

        function createCone(height, radius) {
            const geometry = new THREE.ConeGeometry(radius, height, 32);
            const cone = new THREE.Mesh(geometry, coneMaterial);
            cone.rotation.x = Math.PI / 2;
            return cone;
        }

        function calculateMagnitude(j) {
            return Math.sqrt(j * (j + 1));
        }

        function updateScene() {
            scene.remove(arrowJ, arrowJ1, arrowJ2, coneJ, coneJ1, coneJ2);

            const L_J = calculateMagnitude(j);
            const L_J1 = calculateMagnitude(j1);
            const L_J2 = calculateMagnitude(j2);

            if (j >= j1 && j >= j2) {
                const thetaJ = Math.acos(m1 + m2 / L_J);
                const phiJ = 0;

                arrowJ = createArrow(L_J, 0x000000);
                arrowJ.position.set(0, 0, 0);
                arrowJ.rotation.set(thetaJ, phiJ, 0);
                scene.add(arrowJ);

                coneJ = createCone(L_J, 0.2);
                scene.add(coneJ);

                const thetaJ1 = Math.acos(m1 / L_J1);
                const phiJ1 = 0;

                arrowJ1 = createArrow(L_J1, 0xff0000);
                arrowJ1.position.set(0, 0, 0);
                arrowJ1.rotation.set(thetaJ1, phiJ1, 0);
                arrowJ1.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 0, 1), phi_j));
                arrowJ1.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(1, 0, 0), thetaJ));
                arrowJ1.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 1, 0), phi_z));
                scene.add(arrowJ1);

                const thetaJ2 = Math.acos(m2 / L_J2);
                const phiJ2 = 0;

                arrowJ2 = createArrow(L_J2, 0x0000ff);
                arrowJ2.position.set(0, 0, 0);
                arrowJ2.rotation.set(thetaJ2, phiJ2, 0);
                arrowJ2.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 0, 1), phi_j));
                arrowJ2.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(1, 0, 0), thetaJ));
                arrowJ2.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 1, 0), phi_z));
                scene.add(arrowJ2);

                const coneJ1Local = createCone(L_J1, 0.2);
                coneJ1Local.rotation.x = Math.PI / 2;
                coneJ1Local.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 0, 1), phi_j));
                coneJ1Local.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(1, 0, 0), thetaJ));
                coneJ1Local.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 1, 0), phi_z));
                scene.add(coneJ1Local);

                const coneJ2Local = createCone(L_J2, 0.2);
                coneJ2Local.rotation.x = Math.PI / 2;
                coneJ2Local.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 0, 1), phi_j));
                coneJ2Local.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(1, 0, 0), thetaJ));
                coneJ2Local.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 1, 0), phi_z));
                scene.add(coneJ2Local);
            } else {
                const thetaJ1 = Math.acos(m1 / L_J1);
                const phiJ1 = 0;

                arrowJ1 = createArrow(L_J1, 0xff0000);
                arrowJ1.position.set(0, 0, 0);
                arrowJ1.rotation.set(thetaJ1, phiJ1, 0);
                arrowJ1.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 1, 0), phi_z));
                scene.add(arrowJ1);

                const coneJ1 = createCone(L_J1, 0.2);
                coneJ1.rotation.x = Math.PI / 2;
                scene.add(coneJ1);

                const thetaJ2 = Math.acos(m2 / L_J2);
                const phiJ2 = 0;

                arrowJ2 = createArrow(L_J2, 0x0000ff);
                arrowJ2.position.set(0, 0, 0);
                arrowJ2.rotation.set(thetaJ2, phiJ2, 0);
                arrowJ2.applyMatrix4(new THREE.Matrix4().makeRotationAxis(new THREE.Vector3(0, 1, 0), phi_z));
                scene.add(arrowJ2);

                const coneJ2 = createCone(L_J2, 0.2);
                coneJ2.rotation.x = Math.PI / 2;
                scene.add(coneJ2);
            }
        }

        function animate() {
            requestAnimationFrame(animate);

            if (isAnimating) {
                phi_z += 0.01;
                phi_j += 0.01;
            }

            updateScene();

            renderer.render(scene, camera);
        }

        function resetAnimation() {
            phi_z = 0;
            phi_j = 0;
            isAnimating = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            updateScene();
        }

        document.getElementById('j1-selector').addEventListener('click', (event) => {
            if (event.target.tagName === 'BUTTON') {
                j1 = parseFloat(event.target.value);
                document.querySelectorAll('#j1-selector button').forEach(btn => btn.classList.toggle('active', btn === event.target));
                populateMSelector('m1-selector', j1);
                populateJSelector();
                resetAnimation();
            }
        });

        document.getElementById('j2-selector').addEventListener('click', (event) => {
            if (event.target.tagName === 'BUTTON') {
                j2 = parseFloat(event.target.value);
                document.querySelectorAll('#j2-selector button').forEach(btn => btn.classList.toggle('active', btn === event.target));
                populateMSelector('m2-selector', j2);
                populateJSelector();
                resetAnimation();
            }
        });

        document.getElementById('m1-selector').addEventListener('click', (event) => {
            if (event.target.tagName === 'BUTTON') {
                m1 = parseFloat(event.target.value);
                document.querySelectorAll('#m1-selector button').forEach(btn => btn.classList.toggle('active', btn === event.target));
                resetAnimation();
            }
        });

        document.getElementById('m2-selector').addEventListener('click', (event) => {
            if (event.target.tagName === 'BUTTON') {
                m2 = parseFloat(event.target.value);
                document.querySelectorAll('#m2-selector button').forEach(btn => btn.classList.toggle('active', btn === event.target));
                resetAnimation();
            }
        });

        document.getElementById('j-selector').addEventListener('click', (event) => {
            if (event.target.tagName === 'BUTTON') {
                j = parseFloat(event.target.value);
                document.querySelectorAll('#j-selector button').forEach(btn => btn.classList.toggle('active', btn === event.target));
                resetAnimation();
            }
        });

        document.getElementById('btn-play-pause').addEventListener('click', () => {
            isAnimating = !isAnimating;
            document.getElementById('btn-play-pause').textContent = isAnimating ? '❚❚' : '▶';
        });

        document.getElementById('btn-reset').addEventListener('click', resetAnimation);

        function populateMSelector(selectorId, j) {
            const mSelector = document.getElementById(selectorId);
            mSelector.innerHTML = '';
            for (let m = -j; m <= j; m += 1) {
                const button = document.createElement('button');
                button.value = m;
                button.textContent = m.toString();
                button.addEventListener('click', () => {
                    document.querySelectorAll(`#${selectorId} button`).forEach(btn => btn.classList.toggle('active', btn === button));
                });
                mSelector.appendChild(button);
            }
            document.querySelector(`#${selectorId} button`).classList.add('active');
        }

        function populateJSelector() {
            const jSelector = document.getElementById('j-selector');
            jSelector.innerHTML = '';
            for (let jVal = Math.abs(j1 - j2); jVal <= j1 + j2; jVal += 1) {
                const button = document.createElement('button');
                button.value = jVal;
                button.textContent = jVal.toString();
                button.addEventListener('click', () => {
                    document.querySelectorAll('#j-selector button').forEach(btn => btn.classList.toggle('active', btn === button));
                });
                jSelector.appendChild(button);
            }
            document.querySelector('#j-selector button').classList.add('active');
        }

        window.addEventListener('resize', () => {
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        });

        populateMSelector('m1-selector', j1);
        populateMSelector('m2-selector', j2);
        populateJSelector();
        updateScene();
        animate();
    </script>
</body>
</html>
