<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Flood Fill Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #control-panel {
            display: flex;
            justify-content: center;
            align-items: center;
            margin: 20px;
        }
        #viz-container {
            display: flex;
            justify-content: space-around;
            width: 100%;
            max-width: 600px;
        }
        .grid-display {
            text-align: center;
        }
        .active {
            background-color: #ddd;
        }
        label {
            margin-right: 10px;
        }
        button {
            margin: 0 5px;
            padding: 5px 10px;
            cursor: pointer;
        }
        canvas {
            display: block;
            margin: 0 auto;
        }
    </style>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
</head>
<body>
    <div id="control-panel">
        <label for="steps-slider">steps</label>
        <input type="range" id="steps-slider" min="0" max="61" value="61" step="1">
        <span id="steps-value">+ 61</span>
        <label>box</label>
        <button id="btn-none">None</button>
        <button id="btn-box">Box</button>
        <button id="btn-scattered" class="active">Scattered</button>
    </div>
    <div id="viz-container">
        <div id="recursive-container" class="grid-display">
            <h3>Recursive</h3>
            <div id="canvas-recursive"></div>
        </div>
        <div id="iterative-container" class="grid-display">
            <h3>Iterative</h3>
            <div id="canvas-iterative"></div>
        </div>
    </div>

    <script>
        const EMPTY = 0;
        const OBSTACLE = 1;
        const FILLED = 2;
        const GRID_SIZE = 10;
        const CELL_SIZE = 20;
        const START_X = 4;
        const START_Y = 4;
        let recursiveHistory = [];
        let iterativeHistory = [];
        let grid = [];
        let recursiveSketch;
        let iterativeSketch;

        function setupGrid(pattern) {
            grid = Array.from({ length: GRID_SIZE }, () => Array(GRID_SIZE).fill(EMPTY));
            switch (pattern) {
                case 'none':
                    break;
                case 'box':
                    for (let i = 0; i < GRID_SIZE; i++) {
                        grid[0][i] = OBSTACLE;
                        grid[GRID_SIZE - 1][i] = OBSTACLE;
                        grid[i][0] = OBSTACLE;
                        grid[i][GRID_SIZE - 1] = OBSTACLE;
                    }
                    break;
                case 'scattered':
                    for (let i = 0; i < GRID_SIZE; i++) {
                        for (let j = 0; j < GRID_SIZE; j++) {
                            if (Math.random() < 0.25 && !(i === START_X && j === START_Y)) {
                                grid[i][j] = OBSTACLE;
                            }
                        }
                    }
                    break;
            }
        }

        function floodFillRecursive(x, y) {
            if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE || grid[x][y] !== EMPTY) return;
            grid[x][y] = FILLED;
            recursiveHistory.push([x, y]);
            floodFillRecursive(x, y - 1);
            floodFillRecursive(x - 1, y);
            floodFillRecursive(x, y + 1);
            floodFillRecursive(x + 1, y);
        }

        function floodFillIterative() {
            let queue = [[START_X, START_Y]];
            while (queue.length > 0) {
                let [x, y] = queue.shift();
                if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE || grid[x][y] !== EMPTY) continue;
                grid[x][y] = FILLED;
                iterativeHistory.push([x, y]);
                queue.push([x, y - 1], [x - 1, y], [x, y + 1], [x + 1, y]);
            }
        }

        function drawGrid(sketch, history, step) {
            sketch.background(255);
            sketch.stroke(0);
            sketch.strokeWeight(1);
            for (let i = 0; i <= GRID_SIZE; i++) {
                sketch.line(0, i * CELL_SIZE, GRID_SIZE * CELL_SIZE, i * CELL_SIZE);
                sketch.line(i * CELL_SIZE, 0, i * CELL_SIZE, GRID_SIZE * CELL_SIZE);
            }
            for (let i = 0; i < GRID_SIZE; i++) {
                for (let j = 0; j < GRID_SIZE; j++) {
                    if (grid[i][j] === OBSTACLE) {
                        sketch.fill(0);
                        sketch.rect(j * CELL_SIZE, i * CELL_SIZE, CELL_SIZE, CELL_SIZE);
                    } else if (grid[i][j] === FILLED) {
                        sketch.fill(0, 0, 255);
                        sketch.rect(j * CELL_SIZE, i * CELL_SIZE, CELL_SIZE, CELL_SIZE);
                    }
                }
            }
            for (let k = 0; k < step && k < history.length; k++) {
                let [x, y] = history[k];
                sketch.fill(0, 0, 255);
                sketch.rect(y * CELL_SIZE, x * CELL_SIZE, CELL_SIZE, CELL_SIZE);
            }
        }

        function resetSimulation(pattern) {
            setupGrid(pattern);
            recursiveHistory = [];
            iterativeHistory = [];
            floodFillRecursive(START_X, START_Y);
            floodFillIterative();
            let maxLength = Math.max(recursiveHistory.length, iterativeHistory.length);
            document.getElementById('steps-slider').max = maxLength;
            document.getElementById('steps-slider').value = 0;
            document.getElementById('steps-value').textContent = '+ 0';
            drawGrid(recursiveSketch, recursiveHistory, 0);
            drawGrid(iterativeSketch, iterativeHistory, 0);
        }

        function initP5() {
            recursiveSketch = new p5(function(sketch) {
                sketch.setup = function() {
                    sketch.createCanvas(GRID_SIZE * CELL_SIZE, GRID_SIZE * CELL_SIZE);
                    sketch.parent('canvas-recursive');
                };
                sketch.draw = function() {
                    drawGrid(sketch, recursiveHistory, parseInt(document.getElementById('steps-slider').value));
                };
            });

            iterativeSketch = new p5(function(sketch) {
                sketch.setup = function() {
                    sketch.createCanvas(GRID_SIZE * CELL_SIZE, GRID_SIZE * CELL_SIZE);
                    sketch.parent('canvas-iterative');
                };
                sketch.draw = function() {
                    drawGrid(sketch, iterativeHistory, parseInt(document.getElementById('steps-slider').value));
                };
            });
        }

        document.addEventListener('DOMContentLoaded', () => {
            initP5();
            resetSimulation('scattered');

            document.getElementById('steps-slider').addEventListener('input', (event) => {
                let value = parseInt(event.target.value);
                document.getElementById('steps-value').textContent = '+ ' + value;
            });

            document.getElementById('btn-none').addEventListener('click', () => {
                document.querySelectorAll('#control-panel button').forEach(btn => btn.classList.remove('active'));
                document.getElementById('btn-none').classList.add('active');
                resetSimulation('none');
            });

            document.getElementById('btn-box').addEventListener('click', () => {
                document.querySelectorAll('#control-panel button').forEach(btn => btn.classList.remove('active'));
                document.getElementById('btn-box').classList.add('active');
                resetSimulation('box');
            });

            document.getElementById('btn-scattered').addEventListener('click', () => {
                document.querySelectorAll('#control-panel button').forEach(btn => btn.classList.remove('active'));
                document.getElementById('btn-scattered').classList.add('active');
                resetSimulation('scattered');
            });
        });
    </script>
</body>
</html>
