<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Bouncing Ball Simulation</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
            margin: 0;
            padding: 20px;
        }
        #control-panel {
            width: 300px;
            margin-bottom: 20px;
        }
        #control-panel div {
            margin-bottom: 10px;
        }
        #control-panel label {
            display: inline-block;
            width: 150px;
        }
        #canvas-container {
            width: 700px;
            height: 400px;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <div>
            <label for="slider-k">k</label>
            <input type="range" id="slider-k" min="0.6" max="0.95" step="0.01" value="0.9">
        </div>
        <div>
            <label for="slider-time">time</label>
            <input type="range" id="slider-time" min="5" max="25" step="0.5" value="15">
        </div>
        <div>
            <label for="slider-vx">initial velocity in x</label>
            <input type="range" id="slider-vx" min="1" max="8" step="0.1" value="2.5">
        </div>
        <div>
            <label for="slider-dt">Δt</label>
            <input type="range" id="slider-dt" min="0.05" max="0.4" step="0.01" value="0.15">
        </div>
    </div>
    <div id="canvas-container"></div>

    <!-- CDN for p5.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let k, time, vx, dt;
        let pathPoints = [];
        let strobePoints = [];
        const g = 9.8;
        const y0 = 10;
        const vy0 = 0;
        const x0 = 0;
        const padding = 40;

        function setup() {
            createCanvas(700, 400);
            noLoop();
            updateSliders();
            redraw();
        }

        function updateSliders() {
            k = parseFloat(document.getElementById('slider-k').value);
            time = parseFloat(document.getElementById('slider-time').value);
            vx = parseFloat(document.getElementById('slider-vx').value);
            dt = parseFloat(document.getElementById('slider-dt').value);
        }

        function draw() {
            updateSliders();
            pathPoints = [];
            strobePoints = [];

            let currentTime = 0;
            let currentX = x0;
            let currentY = y0;
            let currentVy = vy0;

            while (currentTime < time) {
                let vy_impact = -sqrt(2 * g * currentY);
                let t_flight = (-currentVy - vy_impact) / g;

                for (let t = 0; t <= t_flight; t += 0.01) {
                    let x = currentX + vx * t;
                    let y = currentY + currentVy * t - 0.5 * g * t * t;
                    pathPoints.push({ x, y });
                }

                for (let t = currentTime % dt; t <= t_flight; t += dt) {
                    let x = currentX + vx * t;
                    let y = currentY + currentVy * t - 0.5 * g * t * t;
                    strobePoints.push({ x, y });
                }

                currentTime += t_flight;
                currentX += vx * t_flight;
                let vy_new = sqrt(k) * vy_impact;
                currentY = 0;
                currentVy = vy_new;
            }

            background(0);
            translate(padding, height - padding);
            scale(1, -1);

            let maxX = max(pathPoints.map(p => p.x));
            let maxY = max(pathPoints.map(p => p.y));
            let scaleX = (width - 2 * padding) / maxX;
            let scaleY = (height - 2 * padding) / maxY;

            stroke(100, 100, 150);
            line(0, 0, width - 2 * padding, 0);
            line(0, 0, 0, height - 2 * padding);

            for (let i = 0; i < maxX; i += 1) {
                line(i * scaleX, -2, i * scaleX, 2);
            }
            for (let i = 0; i < maxY; i += 1) {
                line(-2, i * scaleY, 2, i * scaleY);
            }

            stroke(255, 0, 0);
            strokeWeight(2);
            noFill();
            beginShape();
            pathPoints.forEach(p => vertex(p.x * scaleX, p.y * scaleY));
            endShape();

            fill(255, 255, 0);
            noStroke();
            strobePoints.forEach(p => ellipse(p.x * scaleX, p.y * scaleY, 8, 8));
        }

        document.getElementById('slider-k').addEventListener('input', redraw);
        document.getElementById('slider-time').addEventListener('input', redraw);
        document.getElementById('slider-vx').addEventListener('input', redraw);
        document.getElementById('slider-dt').addEventListener('input', redraw);
    </script>
</body>
</html>
