<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Angle Bisectors on the Circumcircle</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
            margin: 0;
            padding: 20px;
        }
        #canvas-container {
            display: flex;
            justify-content: center;
            margin-top: 20px;
        }
        canvas {
            background-color: #F0F0F0;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <h1>Angle Bisectors on the Circumcircle</h1>
    <p>Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'.</p>
    <div id="canvas-container">
        <canvas id="p5-canvas"></canvas>
    </div>

    <script>
        let centerX, centerY;
        let radius = 200;
        let angleA = 7 * PI / 6;
        let angleB = 11 * PI / 6;
        let angleC = PI / 2;
        let activePoint = null;

        function setup() {
            createCanvas(500, 500);
            centerX = width / 2;
            centerY = height / 2;
        }

        function draw() {
            background('#F0F0F0');
            drawCircumcircle();
            drawTriangleABC();
            drawBisectorsAndTriangleAPrimeBPrimeCPrime();
            drawPoints();
        }

        function drawCircumcircle() {
            noFill();
            stroke('#6A5ACD');
            strokeWeight(4);
            ellipse(centerX, centerY, radius * 2, radius * 2);
        }

        function drawTriangleABC() {
            stroke('#6A5ACD');
            strokeWeight(4);
            line(...pointOnCircle(angleA), ...pointOnCircle(angleB));
            line(...pointOnCircle(angleB), ...pointOnCircle(angleC));
            line(...pointOnCircle(angleC), ...pointOnCircle(angleA));
        }

        function drawBisectorsAndTriangleAPrimeBPrimeCPrime() {
            let angleAPrime = atan2(sin(angleB) + sin(angleC), cos(angleB) + cos(angleC));
            let angleBPrime = atan2(sin(angleA) + sin(angleC), cos(angleA) + cos(angleC));
            let angleCPrime = atan2(sin(angleA) + sin(angleB), cos(angleA) + cos(angleB));

            stroke('#FFA500');
            strokeWeight(4);
            line(...pointOnCircle(angleA), ...pointOnCircle(angleAPrime));
            line(...pointOnCircle(angleB), ...pointOnCircle(angleBPrime));
            line(...pointOnCircle(angleC), ...pointOnCircle(angleCPrime));

            line(...pointOnCircle(angleAPrime), ...pointOnCircle(angleBPrime));
            line(...pointOnCircle(angleBPrime), ...pointOnCircle(angleCPrime));
            line(...pointOnCircle(angleCPrime), ...pointOnCircle(angleAPrime));
        }

        function drawPoints() {
            drawInteractivePoint(angleA, 'A');
            drawInteractivePoint(angleB, 'B');
            drawInteractivePoint(angleC, 'C');

            let angleAPrime = atan2(sin(angleB) + sin(angleC), cos(angleB) + cos(angleC));
            let angleBPrime = atan2(sin(angleA) + sin(angleC), cos(angleA) + cos(angleC));
            let angleCPrime = atan2(sin(angleA) + sin(angleB), cos(angleA) + cos(angleB));

            drawCalculatedPoint(angleAPrime, 'A\'');
            drawCalculatedPoint(angleBPrime, 'B\'');
            drawCalculatedPoint(angleCPrime, 'C\'');
        }

        function drawInteractivePoint(angle, label) {
            let [x, y] = pointOnCircle(angle);
            fill('#FFA500');
            stroke('#000000');
            strokeWeight(1);
            circle(x, y, 12);

            strokeWeight(1);
            line(x - 6, y, x + 6, y);
            line(x, y - 6, x, y + 6);

            fill('#000000');
            noStroke();
            textSize(16);
            text(label, x + 15, y + 5);
        }

        function drawCalculatedPoint(angle, label) {
            let [x, y] = pointOnCircle(angle);
            fill('#FFA500');
            stroke('#000000');
            strokeWeight(1);
            circle(x, y, 12);

            fill('#000000');
            noStroke();
            textSize(16);
            text(label, x + 15, y + 5);
        }

        function pointOnCircle(angle) {
            return [centerX + radius * cos(angle), centerY + radius * sin(angle)];
        }

        function mousePressed() {
            if (distanceToAngle(mouseX, mouseY, angleA) < 15) activePoint = 'A';
            else if (distanceToAngle(mouseX, mouseY, angleB) < 15) activePoint = 'B';
            else if (distanceToAngle(mouseX, mouseY, angleC) < 15) activePoint = 'C';
        }

        function mouseDragged() {
            if (activePoint === 'A') angleA = atan2(mouseY - centerY, mouseX - centerX);
            else if (activePoint === 'B') angleB = atan2(mouseY - centerY, mouseX - centerX);
            else if (activePoint === 'C') angleC = atan2(mouseY - centerY, mouseX - centerX);
        }

        function mouseReleased() {
            activePoint = null;
        }

        function distanceToAngle(x, y, angle) {
            let [cx, cy] = pointOnCircle(angle);
            return dist(x, y, cx, cy);
        }
    </script>
</body>
</html>
