    <script>
        let sliderSteps;
        let hoverDescription = '';

        function setup() {
            const canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');
            sliderSteps = select('#slider-steps');
        }

        function draw() {
            background(220);
            drawStaticScene();
            drawProcesses();
            drawTooltip();
