        function drawStaticScene() {
            // Sky
            fill('#4169E1');
            rect(0, 0, width, 200);

            // Sun
            fill('#FFD700');
            ellipse(550, 50, 50, 50);

            // Cloud
            fill('#FFFFFF');
            ellipse(300, 100, 40, 30);
            ellipse(330, 110, 40, 30);
            ellipse(360, 100, 40, 30);

            // Grass
            fill('#228B22');
            rect(0, 200, width, 150);

            // Soil
            fill('#8B4513');
            rect(0, 350, width, 150);

            // Water
            fill('#0000CD');
            beginShape();
            vertex(400, 350);
            vertex(600, 350);
            vertex(600, 500);
            vertex(500, 450);
            endShape(CLOSE);

            // Trees
            fill('#696969');
            rect(280, 150, 20, 80);
            fill('#006400');
            ellipse(290, 120, 60, 80);
            fill('#696969');
            rect(380, 150, 20, 80);
            fill('#006400');
            ellipse(390, 120, 60, 80);

            // Sheep
            fill('#FFFFFF');
            ellipse(200, 250, 40, 30);
            ellipse(210, 260, 30, 20);
            ellipse(215, 250, 10, 10);
            fill('#000000');
            ellipse(217, 252, 3, 3);

            // Factory
            fill('#696969');
            rect(450, 180, 50, 100);
            fill('#A9A9A9');
            rect(460, 150, 30, 30);
            rect(480, 150, 30, 30);
            rect(470, 130, 10, 20);

            // Fossil Deposits
            fill('#000000');
            ellipse(300, 400, 10, 10);
            ellipse(350, 420, 10, 10);
            ellipse(400, 410, 10, 10);
        }

        function drawProcesses() {
            const sliderValue = int(sliderSteps.value());

            if (sliderValue >= 1) {
                drawProcess("Cellular Respiration", 200, 240, 250, 180, "Animals and plants break down sugars for energy, releasing carbon dioxide.");
            }
            if (sliderValue >= 2) {
                drawProcess("Organisms' Waste and Decay", 220, 280, 220, 350, "Decomposers break down dead organic matter, returning carbon to the soil and atmosphere.");
            }
            if (sliderValue >= 3) {
                drawProcess("Fossils and Fossil Fuels", 220, 370, 220, 440, "Over millions of years, buried organic matter can turn into fossil fuels like coal and oil.");
            }
            if (sliderValue >= 4) {
                drawProcess("Photosynthesis", 350, 80, 300, 180, "Plants use sunlight, water, and carbon dioxide to create food, releasing oxygen.");
            }
            if (sliderValue >= 5) {
                drawProcess("Fossil Fuel Emissions", 480, 200, 430, 120, "Burning fossil fuels releases large amounts of carbon dioxide into the atmosphere.");
            }
            if (sliderValue >= 6) {
                drawProcess("Fossil Fuel Extraction", 450, 420, 480, 280, "Humans extract fossil fuels from the ground for energy.");
            }
            if (sliderValue >= 7) {
                drawProcess("CO2 Diffusion", 520, 250, 550, 400, "Carbon dioxide dissolves into and is released from bodies of water like oceans.");
                drawProcess("", 570, 400, 540, 250, "");
            }
        }

        function drawProcess(labelText, x1, y1, x2, y2, description) {
            drawArrow(x1, y1, x2, y2);
            const labelX = (x1 + x2) / 2;
            const labelY = (y1 + y2) / 2;
            textSize(12);
            textAlign(CENTER, CENTER);
            fill(0);
            const labelWidth = textWidth(labelText) + 10;
            const labelHeight = 20;
            const labelXOffset = labelWidth / 2;
            const labelYOffset = labelHeight / 2;

            text(labelText, labelX, labelY - 5);

            // Check for hover
            if (mouseX > labelX - labelXOffset && mouseX < labelX + labelXOffset && mouseY > labelY - labelYOffset && mouseY < labelY + labelYOffset) {
                hoverDescription = description;
            }
        }

        function drawArrow(x1, y1, x2, y2) {
            stroke(0);
            strokeWeight(2);
            line(x1, y1, x2, y2);
            push();
            translate(x2, y2);
            rotate(atan2(y1 - y2, x1 - x2) - PI / 2);
            triangle(-10, -5, 0, 10, 10, -5);
            pop();
        }

        function drawTooltip() {
            if (hoverDescription !== '') {
                fill(0, 0, 0, 150);
                noStroke();
                rect(mouseX + 10, mouseY + 10, 150, 50);
                fill(255);
                textSize(12);
                textAlign(LEFT, TOP);
                text(hoverDescription, mouseX + 15, mouseY + 15);
            }
        }

        function mouseMoved() {
            hoverDescription = '';
        }
    </script>
