    <script>
        let t = 0.4;
        let state = {
            position: 'off',
            velocity: 'off',
            acceleration: 'on',
            coords: 'rectangular',
            circle: 'off'
        };

        function setup() {
            createCanvas(windowWidth - 250, windowHeight);
            document.getElementById('slider-time').addEventListener('input', updateTime);
            document.getElementById('btn-reset-time').addEventListener('click', resetTime);
            document.querySelectorAll('#toggle-position input').forEach(input => input.addEventListener('change', updateState));
            document.querySelectorAll('#toggle-velocity input').forEach(input => input.addEventListener('change', updateState));
            document.querySelectorAll('#toggle-acceleration input').forEach(input => input.addEventListener('change', updateState));
            document.getElementById('select-coords').addEventListener('change', updateState);
            document.querySelectorAll('#toggle-circle input').forEach(input => input.addEventListener('change', updateState));
        }

        function draw() {
            background(255);
            drawAxes();
            drawLemniscate();
            drawPoint();
            drawVectors();
            drawBasisVectors();
            drawInstantaneousCircle();
        }

        function updateTime(event) {
            t = parseFloat(event.target.value);
            redraw();
        }

        function resetTime() {
            t = 0.4;
            document.getElementById('slider-time').value = t;
            redraw();
        }

        function updateState(event) {
            const name = event.target.name;
            const value = event.target.value;
            state[name] = value;
            redraw();
        }

        function drawAxes() {
            stroke(0);
            strokeWeight(1);
            line(width / 2, 0, width / 2, height);
            line(0, height / 2, width, height / 2);

            // Tick marks and labels
            const scale = 100;
            for (let i = -2; i <= 2; i++) {
                if (i !== 0) {
                    line(width / 2 + i * scale, height / 2 - 5, width / 2 + i * scale, height / 2 + 5);
                    line(width / 2 - 5, height / 2 - i * scale, width / 2 + 5, height / 2 - i * scale);
                }
            }
            textSize(12);
            text("x(t)", width / 2 + 2 * scale + 10, height / 2);
            text("y(t)", width / 2, height / 2 - 2 * scale - 10);
        }

        function drawLemniscate() {
            noFill();
            stroke(0, 0, 255);
            beginShape();
            for (let theta = 0; theta <= TWO_PI; theta += 0.01) {
                const x = cos(theta);
                const y = sin(2 * theta);
                vertex(map(x, -2, 2, 0, width), map(y, -2, 2, height, 0));
            }
            endShape();
        }

        function drawPoint() {
            const x = cos(t);
            const y = sin(2 * t);
            fill(0);
            ellipse(map(x, -2, 2, 0, width), map(y, -2, 2, height, 0), 5, 5);
        }

        function drawVectors() {
            const x = cos(t);
            const y = sin(2 * t);
            const vx = -sin(t);
            const vy = 2 * cos(2 * t);
            const ax = -cos(t);
            const ay = -4 * sin(2 * t);

            if (state.position === 'on') {
                drawVector(x, y, 0, 0, color(0, 100, 0), 'r');
            }

            if (state.velocity === 'on') {
                drawVector(x, y, vx, vy, color(128, 0, 0), 'v');
            }

            if (state.acceleration === 'on') {
                drawVector(x, y, ax, ay, color(128, 0, 128), 'a');
            }
        }

        function drawBasisVectors() {
            const x = cos(t);
            const y = sin(2 * t);
            const vx = -sin(t);
            const vy = 2 * cos(2 * t);
            const vMag = sqrt(vx * vx + vy * vy);
            const nx = -vy / vMag;
            const ny = vx / vMag;

            stroke(0);
            strokeWeight(1);
            strokeDashArray([5, 5]);

            if (state.coords === 'rectangular') {
                drawVector(x, y, 1, 0, color(0), 'î');
                drawVector(x, y, 0, 1, color(0), 'ĵ');
            } else if (state.coords === 'normal-tangential') {
                drawVector(x, y, vx / vMag, vy / vMag, color(0), 'ê_T');
                drawVector(x, y, nx, ny, color(0), 'ê_N');
            } else if (state.coords === 'polar') {
                const rMag = sqrt(x * x + y * y);
                drawVector(x, y, x / rMag, y / rMag, color(0), 'ê_r');
                drawVector(x, y, -y / rMag, x / rMag, color(0), 'ê_θ');
            }
        }

        function drawInstantaneousCircle() {
            if (state.circle === 'off') return;

            const x = cos(t);
            const y = sin(2 * t);
            const vx = -sin(t);
            const vy = 2 * cos(2 * t);
            const ax = -cos(t);
            const ay = -4 * sin(2 * t);
            const vMag = sqrt(vx * vx + vy * vy);
            const nx = -vy / vMag;
            const ny = vx / vMag;

            const rho = pow(vMag, 3) / abs(vx * ay - vy * ax);
            const cx = x + rho * nx;
            const cy = y + rho * ny;

            stroke(128);
            strokeWeight(1);
            strokeDashArray([5, 5]);
            noFill();
            ellipse(map(cx, -2, 2, 0, width), map(cy, -2, 2, height, 0), rho * 2 * 100, rho * 2 * 100);
        }

        function drawVector(x, y, vx, vy, col, label) {
            stroke(col);
            strokeWeight(2);
            strokeDashArray([]);
            line(map(x, -2, 2, 0, width), map(y, -2, 2, height, 0), map(x + vx, -2, 2, 0, width), map(y + vy, -2, 2, height, 0));

            // Arrowhead
            const angle = atan2(vy, vx);
            push();
            translate(map(x + vx, -2, 2, 0, width), map(y + vy, -2, 2, height, 0));
            rotate(angle - HALF_PI);
            triangle(0, 0, -5, 10, 5, 10);
            pop();

            // Label
            fill(col);
            noStroke();
            textSize(12);
            text(label, map(x + vx * 1.1, -2, 2, 0, width), map(y + vy * 1.1, -2, 2, height, 0));
        }

        function windowResized() {
            resizeCanvas(windowWidth - 250, windowHeight);
        }
    </script>
</body>
</html>
