    <script>
        let amplitude = 0.3;
        let stiffness = 1.0;
        let mass = 1.0;
        let time = 0;
        let isPlaying = false;
        let angularFrequency = Math.sqrt(stiffness / mass);
        let positionHistory = [];

        const plotCanvasWidth = 600;
        const plotCanvasHeight = 400;
        const animationCanvasWidth = 300;
        const animationCanvasHeight = 400;

        let plotCanvas;
        let animationCanvas;

        function setup() {
            plotCanvas = createCanvas(plotCanvasWidth, plotCanvasHeight);
            plotCanvas.parent('plot-canvas-container');

            animationCanvas = createCanvas(animationCanvasWidth, animationCanvasHeight);
            animationCanvas.parent('animation-canvas-container');

            document.getElementById('slider-amplitude').addEventListener('input', updateAmplitude);
            document.getElementById('slider-stiffness').addEventListener('input', updateStiffness);
            document.getElementById('slider-mass').addEventListener('input', updateMass);
            document.getElementById('btn-play-pause').addEventListener('click', togglePlayPause);
            document.getElementById('btn-step-back').addEventListener('click', stepBack);
            document.getElementById('btn-step-forward').addEventListener('click', stepForward);
            document.getElementById('reset-btn').addEventListener('click', resetSimulation);

            updateDisplayValues();
            drawPlot();
            drawAnimation();
        }

        function updateAmplitude(event) {
            amplitude = parseFloat(event.target.value);
            updateDisplayValues();
            recalculateAngularFrequency();
            resetSimulation();
        }

        function updateStiffness(event) {
            stiffness = parseFloat(event.target.value);
            updateDisplayValues();
            recalculateAngularFrequency();
            resetSimulation();
        }

        function updateMass(event) {
            mass = parseFloat(event.target.value);
            updateDisplayValues();
            recalculateAngularFrequency();
            resetSimulation();
        }

        function recalculateAngularFrequency() {
            angularFrequency = Math.sqrt(stiffness / mass);
        }

        function updateDisplayValues() {
            document.getElementById('amplitude-value').textContent = amplitude.toFixed(3);
            document.getElementById('stiffness-value').textContent = stiffness.toFixed(2);
            document.getElementById('mass-value').textContent = mass.toFixed(2);
        }

        function togglePlayPause() {
            isPlaying = !isPlaying;
            if (isPlaying) {
                document.getElementById('btn-play-pause').textContent = '||';
                loop();
            } else {
                document.getElementById('btn-play-pause').textContent = '▶';
                noLoop();
            }
        }

        function stepBack() {
            if (!isPlaying) {
                time -= 0.1;
                if (time < 0) time = 0;
                updatePositionHistory();
                drawPlot();
                drawAnimation();
            }
        }

        function stepForward() {
            if (!isPlaying) {
                time += 0.1;
                updatePositionHistory();
                drawPlot();
                drawAnimation();
            }
        }

        function resetSimulation() {
            time = 0;
            isPlaying = false;
            positionHistory = [];
            document.getElementById('btn-play-pause').textContent = '▶';
            noLoop();
            drawPlot();
            drawAnimation();
        }

        function updatePositionHistory() {
            const position = amplitude * Math.sin(angularFrequency * time);
            positionHistory.push({ time, position });
        }

        function drawPlot() {
            plotCanvas.background(255);
            plotCanvas.stroke(0);
            plotCanvas.strokeWeight(1);

            // Draw axes
            plotCanvas.line(0, plotCanvasHeight / 2, plotCanvasWidth, plotCanvasHeight / 2); // x-axis
            plotCanvas.line(0, 0, 0, plotCanvasHeight); // y-axis

            // Draw grid and labels
            plotCanvas.stroke(200);
            for (let i = -1; i <= 1; i++) {
                plotCanvas.line(0, map(i, -1, 1, plotCanvasHeight, 0), plotCanvasWidth, map(i, -1, 1, plotCanvasHeight, 0));
                plotCanvas.text(i, 5, map(i, -1, 1, plotCanvasHeight, 0) + 5);
            }

            // Draw plot
            plotCanvas.stroke(99, 99, 178);
            plotCanvas.strokeWeight(2);
            plotCanvas.beginShape();
            for (const point of positionHistory) {
                plotCanvas.vertex(map(point.time, 0, getMaxX(), 0, plotCanvasWidth), map(point.position, -1, 1, plotCanvasHeight, 0));
            }
            plotCanvas.endShape();
        }

        function drawAnimation() {
            animationCanvas.background(255);
            animationCanvas.stroke(0);
            animationCanvas.strokeWeight(1);

            // Draw axes
            animationCanvas.line(animationCanvasWidth / 2, 0, animationCanvasWidth / 2, animationCanvasHeight); // y-axis

            // Draw grid and labels
            animationCanvas.stroke(200);
            for (let i = -1; i <= 1; i++) {
                animationCanvas.line(animationCanvasWidth / 2, map(i, -1.2, 1.2, animationCanvasHeight, 0), animationCanvasWidth / 2, map(i, -1.2, 1.2, animationCanvasHeight, 0));
                animationCanvas.text(i, animationCanvasWidth / 2 + 5, map(i, -1.2, 1.2, animationCanvasHeight, 0) + 5);
            }

            // Draw anchor point
            animationCanvas.stroke(0);
            animationCanvas.line(animationCanvasWidth / 2 - 20, 20, animationCanvasWidth / 2 + 20, 20);

            // Calculate mass position
            const y = amplitude * Math.sin(angularFrequency * time);
            const massY = map(y, -1.2, 1.2, animationCanvasHeight, 0);

            // Draw spring
            animationCanvas.stroke(0);
            animationCanvas.strokeWeight(2);
            let springY = 20;
            let direction = 1;
            const segmentHeight = (massY - 20) / 10;
            for (let i = 0; i < 10; i++) {
                const nextSpringY = springY + segmentHeight;
                animationCanvas.line(animationCanvasWidth / 2, springY, animationCanvasWidth / 2 + 10 * direction, nextSpringY);
                springY = nextSpringY;
                direction *= -1;
            }
            animationCanvas.line(animationCanvasWidth / 2, springY, animationCanvasWidth / 2, massY);

            // Draw mass
            animationCanvas.fill(99, 99, 178);
            animationCanvas.rectMode(CENTER);
            animationCanvas.rect(animationCanvasWidth / 2, massY, 50, 20);
        }

        function getMaxX() {
            return positionHistory.length > 0 ? Math.max(...positionHistory.map(point => point.time)) : 25;
        }

        function draw() {
            if (isPlaying) {
                time += 0.05;
                updatePositionHistory();
                drawPlot();
                drawAnimation();
            }
        }
    </script>
</body>
</html>
