<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Distance Transform Visualization</title>
    <style>
        body {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            height: 100vh;
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            margin-bottom: 20px;
        }
        #control-panel span {
            margin-right: 10px;
        }
        #control-panel button {
            padding: 5px 10px;
            margin-right: 5px;
            cursor: pointer;
            background-color: #E0E0E0;
            border: none;
            border-radius: 3px;
        }
        #control-panel button.active {
            background-color: #A9A9A9;
        }
        #grid-container {
            display: grid;
            grid-template-columns: repeat(7, 1fr);
            grid-template-rows: repeat(9, 1fr);
            gap: 2px;
        }
        .cell {
            width: 40px;
            height: 40px;
            display: flex;
            align-items: center;
            justify-content: center;
            border: 1px solid #000;
            cursor: pointer;
            background-color: #D3D3D3;
        }
        .cell-on {
            background-color: #FFD700;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <span>distance function</span>
        <button id="btn-manhattan" class="active">Manhattan</button>
        <button id="btn-euclidean">squared Euclidean</button>
        <button id="btn-chebyshev">Chebyshev</button>
    </div>
    <div id="grid-container"></div>

    <script>
        const gridContainer = document.getElementById('grid-container');
        const btnManhattan = document.getElementById('btn-manhattan');
        const btnEuclidean = document.getElementById('btn-euclidean');
        const btnChebyshev = document.getElementById('btn-chebyshev');

        let currentDistanceFunction = 'manhattan';
        let gridState = Array.from({ length: 9 }, () => Array(7).fill(false));
        gridState[2][1] = true;
        gridState[6][4] = true;

        function calculateDistance(r1, c1, r2, c2) {
            switch (currentDistanceFunction) {
                case 'manhattan':
                    return Math.abs(r1 - r2) + Math.abs(c1 - c2);
                case 'euclidean':
                    return (r1 - r2) ** 2 + (c1 - c2) ** 2;
                case 'chebyshev':
                    return Math.max(Math.abs(r1 - r2), Math.abs(c1 - c2));
                default:
                    return Infinity;
            }
        }

        function recalculateDistances() {
            const distances = Array.from({ length: 9 }, () => Array(7).fill(Infinity));

            for (let r = 0; r < 9; r++) {
                for (let c = 0; c < 7; c++) {
                    if (gridState[r][c]) {
                        distances[r][c] = 0;
                    } else {
                        for (let sr = 0; sr < 9; sr++) {
                            for (let sc = 0; sc < 7; sc++) {
                                if (gridState[sr][sc]) {
                                    distances[r][c] = Math.min(distances[r][c], calculateDistance(r, c, sr, sc));
                                }
                            }
                        }
                    }
                }
            }

            return distances;
        }

        function renderGrid(distances) {
            gridContainer.innerHTML = '';
            for (let r = 0; r < 9; r++) {
                for (let c = 0; c < 7; c++) {
                    const cell = document.createElement('div');
                    cell.classList.add('cell');
                    if (gridState[r][c]) {
                        cell.classList.add('cell-on');
                    } else {
                        cell.classList.add('cell-off');
                    }
                    cell.textContent = distances[r][c] === 0 ? '' : distances[r][c];
                    cell.addEventListener('click', () => {
                        gridState[r][c] = !gridState[r][c];
                        const newDistances = recalculateDistances();
                        renderGrid(newDistances);
                    });
                    gridContainer.appendChild(cell);
                }
            }
        }

        function setActiveButton(buttonId) {
            [btnManhattan, btnEuclidean, btnChebyshev].forEach(btn => {
                btn.classList.remove('active');
            });
            document.getElementById(buttonId).classList.add('active');
        }

        btnManhattan.addEventListener('click', () => {
            currentDistanceFunction = 'manhattan';
            setActiveButton('btn-manhattan');
            const distances = recalculateDistances();
            renderGrid(distances);
        });

        btnEuclidean.addEventListener('click', () => {
            currentDistanceFunction = 'euclidean';
            setActiveButton('btn-euclidean');
            const distances = recalculateDistances();
            renderGrid(distances);
        });

        btnChebyshev.addEventListener('click', () => {
            currentDistanceFunction = 'chebyshev';
            setActiveButton('btn-chebyshev');
            const distances = recalculateDistances();
            renderGrid(distances);
        });

        // Initial rendering
        const initialDistances = recalculateDistances();
        renderGrid(initialDistances);
    </script>
</body>
</html>
