    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const btnModelEp = document.getElementById('btn-model-ep');
            const btnModelAr = document.getElementById('btn-model-ar');
            const btnTmaxLow = document.getElementById('btn-tmax-low');
            const btnTmaxHigh = document.getElementById('btn-tmax-high');
            const sliderN = document.getElementById('slider-n');
            const sliderTmin = document.getElementById('slider-tmin');
            const sliderTmax = document.getElementById('slider-tmax');
            const sliderTref = document.getElementById('slider-tref');
            const sliderParam = document.getElementById('slider-param');
            const labelN = document.getElementById('label-n');
            const labelTmin = document.getElementById('label-tmin');
            const labelTmax = document.getElementById('label-tmax');
            const labelTref = document.getElementById('label-tref');
            const labelParamName = document.getElementById('label-param-name');
            const labelParamValue = document.getElementById('label-param-value');
            const fitResults = document.getElementById('fit-results');

            function update() {
                // Button logic
                const isModelEp = btnModelEp.classList.contains('active');
                const isTmaxLow = btnTmaxLow.classList.contains('active');

                // Slider constraint logic
                sliderTmax.max = isTmaxLow ? 1200 : 6000;
                let tmin = parseInt(sliderTmin.value);
                let tmax = parseInt(sliderTmax.value);
                let tref = parseInt(sliderTref.value);

                if (tmin >= tmax) {
                    tmin = tmax - 1;
                    sliderTmin.value = tmin;
                }
                if (tref < tmin) {
                    tref = tmin;
                    sliderTref.value = tref;
                }
                if (tref > tmax) {
                    tref = tmax;
                    sliderTref.value = tref;
                }

                // Dynamic label logic
                labelParamName.textContent = isModelEp ? '\\(a_{EP}\\)' : '\\(a_{Ar}\\)';
                MathJax.typeset();

                // Update span labels
                labelN.textContent = sliderN.value;
                labelTmin.textContent = sliderTmin.value;
                labelTmax.textContent = sliderTmax.value;
                labelTref.textContent = sliderTref.value;
                labelParamValue.textContent = sliderParam.value;

                // Data generation
                const n = parseInt(sliderN.value);
                const tminK = tmin + 273.15;
                const tmaxK = tmax + 273.15;
                const trefK = tref + 273.15;
                const param = parseFloat(sliderParam.value);

                const ti = [];
                const yi = [];

                for (let i = 0; i < n; i++) {
                    const t = tmin + (tmax - tmin) * i / (n - 1);
                    const tK = t + 273.15;
                    ti.push(t);
                    if (isModelEp) {
                        yi.push((tK / trefK) * Math.exp(param * (1 / trefK - 1 / tK)));
                    } else {
                        yi.push(Math.exp(param * (1 / trefK - 1 / tK)));
                    }
                }

                // Data fitting
                const otherModel = isModelEp ? 'Arrhenius' : 'Eyring–Polanyi';
                const aFitRange = Array.from({ length: 10000 }, (_, i) => i);
                let aFit = 0;
                let rssMin = Infinity;

                aFitRange.forEach(a => {
                    const fitYi = ti.map(t => {
                        const tK = t + 273.15;
                        return isModelEp ? Math.exp(a * (1 / trefK - 1 / tK)) : (tK / trefK) * Math.exp(a * (1 / trefK - 1 / tK));
                    });
                    const ssRes = yi.reduce((acc, y, i) => acc + Math.pow(y - fitYi[i], 2), 0);
                    if (ssRes < rssMin) {
                        rssMin = ssRes;
                        aFit = a;
                    }
                });

                const yMean = yi.reduce((acc, y) => acc + y, 0) / n;
                const ssTot = yi.reduce((acc, y) => acc + Math.pow(y - yMean, 2), 0);
                const r2 = 1 - (rssMin / ssTot);

                // Display update
                fitResults.textContent = `${otherModel === 'Arrhenius' ? '\\(a_{Ar}' : '\\(a_{EP}'} = ${aFit.toFixed(0)}\\). \\(r^2 = ${r2.toFixed(3)}\\)`;
                MathJax.typeset();

                // Plot redraw
                drawPlot(ti, yi, aFit, tmin, tmax, otherModel);
            }

            function drawPlot(ti, yi, aFit, tmin, tmax, model) {
                const trefK = parseFloat(sliderTref.value) + 273.15;
                const fitYi = ti.map(t => {
                    const tK = t + 273.15;
                    return model === 'Arrhenius' ? Math.exp(aFit * (1 / trefK - 1 / tK)) : (tK / trefK) * Math.exp(aFit * (1 / trefK - 1 / tK));
                });

                const trace1 = {
                    x: ti,
                    y: yi,
                    mode: 'markers',
                    marker: { color: 'blue', size: 10, opacity: 0.7, line: { color: 'green', width: 2 } },
                    name: 'Generated Data'
                };

                const fitTi = Array.from({ length: 200 }, (_, i) => tmin + (tmax - tmin) * i / 199);
                const fitTik = fitTi.map(t => t + 273.15);
                const fitYik = fitTik.map(tK => model === 'Arrhenius' ? Math.exp(aFit * (1 / trefK - 1 / tK)) : (tK / trefK) * Math.exp(aFit * (1 / trefK - 1 / tK)));

                const trace2 = {
                    x: fitTi,
                    y: fitYik,
                    mode: 'lines',
                    line: { color: 'green', width: 2 },
                    name: `${model} Fit`
                };

                const layout = {
                    title: `${model} model fit to ${btnModelEp.classList.contains('active') ? 'Eyring–Polanyi' : 'Arrhenius'} data`,
                    xaxis: { title: 'T °C' },
                    yaxis: { title: 'k(T) / k(T<sub>ref</sub>)' },
                    showlegend: true
                };

                Plotly.newPlot('plot-container', [trace1, trace2], layout);
            }

            // Event listeners
            btnModelEp.addEventListener('click', () => {
                btnModelEp.classList.add('active');
                btnModelAr.classList.remove('active');
                update();
            });

            btnModelAr.addEventListener('click', () => {
                btnModelAr.classList.add('active');
                btnModelEp.classList.remove('active');
                update();
            });

            btnTmaxLow.addEventListener('click', () => {
                btnTmaxLow.classList.add('active');
                btnTmaxHigh.classList.remove('active');
                update();
            });

            btnTmaxHigh.addEventListener('click', () => {
                btnTmaxHigh.classList.add('active');
                btnTmaxLow.classList.remove('active');
                update();
            });

            sliderN.addEventListener('input', update);
            sliderTmin.addEventListener('input', update);
            sliderTmax.addEventListener('input', update);
            sliderTref.addEventListener('input', update);
            sliderParam.addEventListener('input', update);

            // Initial update
            update();
        });
    </script>
</body>
</html>
