<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Polygon Dissection Visualization</title>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
            margin: 0;
            padding: 20px;
        }
        #controls-container {
            display: flex;
            justify-content: center;
            gap: 10px;
            margin-bottom: 20px;
        }
        #controls-container button {
            padding: 10px 20px;
            background-color: #f0f0f0;
            border: none;
            cursor: pointer;
            transition: background-color 0.3s;
        }
        #controls-container button.active {
            background-color: #e0e0e0;
        }
        #canvas-container {
            border: 1px solid #ccc;
            overflow: hidden;
        }
    </style>
</head>
<body>
    <div id="controls-container">
        <span id="n-display">6</span>
        <button id="btn-n-3">3</button>
        <button id="btn-n-4">4</button>
        <button id="btn-n-5">5</button>
        <button id="btn-n-6">6</button>
        <button id="btn-n-7">7</button>
        <button id="btn-n-8">8</button>
        <button id="btn-n-9">9</button>
        <button id="btn-n-10">10</button>
    </div>
    <div id="canvas-container"></div>

    <script>
        let n = 6;
        const colors = ['#fefd00', '#ee1f24', '#0048a1'];

        function setup() {
            const canvasWidth = 800;
            const canvasHeight = 600;
            createCanvas(canvasWidth, canvasHeight);
            updateControls();
            drawScene();
        }

        function drawScene() {
            clear();
            drawPolygon();
            drawRectangles();
        }

        function drawPolygon() {
            const R = 150;
            const cx = width / 4;
            const cy = height / 2;
            const angleStep = PI / n;

            for (let j = 1; j <= 2 * n; j++) {
                const k1 = j - 1;
                const k2 = j % (2 * n);
                const x1 = cx + R * cos(k1 * angleStep - PI / 2);
                const y1 = cy + R * sin(k1 * angleStep - PI / 2);
                const x2 = cx + R * cos(k2 * angleStep - PI / 2);
                const y2 = cy + R * sin(k2 * angleStep - PI / 2);

                fill(colors[(j - 1) % 3]);
                stroke(0);
                triangle(cx, cy, x1, y1, x2, y2);
            }
        }

        function drawRectangles() {
            const startX = width / 2;
            const startY = height / 2;
            const R = 150;
            const angleStep = PI / n;
            const s = 2 * R * sin(angleStep / 2);
            const a = R * cos(angleStep / 2);

            for (let k = 1; k <= floor(n / 2); k++) {
                const x = startX + (k - 1) * (s + 10);
                const y = startY - a;
                drawFullRectangle(x, y, s, a);
            }

            if (n % 2 !== 0) {
                const x = startX + floor(n / 2) * (s + 10);
                const y = startY - a;
                drawHalfRectangle(x, y, s, a);
            }
        }

        function drawFullRectangle(x, y, s, a) {
            // Top triangle (Yellow)
            fill(colors[0]);
            triangle(x, y, x + s, y, x + s / 2, y - a);
            // Bottom triangle (Red)
            fill(colors[1]);
            triangle(x, y + 2 * a, x + s, y + 2 * a, x + s / 2, y + a);
            // Left triangle (Blue)
            fill(colors[2]);
            triangle(x, y, x, y + 2 * a, x + s / 2, y + a);
            // Right triangle (Yellow)
            fill(colors[0]);
            triangle(x + s, y, x + s, y + 2 * a, x + s / 2, y + a);
            // Outline
            stroke(0);
            noFill();
            rect(x, y, s, 2 * a);
            line(x + s / 2, y, x + s / 2, y + 2 * a);
        }

        function drawHalfRectangle(x, y, s, a) {
            // Top triangle (Blue)
            fill(colors[2]);
            triangle(x, y, x + s, y, x + s / 2, y - a);
            // Bottom triangle (Red)
            fill(colors[1]);
            triangle(x, y + 2 * a, x + s, y + 2 * a, x + s / 2, y + a);
            // Outline
            stroke(0);
            noFill();
            line(x, y, x + s, y);
            line(x, y + 2 * a, x + s, y + 2 * a);
            line(x + s / 2, y, x + s / 2, y + 2 * a);
        }

        function updateControls() {
            document.getElementById('n-display').textContent = n;
            document.querySelectorAll('#controls-container button').forEach(btn => {
                btn.classList.toggle('active', parseInt(btn.id.split('-')[2]) === n);
            });
        }

        document.querySelectorAll('#controls-container button').forEach(btn => {
            btn.addEventListener('click', () => {
                n = parseInt(btn.id.split('-')[2]);
                updateControls();
                drawScene();
            });
        });

        setup();
    </script>
</body>
</html>
