```html
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Robot Simulation</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #controls-container {
            display: flex;
            justify-content: center;
            align-items: center;
            margin-top: 20px;
        }
        .slider-group {
            margin: 0 20px;
        }
        #main-container {
            display: flex;
            justify-content: space-around;
            width: 820px;
            margin-top: 20px;
        }
        #workspace-panel, #phase-space-panel {
            border: 1px solid #ccc;
            padding: 10px;
            box-shadow: 2px 2px 12px rgba(0, 0, 0, 0.1);
        }
        #btn-reset {
            position: absolute;
            top: 10px;
            right: 10px;
            background: none;
            border: none;
            font-size: 20px;
            cursor: pointer;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <button id="btn-reset">x</button>
    <div id="controls-container">
        <div class="slider-group">
            <label for="slider-q">Q</label>
            <input type="range" id="slider-q" min="1" max="7" step="1" value="2">
            <span id="value-q">2</span>
        </div>
        <div class="slider-group">
            <label for="slider-p">P</label>
            <input type="range" id="slider-p" min="0" max="1" step="0.01" value="1">
            <span id="value-p">1.00</span>
        </div>
    </div>
    <div id="main-container">
        <div id="workspace-panel">
            <h3>robot workspace</h3>
            <div id="canvas-workspace"></div>
        </div>
        <div id="phase-space-panel">
            <h3 id="phase-space-title">robot phase space, path length = 0.00 rads</h3>
            <div id="canvas-phase-space"></div>
        </div>
    </div>

    <script>
        const workspaceCanvasId = 'canvas-workspace';
        const phaseSpaceCanvasId = 'canvas-phase-space';
        const workspaceWidth = 400;
        const workspaceHeight = 400;
        const phaseSpaceWidth = 400;
        const phaseSpaceHeight = 400;

        let sliderQ;
        let sliderP;
        let valueQ;
        let valueP;
        let btnReset;

        let workspaceSketch;
        let phaseSpaceSketch;

        let q = 2;
        let p = 1;

        let isDraggingObstacle1 = false;
        let isDraggingObstacle2 = false;
        let isDraggingStart = false;
        let isDraggingGoal = false;

        const basePos = { x: 200, y: 200 };
        const link1Length = 75;
        const link2Length = 50;
        const linkRadius = 10;

        const obstacle1 = { x: 200, y: 100, radius: 15 };
        const obstacle2 = { x: 200, y: 300, radius: 15 };

        let startConfig = { q1: -1.5, q2: 0.5 };
        let goalConfig = { q1: 1.5, q2: 1.0 };

        let cSpace;
        let path;
        let pathLength = 0;

        function setup() {
            sliderQ = document.getElementById('slider-q');
            sliderP = document.getElementById('slider-p');
            valueQ = document.getElementById('value-q');
            valueP = document.getElementById('value-p');
            btnReset = document.getElementById('btn-reset');

            sliderQ.addEventListener('input', handleSliderQChange);
            sliderP.addEventListener('input', handleSliderPChange);
            btnReset.addEventListener('click', resetSimulation);

            workspaceSketch = new p5(workspaceSetup, workspaceCanvasId);
            phaseSpaceSketch = new p5(phaseSpaceSetup, phaseSpaceCanvasId);

            calculateCspace();
            runBFS();
            findShortestPath();
            updatePhaseSpaceTitle();
        }

        function workspaceSetup(p) {
            p.createCanvas(workspaceWidth, workspaceHeight);
            p.background('#F0F0F0');
            p.noLoop();
        }

        function phaseSpaceSetup(p) {
            p.createCanvas(phaseSpaceWidth, phaseSpaceHeight);
            p.background('#F0F0F0');
            p.noLoop();
        }

        function handleSliderQChange(event) {
            q = parseInt(event.target.value);
            valueQ.textContent = q;
            calculateCspace();
            runBFS();
            findShortestPath();
            updatePhaseSpaceTitle();
            workspaceSketch.redraw();
            phaseSpaceSketch.redraw();
        }

        function handleSliderPChange(event) {
            p = parseFloat(event.target.value).toFixed(2);
            valueP.textContent = p;
            workspaceSketch.redraw();
            phaseSpaceSketch.redraw();
        }

        function resetSimulation() {
            q = 2;
            p = 1;
            sliderQ.value = q;
            sliderP.value = p;
            valueQ.textContent = q;
            valueP.textContent = p.toFixed(2);

            obstacle1.x = 200;
            obstacle1.y = 100;
            obstacle2.x = 200;
            obstacle2.y = 300;

            startConfig = { q1: -1.5, q2: 0.5 };
            goalConfig = { q1: 1.5, q2: 1.0 };

            calculateCspace();
            runBFS();
            findShortestPath();
            updatePhaseSpaceTitle();

            workspaceSketch.redraw();
            phaseSpaceSketch.redraw();
        }

        function calculateCspace() {
            const N = 10 * Math.pow(2, q);
            cSpace = Array.from({ length: N }, () => Array(N).fill(false));

            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    const q1 = map(i, 0, N, -Math.PI, Math.PI);
                    const q2 = map(j, 0, N, -Math.PI, Math.PI);
                    if (isColliding(q1, q2)) {
                        cSpace[i][j] = true;
                    }
                }
            }
        }

        function isColliding(q1, q2) {
            const end1 = {
                x: basePos.x + link1Length * Math.cos(q1),
                y: basePos.y + link1Length * Math.sin(q1)
            };
            const end2 = {
                x: end1.x + link2Length * Math.cos(q1 + q2),
                y: end1.y + link2Length * Math.sin(q1 + q2)
            };

            return (
                isLinkColliding(basePos, end1, obstacle1) ||
                isLinkColliding(end1, end2, obstacle1) ||
                isLinkColliding(basePos, end1, obstacle2) ||
                isLinkColliding(end1, end2, obstacle2)
            );
        }

        function isLinkColliding(start, end, obstacle) {
            const lineVec = { x: end.x - start.x, y: end.y - start.y };
            const lineLen = Math.sqrt(lineVec.x * lineVec.x + lineVec.y * lineVec.y);
            const lineUnitVec = { x: lineVec.x / lineLen, y: lineVec.y / lineLen };
            const pointVec = { x: obstacle.x - start.x, y: obstacle.y - start.y };

            const pointProj = pointVec.x * lineUnitVec.x + pointVec.y * lineUnitVec.y;
            const nearestPoint = {
                x: start.x + pointProj * lineUnitVec.x,
                y: start.y + pointProj * lineUnitVec.y
            };

            const nearestDist = Math.sqrt(
                (nearestPoint.x - obstacle.x) * (nearestPoint.x - obstacle.x) +
                (nearestPoint.y - obstacle.y) * (nearestPoint.y - obstacle.y)
            );

            return nearestDist <= linkRadius + obstacle.radius;
        }

        function runBFS() {
            const N = 10 * Math.pow(2, q);
            const queue = [];
            const distField = Array.from({ length: N }, () => Array(N).fill(Infinity));
            const prev = Array.from({ length: N }, () => Array(N).fill(null));

            const startIdx1 = Math.round(map(startConfig.q1, -Math.PI, Math.PI, 0, N - 1));
            const startIdx2 = Math.round(map(startConfig.q2, -Math.PI, Math.PI, 0, N - 1));

            queue.push([startIdx1, startIdx2]);
            distField[startIdx1][startIdx2] = 0;

            while (queue.length > 0) {
                const [i, j] = queue.shift();

                if (cSpace[i][j]) continue;

                const neighbors = [
                    [i - 1, j], [i + 1, j], [i, j - 1], [i, j + 1]
                ];

                for (const [ni, nj] of neighbors) {
                    if (ni >= 0 && ni < N && nj >= 0 && nj < N && !cSpace[ni][nj] && distField[ni][nj] === Infinity) {
                        distField[ni][nj] = distField[i][j] + 1;
                        prev[ni][nj] = [i, j];
                        queue.push([ni, nj]);
                    }
                }
            }

            return { distField, prev };
        }

        function findShortestPath() {
            const N = 10 * Math.pow(2, q);
            const { distField, prev } = runBFS();

            const goalIdx1 = Math.round(map(goalConfig.q1, -Math.PI, Math.PI, 0, N - 1));
            const goalIdx2 = Math.round(map(goalConfig.q2, -Math.PI, Math.PI, 0, N - 1));

            if (distField[goalIdx1][goalIdx2] === Infinity) {
                path = [];
                pathLength = 0;
                return;
            }

            path = [];
            let current = [goalIdx1, goalIdx2];

            while (current) {
                path.push(current);
                current = prev[current[0]][current[1]];
            }

            path.reverse();

            pathLength = 0;
            for (let i = 1; i < path.length; i++) {
                const [i1, j1] = path[i - 1];
                const [i2, j2] = path[i];
                const q11 = map(i1, 0, N, -Math.PI, Math.PI);
                const q21 = map(j1, 0, N, -Math.PI, Math.PI);
                const q12 = map(i2, 0, N, -Math.PI, Math.PI);
                const q22 = map(j2, 0, N, -Math.PI, Math.PI);
                pathLength += Math.sqrt((q12 - q11) * (q12 - q11) + (q22 - q21) * (q22 - q21));
            }
        }

        function updatePhaseSpaceTitle() {
            const phaseSpaceTitle = document.getElementById('phase-space-title');
            phaseSpaceTitle.textContent = `robot phase space, path length = ${pathLength.toFixed(2)} rads`;
        }

        workspaceSketch.draw = function(p) {
            p.background('#F0F0F0');

            const N = 10 * Math.pow(2, q);
            const idx1 = Math.round(map(startConfig.q1, -Math.PI, Math.PI, 0, N - 1));
            const idx2 = Math.round(map(startConfig.q2, -Math.PI, Math.PI, 0, N - 1));
            const interpolatedIdx1 = Math.round(map(p.lerp(startConfig.q1, goalConfig.q1, p), -Math.PI, Math.PI, 0, N - 1));
            const interpolatedIdx2 = Math.round(map(p.lerp(startConfig.q2, goalConfig.q2, p), -Math.PI, Math.PI, 0, N - 1));

            drawRobotArm(p, interpolatedIdx1, interpolatedIdx2);

            p.strokeWeight(1);
            p.stroke(0);
            p.fill(150);
            p.ellipse(obstacle1.x, obstacle1.y, obstacle1.radius * 2, obstacle1.radius * 2);
            p.ellipse(obstacle2.x, obstacle2.y, obstacle2.radius * 2, obstacle2.radius * 2);

            p.strokeWeight(2);
            p.stroke(255, 0, 0);
            p.point(path.map(([i, j]) => map(i, 0, N, 0, phaseSpaceWidth)).find((_, index) => index === Math.floor(p * (path.length - 1))),
                    path.map(([i, j]) => map(j, 0, N, 0, phaseSpaceHeight)).find((_, index) => index === Math.floor(p * (path.length - 1))));
        };

        function drawRobotArm(p, idx1, idx2) {
            const N = 10 * Math.pow(2, q);
            const q1 = map(idx1, 0, N, -Math.PI, Math.PI);
            const q2 = map(idx2, 0, N, -Math.PI, Math.PI);

            const end1 = {
                x: basePos.x + link1Length * Math.cos(q1),
                y: basePos.y + link1Length * Math.sin(q1)
            };
            const end2 = {
                x: end1.x + link2Length * Math.cos(q1 + q2),
                y: end1.y + link2Length * Math.sin(q1 + q2)
            };

            p.strokeWeight(linkRadius * 2);
            p.stroke(0);
            p.fill(150);
            p.line(basePos.x, basePos.y, end1.x, end1.y);
            p.line(end1.x, end1.y, end2.x, end2.y);

            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(12);
            p.fill(0);
            p.text('Robot Arm', end2.x, end2.y);

            p.strokeWeight(1);
            p.stroke(0);
            p.arc(basePos.x, basePos.y, 50, 50, 0, q1);
            p.arc(end1.x, end1.y, 50, 50, q1, q1 + q2);
        }

        phaseSpaceSketch.draw = function(p) {
            p.background('#F0F0F0');

            const N = 10 * Math.pow(2, q);
            const cellSize = phaseSpaceWidth / N;

            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    const x = i * cellSize;
                    const y = j * cellSize;

                    if (cSpace[i][j]) {
                        p.fill('#CC0000');
                    } else {
                        const distance = distField[i][j];
                        const brightness = p.map(distance, 0, 100, 255, 0);
                        p.fill(brightness);
                    }

                    p.rect(x, y, cellSize, cellSize);
                }
            }

            if (path.length > 0) {
                p.stroke('#00FF00');
                p.strokeWeight(3);
                p.beginShape();
                path.forEach(([i, j]) => {
                    const cx = i * cellSize + cellSize / 2;
                    const cy = j * cellSize + cellSize / 2;
                    p.vertex(cx, cy);
                });
                p.endShape();
            }

            const startIdx1 = Math.round(map(startConfig.q1, -Math.PI, Math.PI, 0, N - 1));
            const startIdx2 = Math.round(map(startConfig.q2, -Math.PI, Math.PI, 0, N - 1));
            const goalIdx1 = Math.round(map(goalConfig.q1, -Math.PI, Math.PI, 0, N - 1));
            const goalIdx2 = Math.round(map(goalConfig.q2, -Math.PI, Math.PI, 0, N - 1));

            drawMarker(p, startIdx1, startIdx2, '#00FF00');
            drawMarker(p, goalIdx1, goalIdx2, '#00FF00');
        };

        function drawMarker(p, i, j, color) {
            const N = 10 * Math.pow(2, q);
            const cellSize = phaseSpaceWidth / N;
            const cx = i * cellSize + cellSize / 2;
            const cy = j * cellSize + cellSize / 2;

            p.stroke(color);
            p.strokeWeight(2);
            p.fill(color);
            p.circle(cx, cy, cellSize * 0.8);

            p.strokeWeight(1);
            p.stroke(255);
            p.line(cx - cellSize * 0.2, cy, cx + cellSize * 0.2, cy);
            p.line(cx, cy - cellSize * 0.2, cx, cy + cellSize * 0.2);
        }

        workspaceSketch.mousePressed = function(p) {
            if (p.dist(p.mouseX, p.mouseY, obstacle1.x, obstacle1.y) < obstacle1.radius) {
                isDraggingObstacle1 = true;
            } else if (p.dist(p.mouseX, p.mouseY, obstacle2.x, obstacle2.y) < obstacle2.radius) {
                isDraggingObstacle2 = true;
            }
        };

        workspaceSketch.mouseDragged = function(p) {
            if (isDraggingObstacle1) {
                obstacle1.x = p.mouseX;
                obstacle1.y = p.mouseY;
                calculateCspace();
                runBFS();
                findShortestPath();
                updatePhaseSpaceTitle();
            } else if (isDraggingObstacle2) {
                obstacle2.x = p.mouseX;
                obstacle2.y = p.mouseY;
                calculateCspace();
                runBFS();
                findShortestPath();
                updatePhaseSpaceTitle();
            }
        };

        workspaceSketch.mouseReleased = function(p) {
            isDraggingObstacle1 = false;
            isDraggingObstacle2 = false;
        };

        phaseSpaceSketch.mousePressed = function(p) {
            const N = 10 * Math.pow(2, q);
            const cellSize = phaseSpaceWidth / N;
            const i = Math.floor(p.mouseX / cellSize);
            const j = Math.floor(p.mouseY / cellSize);

            if (cSpace[i][j]) return;

            if (p.dist(p.mouseX, p.mouseY, i * cellSize + cellSize / 2, j * cellSize + cellSize / 2) < cellSize * 0.4) {
                isDraggingStart = true;
            } else if (p.dist(p.mouseX, p.mouseY, i * cellSize + cellSize / 2, j * cellSize + cellSize / 2) < cellSize * 0.4) {
                isDraggingGoal = true;
            }
        };

        phaseSpaceSketch.mouseDragged = function(p) {
            const N = 10 * Math.pow(2, q);
            const cellSize = phaseSpaceWidth / N;
            const i = Math.floor(p.mouseX / cellSize);
            const j = Math.floor(p.mouseY / cellSize);

            if (cSpace[i][j]) return;

            if (isDraggingStart) {
                startConfig.q1 = map(i, 0, N, -Math.PI, Math.PI);
                startConfig.q2 = map(j, 0, N, -Math.PI, Math.PI);
                findShortestPath();
            } else if (isDraggingGoal) {
                goalConfig.q1 = map(i, 0, N, -Math.PI, Math.PI);
                goalConfig.q2 = map(j, 0, N, -Math.PI, Math.PI);
                calculateCspace();
                runBFS();
                findShortestPath();
                updatePhaseSpaceTitle();
            }
        };

        phaseSpaceSketch.mouseReleased = function(p) {
            isDraggingStart = false;
            isDraggingGoal = false;
        };

        window.onload = setup;
    </script>
</body>
</html>
