                    <button id="btn-reset">|◄</button>
                </div>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let dimension = '2D';
        let seed = 525;
        let numberOfPoints = 40;
        let algorithm = 'prim';
        let animationState = 'stopped';
        let points = [];
        let mstEdges = [];
        let algorithmSteps = [];
        let currentStep = 0;

        const setup = () => {
            createCanvas(windowWidth - 250, windowHeight, dimension === '3D' ? WEBGL : P2D);
            background('#E0E0E0');
            reset();
        };

        const draw = () => {
            if (animationState === 'playing') {
                if (currentStep < algorithmSteps.length) {
                    mstEdges.push(algorithmSteps[currentStep]);
                    currentStep++;
                } else {
                    stopAnimation();
                }
            }

            background('#E0E0E0');
            if (dimension === '3D') {
                orbitControl();
                stroke(100);
                box(500);
            }

            points.forEach(point => {
                fill('blue');
                noStroke();
                if (dimension === '2D') {
                    ellipse(point.x, point.y, 10, 10);
                } else {
                    push();
                    translate(point.x, point.y, point.z);
                    sphere(5);
                    pop();
                }
            });

            stroke('red');
            strokeWeight(2);
            mstEdges.forEach(edge => {
                if (dimension === '2D') {
                    line(edge[0].x, edge[0].y, edge[1].x, edge[1].y);
                } else {
                    line(edge[0].x, edge[0].y, edge[0].z, edge[1].x, edge[1].y, edge[1].z);
                }
            });
        };

        const reset = () => {
            animationState = 'stopped';
            currentStep = 0;
            mstEdges = [];
            algorithmSteps = [];
            generatePoints();
            calculateAlgorithmSteps();
            document.getElementById('btn-play').disabled = false;
            document.getElementById('btn-pause').disabled = true;
            document.getElementById('btn-reset').disabled = false;
        };

        const generatePoints = () => {
            randomSeed(seed);
            points = Array.from({ length: numberOfPoints }, () => {
                return createVector(
                    random(-250, 250),
                    random(-250, 250),
                    dimension === '3D' ? random(-250, 250) : 0
                );
            });
        };

        const calculateAlgorithmSteps = () => {
            if (algorithm === 'prim') {
                primAlgorithm();
            } else {
                kruskalAlgorithm();
            }
        };

        const primAlgorithm = () => {
            const visited = new Set();
            const minHeap = [];
            const edges = [];

            const addEdges = (pointIndex) => {
                visited.add(pointIndex);
                points.forEach((point, index) => {
                    if (!visited.has(index)) {
                        const distance = p5.Vector.dist(points[pointIndex], point);
                        minHeap.push({ distance, from: pointIndex, to: index });
                    }
                });
                minHeap.sort((a, b) => a.distance - b.distance);
            };

            addEdges(0);

            while (edges.length < numberOfPoints - 1 && minHeap.length > 0) {
                const { distance, from, to } = minHeap.shift();
                if (!visited.has(to)) {
                    edges.push([points[from], points[to]]);
                    addEdges(to);
                }
            }

            algorithmSteps = edges;
        };

        const kruskalAlgorithm = () => {
            const edges = [];
            const parent = Array.from({ length: numberOfPoints }, (_, i) => i);

            const find = (node) => {
                if (parent[node] !== node) {
                    parent[node] = find(parent[node]);
                }
                return parent[node];
            };

            const union = (node1, node2) => {
                const root1 = find(node1);
                const root2 = find(node2);
                if (root1 !== root2) {
                    parent[root2] = root1;
                    return true;
                }
                return false;
            };

            points.forEach((point, i) => {
                points.slice(i + 1).forEach((otherPoint, j) => {
                    const distance = p5.Vector.dist(point, otherPoint);
                    edges.push({ distance, from: i, to: i + j + 1 });
                });
            });

            edges.sort((a, b) => a.distance - b.distance);

            const mstEdges = [];
            for (const { from, to } of edges) {
                if (union(from, to)) {
                    mstEdges.push([points[from], points[to]]);
                    if (mstEdges.length === numberOfPoints - 1) break;
                }
            }

            algorithmSteps = mstEdges;
        };

        const startAnimation = () => {
            animationState = 'playing';
            document.getElementById('btn-play').disabled = true;
            document.getElementById('btn-pause').disabled = false;
        };

        const pauseAnimation = () => {
            animationState = 'paused';
            document.getElementById('btn-play').disabled = false;
            document.getElementById('btn-pause').disabled = true;
        };

        const stopAnimation = () => {
            animationState = 'stopped';
            document.getElementById('btn-play').disabled = false;
            document.getElementById('btn-pause').disabled = true;
        };

        document.getElementById('btn-2d').addEventListener('click', () => {
            if (dimension !== '2D') {
                dimension = '2D';
                document.getElementById('btn-2d').classList.add('active');
                document.getElementById('btn-3d').classList.remove('active');
                reset();
            }
        });

        document.getElementById('btn-3d').addEventListener('click', () => {
            if (dimension !== '3D') {
                dimension = '3D';
                document.getElementById('btn-3d').classList.add('active');
                document.getElementById('btn-2d').classList.remove('active');
                reset();
            }
        });

        document.getElementById('slider-seed').addEventListener('input', () => {
            seed = parseInt(document.getElementById('slider-seed').value);
            document.getElementById('seed-value').textContent = seed;
            reset();
        });

        document.getElementById('slider-number').addEventListener('input', () => {
            numberOfPoints = parseInt(document.getElementById('slider-number').value);
            document.getElementById('number-value').textContent = numberOfPoints;
            reset();
        });

        document.getElementById('btn-prim').addEventListener('click', () => {
            if (algorithm !== 'prim') {
                algorithm = 'prim';
                document.getElementById('btn-prim').classList.add('active');
                document.getElementById('btn-kruskal').classList.remove('active');
                reset();
            }
        });

        document.getElementById('btn-kruskal').addEventListener('click', () => {
            if (algorithm !== 'kruskal') {
                algorithm = 'kruskal';
                document.getElementById('btn-kruskal').classList.add('active');
                document.getElementById('btn-prim').classList.remove('active');
                reset();
            }
        });

        document.getElementById('btn-play').addEventListener('click', startAnimation);
        document.getElementById('btn-pause').addEventListener('click', pauseAnimation);
        document.getElementById('btn-reset').addEventListener('click', reset);

        new p5(setup, document.getElementById('canvas-container'));
    </script>
</body>
</html>
