    <script>
        const num1Slider = document.getElementById('slider-num1');
        const num2Slider = document.getElementById('slider-num2');
        const intYesBtn = document.getElementById('btn-int-yes');
        const intNoBtn = document.getElementById('btn-int-no');
        const methodSelect = document.getElementById('select-method');
        const outputDisplay = document.getElementById('output-display');

        function updateDisplay() {
            const num1 = parseFloat(num1Slider.value);
            const num2 = parseFloat(num2Slider.value);
            const method = methodSelect.value;

            const num1AsInt = parseInt(num1.toString().replace('.', ''));
            const num2AsInt = parseInt(num2.toString().replace('.', ''));
            const productOfInts = num1AsInt * num2AsInt;

            const num1Factor = getPowerOfTenFactor(num1);
            const num2Factor = getPowerOfTenFactor(num2);
            const productOfFactors = num1Factor * num2Factor;

            const roundedNum1 = Math.round(num1);
            const roundedNum2 = Math.round(num2);
            const productOfRounded = roundedNum1 * roundedNum2;

            let outputContent = '';

            if (method === 'estimation') {
                outputContent += `<p>start with: ${num1} \u00D7 ${num2}</p>`;
                outputContent += `<p>convert to: ${num1AsInt}. \u00D7 ${num2AsInt}. = ${productOfInts}.</p>`;
                outputContent += `<p>estimate as: ${roundedNum1} \u00D7 ${roundedNum2} = ${productOfRounded}</p>`;
                outputContent += `<p>result is: ${num1 * num2}</p>`;
            } else if (method === 'place values') {
                outputContent += `<p>start with: ${num1} \u00D7 ${num2}</p>`;
                if (num1Factor !== 1 || num2Factor !== 1) {
                    outputContent += `<p>rewrite as: (${num1AsInt}. \u00F7 ${num1Factor}) \u00D7 (${num2AsInt}. \u00F7 ${num2Factor})</p>`;
                    outputContent += `<p>= (${num1AsInt}. \u00D7 ${num2AsInt}.) \u00F7 (${num1Factor} \u00D7 ${num2Factor})</p>`;
                }
                outputContent += `<p>= ${productOfInts}. \u00F7 ${productOfFactors}</p>`;
                outputContent += `<p>= ${num1 * num2}</p>`;
            }

            outputDisplay.innerHTML = outputContent;
        }

        function getPowerOfTenFactor(num) {
            const decimalPlaces = num.toString().split('.')[1]?.length || 0;
            return Math.pow(10, decimalPlaces);
        }

        num1Slider.addEventListener('input', updateDisplay);
        num2Slider.addEventListener('input', updateDisplay);
        methodSelect.addEventListener('change', updateDisplay);

        intYesBtn.addEventListener('click', () => {
            num2Slider.step = '1';
            num2Slider.value = Math.round(num2Slider.value).toString();
            intYesBtn.classList.add('active');
            intNoBtn.classList.remove('active');
            intNoBtn.classList.add('inactive');
            updateDisplay();
        });

        intNoBtn.addEventListener('click', () => {
            num2Slider.step = '0.1';
            intNoBtn.classList.add('active');
            intYesBtn.classList.remove('active');
            intYesBtn.classList.add('inactive');
            updateDisplay();
        });

        // Initial call to set up the display
        updateDisplay();
    </script>
</body>
</html>
