            const ambientLight = new THREE.AmbientLight(0x404040); // soft white light
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight.position.set(1, 1, 1).normalize();
            scene.add(directionalLight);

            // Carbon atom
            carbon = new THREE.Mesh(
                new THREE.SphereGeometry(0.3, 32, 32),
                new THREE.MeshLambertMaterial({ color: 0x000000 })
            );
            scene.add(carbon);

            // Hydrogen atoms
            hydrogens = [];
            const positions = [
                { x: 1, y: 1, z: 1 },
                { x: 1, y: -1, z: -1 },
                { x: -1, y: 1, z: -1 },
                { x: -1, y: -1, z: 1 }
            ];
            positions.forEach(pos => {
                const sphere = new THREE.Mesh(
                    new THREE.SphereGeometry(0.1, 32, 32),
                    new THREE.MeshLambertMaterial({ color: 0xd8f0ff })
                );
                sphere.position.set(pos.x, pos.y, pos.z);
                hydrogens.push(sphere);
                scene.add(sphere);
            });

            // Bonds
            bonds = [];
            hydrogens.forEach(hydrogen => {
                const bond = new THREE.Mesh(
                    new THREE.CylinderGeometry(0.01, 0.01, hydrogen.position.distanceTo(carbon.position), 32),
                    new THREE.MeshBasicMaterial({ color: 0x808080 })
                );
                bond.position.copy(hydrogen.position.clone().add(carbon.position).multiplyScalar(0.5));
                bond.lookAt(carbon.position);
                bonds.push(bond);
                scene.add(bond);
            });

            // Arrows for dipole vectors
            arrows = [];
            hydrogens.forEach(hydrogen => {
                const arrow = new THREE.ArrowHelper(
                    new THREE.Vector3().copy(hydrogen.position).normalize(),
                    carbon.position,
                    hydrogen.position.distanceTo(carbon.position) * 0.5,
                    0x0000ff
                );
                arrows.push(arrow);
                scene.add(arrow);
            });

            // Net dipole arrow
            netDipoleArrow = new THREE.ArrowHelper(
                new THREE.Vector3(0, 0, 0),
                carbon.position,
                0,
                0xff0000
            );
            scene.add(netDipoleArrow);
        }

        function animate() {
            time += 0.01;
            updatePositions(time);
            renderer.setViewport(0, 0, window.innerWidth / 2, 600);
            renderer.render(scene, camera1);
            renderer.setViewport(window.innerWidth / 2, 0, window.innerWidth / 2, 600);
            renderer.render(scene, camera2);
            animationFrameId = requestAnimationFrame(animate);
        }

        function updatePositions(t) {
            const disp = modes[currentMode].displacement(t);
            hydrogens.forEach((hydrogen, index) => {
                const pos = positions[index];
                if (Array.isArray(disp)) {
                    hydrogen.position.set(pos.x * (1 + disp[index].x), pos.y * (1 + disp[index].y), pos.z * (1 + disp[index].z));
                } else {
                    hydrogen.position.set(pos.x * (1 + disp.x), pos.y * (1 + disp.y), pos.z * (1 + disp.z));
                }
                bonds[index].position.copy(hydrogen.position.clone().add(carbon.position).multiplyScalar(0.5));
                bonds[index].lookAt(carbon.position);
                bonds[index].scale.z = hydrogen.position.distanceTo(carbon.position);
                arrows[index].setDirection(new THREE.Vector3().copy(hydrogen.position).sub(carbon.position).normalize());
                arrows[index].setLength(hydrogen.position.distanceTo(carbon.position) * 0.5);
            });

            // Calculate net dipole
            const netDipole = new THREE.Vector3();
            arrows.forEach(arrow => {
                netDipole.addScaledVector(arrow.direction, arrow.length);
            });
            if (netDipole.length() > 0.1) {
                netDipoleArrow.setDirection(netDipole.normalize());
                netDipoleArrow.setLength(netDipole.length());
                netDipoleArrow.visible = true;
            } else {
                netDipoleArrow.visible = false;
            }
        }

        function stopAnimation() {
            cancelAnimationFrame(animationFrameId);
        }

        function resetAnimation() {
            stopAnimation();
            time = 0;
            updatePositions(time);
        }

        function updateLabels(mode) {
            const modeInfo = modes[mode];
            document.getElementById('label-left').innerText = modeInfo.labelLeft;
            document.getElementById('label-right').innerText = modeInfo.labelRight;
            document.getElementById('label-left').style.color = modeInfo.color;
            document.getElementById('label-right').style.color = modeInfo.color;
        }

        function handleModeChange(event) {
            currentMode = event.target.value;
            resetAnimation();
            if (currentMode === "ir-spectrum") {
                document.getElementById('three-js-canvas-container').style.display = 'none';
                document.getElementById('description-area').style.display = 'none';
                document.getElementById('plotly-chart-container').style.display = 'block';
                document.getElementById('btn-play').disabled = true;
                document.getElementById('btn-pause').disabled = true;
                document.getElementById('btn-reset').disabled = true;
                renderIRChart();
            } else {
                document.getElementById('three-js-canvas-container').style.display = 'flex';
                document.getElementById('description-area').style.display = 'flex';
                document.getElementById('plotly-chart-container').style.display = 'none';
                document.getElementById('btn-play').disabled = false;
                document.getElementById('btn-pause').disabled = false;
                document.getElementById('btn-reset').disabled = false;
                updateLabels(currentMode);
            }
        }

        function renderIRChart() {
            const x = Array.from({ length: 3601 }, (_, i) => 400 + i);
            const baseline = Array.from(x, () => 0.95 + Math.random() * 0.02);
            const gaussian = (x, mu, sigma) => Math.exp(-((x - mu) ** 2) / (2 * sigma ** 2));
            const peaks = [
                { mu: 1306, sigma: 100, height: 0.85 },
                { mu: 3019, sigma: 100, height: 0.85 }
            ];
            peaks.forEach(({ mu, sigma, height }) => {
                baseline.forEach((_, i) => {
                    baseline[i] -= height * gaussian(x[i], mu, sigma);
                });
            });
            const fineStructure = [
                { mu: 1306, sigma: 5, height: 0.1 },
                { mu: 1320, sigma: 5, height: 0.1 },
                { mu: 1340, sigma: 5, height: 0.1 },
                { mu: 3019, sigma: 5, height: 0.1 },
                { mu: 3030, sigma: 5, height: 0.1 },
                { mu: 3050, sigma: 5, height: 0.1 }
            ];
            fineStructure.forEach(({ mu, sigma, height }) => {
                baseline.forEach((_, i) => {
                    baseline[i] -= height * gaussian(x[i], mu, sigma);
                });
            });

            const trace = {
                x: x,
                y: baseline,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'black' }
            };
            const layout = {
                title: 'Methane IR Spectrum',
                xaxis: { title: 'wavenumber (cm⁻¹)', range: [400, 4000] },
                yaxis: { title: 'Transmittance', range: [0, 1.1] },
                annotations: [
                    {
                        x: 1306,
                        y: 0.9,
                        text: 'ν F₂ 1306 cm⁻¹',
                        showarrow: true,
                        arrowhead: 7,
                        ax: 0,
                        ay: -50,
                        font: { color: 'red' }
                    },
                    {
                        x: 3019,
                        y: 0.9,
                        text: 'ν F₂ 3019 cm⁻¹',
                        showarrow: true,
                        arrowhead: 7,
                        ax: 0,
                        ay: -50,
                        font: { color: 'red' }
                    }
                ],
                images: [
                    {
                        source: renderMoleculeImage("bending-f2"),
                        xref: "x",
                        yref: "y",
                        x: 1306,
                        y: 1.05,
                        sizex: 100,
                        sizey: 100,
                        xanchor: "center",
                        yanchor: "bottom"
                    },
                    {
                        source: renderMoleculeImage("asymmetric-stretch"),
                        xref: "x",
                        yref: "y",
                        x: 3019,
                        y: 1.05,
                        sizex: 100,
                        sizey: 100,
                        xanchor: "center",
                        yanchor: "bottom"
                    }
                ]
            };
            Plotly.newPlot('plotly-chart-container', [trace], layout);
        }

        function renderMoleculeImage(mode) {
            const offScene = new THREE.Scene();
            const offCamera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
            offCamera.position.z = 5;

            const offRenderer = new THREE.WebGLRenderer({ alpha: true });
            offRenderer.setSize(100, 100);

            const offCarbon = new THREE.Mesh(
                new THREE.SphereGeometry(0.3, 32, 32),
                new THREE.MeshLambertMaterial({ color: 0x000000 })
            );
            offScene.add(offCarbon);

            const offHydrogens = [];
            positions.forEach(pos => {
                const sphere = new THREE.Mesh(
                    new THREE.SphereGeometry(0.1, 32, 32),
                    new THREE.MeshLambertMaterial({ color: 0xd8f0ff })
                );
                sphere.position.set(pos.x, pos.y, pos.z);
                offHydrogens.push(sphere);
                offScene.add(sphere);
            });

            const offBonds = [];
            offHydrogens.forEach(hydrogen => {
                const bond = new THREE.Mesh(
                    new THREE.CylinderGeometry(0.01, 0.01, hydrogen.position.distanceTo(offCarbon.position), 32),
                    new THREE.MeshBasicMaterial({ color: 0x808080 })
                );
                bond.position.copy(hydrogen.position.clone().add(offCarbon.position).multiplyScalar(0.5));
                bond.lookAt(offCarbon.position);
                offBonds.push(bond);
                offScene.add(bond);
            });

            const offArrows = [];
            offHydrogens.forEach(hydrogen => {
                const arrow = new THREE.ArrowHelper(
                    new THREE.Vector3().copy(hydrogen.position).normalize(),
                    offCarbon.position,
                    hydrogen.position.distanceTo(offCarbon.position) * 0.5,
                    0x0000ff
                );
                offArrows.push(arrow);
                offScene.add(arrow);
            });

            const offNetDipoleArrow = new THREE.ArrowHelper(
                new THREE.Vector3(0, 0, 0),
                offCarbon.position,
                0,
                0xff0000
            );
            offScene.add(offNetDipoleArrow);

            const disp = modes[mode].displacement(0);
            offHydrogens.forEach((hydrogen, index) => {
                const pos = positions[index];
                if (Array.isArray(disp)) {
                    hydrogen.position.set(pos.x * (1 + disp[index].x), pos.y * (1 + disp[index].y), pos.z * (1 + disp[index].z));
                } else {
                    hydrogen.position.set(pos.x * (1 + disp.x), pos.y * (1 + disp.y), pos.z * (1 + disp.z));
                }
                offBonds[index].position.copy(hydrogen.position.clone().add(offCarbon.position).multiplyScalar(0.5));
                offBonds[index].lookAt(offCarbon.position);
                offBonds[index].scale.z = hydrogen.position.distanceTo(offCarbon.position);
                offArrows[index].setDirection(new THREE.Vector3().copy(hydrogen.position).sub(offCarbon.position).normalize());
                offArrows[index].setLength(hydrogen.position.distanceTo(offCarbon.position) * 0.5);
            });

            const offNetDipole = new THREE.Vector3();
            offArrows.forEach(arrow => {
                offNetDipole.addScaledVector(arrow.direction, arrow.length);
            });
            if (offNetDipole.length() > 0.1) {
                offNetDipoleArrow.setDirection(offNetDipole.normalize());
                offNetDipoleArrow.setLength(offNetDipole.length());
                offNetDipoleArrow.visible = true;
            } else {
                offNetDipoleArrow.visible = false;
            }

            offRenderer.render(offScene, offCamera);
            return offRenderer.domElement.toDataURL();
        }

        document.getElementById('select-vibration-mode').addEventListener('change', handleModeChange);
        document.getElementById('btn-play').addEventListener('click', () => {
            document.getElementById('btn-play').disabled = true;
            document.getElementById('btn-pause').disabled = false;
            animate();
        });
        document.getElementById('btn-pause').addEventListener('click', () => {
            document.getElementById('btn-play').disabled = false;
            document.getElementById('btn-pause').disabled = true;
            stopAnimation();
        });
        document.getElementById('btn-reset').addEventListener('click', resetAnimation);

        initThreeJS();
        updateLabels(currentMode);
    </script>
</body>
</html>
