<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Stefan Distance Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
            margin: 0;
            padding: 20px;
        }
        #control-panel {
            display: flex;
            align-items: center;
            margin-bottom: 20px;
        }
        #control-panel label {
            margin-right: 10px;
        }
        #control-panel span {
            margin-left: 10px;
            margin-right: 20px;
        }
        #btn-reset {
            width: 30px;
            height: 30px;
            border-radius: 50%;
            background-color: #007bff;
            color: white;
            border: none;
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
        }
        #material-visualization {
            width: 600px;
            height: 80px;
            margin-bottom: 20px;
            border: 1px solid #ccc;
        }
        #div-plot {
            width: 600px;
            height: 400px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-time">time (seconds)</label>
            <input type="range" id="slider-time" min="0" max="100" step="0.1" value="9.8">
            <span id="span-time-value">9.8</span>
            <button id="btn-reset">+</button>
        </div>
        <canvas id="canvas-material-bar" width="600" height="80"></canvas>
        <div id="div-plot"></div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        const sliderTime = document.getElementById('slider-time');
        const spanTimeValue = document.getElementById('span-time-value');
        const btnReset = document.getElementById('btn-reset');
        const canvasMaterialBar = document.getElementById('canvas-material-bar');
        const ctx = canvasMaterialBar.getContext('2d');
        const divPlot = document.getElementById('div-plot');

        const totalLength = 16; // in mm
        const canvasWidth = 600; // in pixels
        const canvasHeight = 80; // in pixels
        const scale = canvasWidth / totalLength; // pixels per mm

        function stefanDistance(t) {
            return 0.155 * Math.sqrt(t);
        }

        function drawMaterialBar(t) {
            ctx.clearRect(0, 0, canvasWidth, canvasHeight);

            // Draw glass block
            const glassWidth = 6 * scale;
            const glassGradient = ctx.createLinearGradient(0, 0, glassWidth, 0);
            glassGradient.addColorStop(0, '#FF0000');
            glassGradient.addColorStop(1, '#FFFFFF');
            ctx.fillStyle = glassGradient;
            ctx.fillRect(0, 0, glassWidth, canvasHeight);

            // Draw melted water
            const waterWidth = stefanDistance(t) * scale;
            ctx.fillStyle = '#00BFFF';
            ctx.fillRect(glassWidth, 0, waterWidth, canvasHeight);

            // Draw ice block
            const iceStart = glassWidth + waterWidth;
            ctx.fillStyle = '#003366';
            ctx.fillRect(iceStart, 0, canvasWidth - iceStart, canvasHeight);

            // Draw axis
            ctx.strokeStyle = '#000000';
            ctx.beginPath();
            ctx.moveTo(0, canvasHeight - 10);
            ctx.lineTo(canvasWidth, canvasHeight - 10);
            ctx.stroke();

            // Draw axis labels
            ctx.font = '12px Arial';
            ctx.textAlign = 'center';
            [0, 5, 10, 15].forEach(label => {
                const x = label * scale;
                ctx.fillText(label, x, canvasHeight - 5);
            });
        }

        function updatePlot(t) {
            const s = stefanDistance(t);
            Plotly.relayout(divPlot, {
                shapes: [{
                    type: 'line',
                    x0: t,
                    y0: 0,
                    x1: t,
                    y1: s,
                    line: {
                        color: 'black',
                        width: 2
                    }
                }]
            });
        }

        function masterUpdate(t) {
            spanTimeValue.textContent = t.toFixed(1);
            drawMaterialBar(t);
            updatePlot(t);
        }

        function initPlot() {
            const xValues = Array.from({ length: 101 }, (_, i) => i);
            const yValues = xValues.map(stefanDistance);

            Plotly.newPlot(divPlot, [{
                x: xValues,
                y: yValues,
                mode: 'lines',
                line: {
                    color: '#FFA500'
                }
            }], {
                title: 'Stefan distance versus time',
                xaxis: {
                    title: 'time (s)',
                    range: [0, 100]
                },
                yaxis: {
                    title: 'distance (mm)',
                    range: [0, 1.6]
                },
                shapes: [{
                    type: 'line',
                    x0: 9.8,
                    y0: 0,
                    x1: 9.8,
                    y1: stefanDistance(9.8),
                    line: {
                        color: 'black',
                        width: 2
                    }
                }]
            });
        }

        sliderTime.addEventListener('input', () => {
            const t = parseFloat(sliderTime.value);
            masterUpdate(t);
        });

        btnReset.addEventListener('click', () => {
            sliderTime.value = 0;
            sliderTime.dispatchEvent(new Event('input'));
        });

        // Initial setup
        initPlot();
        masterUpdate(9.8);
    </script>
</body>
</html>
