            width: 100%;
        }
        #local-clock-group, #solar-clock-group {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #local-clock-wrapper, #solar-clock-wrapper {
            position: relative;
            width: 200px;
            height: 200px;
        }
        #local-clock-canvas, #solar-clock-canvas {
            width: 100%;
            height: 100%;
        }
        #local-time-digital, #solar-time-digital, #offset-display {
            background: white;
            border: 1px solid black;
            padding: 2px 5px;
            margin-top: 10px;
            text-align: center;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label for="date-slider">date</label>
            <input type="range" id="date-slider" min="0" max="364" step="1" value="340">
            <span id="date-display">Friday, 07 December</span>
            <button id="today-button">today</button>
        </div>
        <div id="visualization-panel">
            <div id="clocks-container">
                <div id="local-clock-group">
                    <h2>Local Time</h2>
                    <div id="local-clock-wrapper">
                        <canvas id="local-clock-canvas"></canvas>
                        <div id="local-time-digital">00:00:00</div>
                    </div>
                </div>
                <div id="solar-clock-group">
                    <h2>Solar Time</h2>
                    <div id="solar-clock-wrapper">
                        <canvas id="solar-clock-canvas"></canvas>
                        <div id="solar-time-digital">00:00:00</div>
                    </div>
                </div>
            </div>
        </div>
        <div id="offset-display">offset of solar time from local time: -46 minutes</div>
    </div>

    <script>
        let userLatitude = 51.5;
        let userLongitude = 0;

        const dateSlider = document.getElementById('date-slider');
        const dateDisplay = document.getElementById('date-display');
        const todayButton = document.getElementById('today-button');
        const localTimeDigital = document.getElementById('local-time-digital');
        const solarTimeDigital = document.getElementById('solar-time-digital');
        const offsetDisplay = document.getElementById('offset-display');

        navigator.geolocation.getCurrentPosition(
            (position) => {
                userLatitude = position.coords.latitude;
                userLongitude = position.coords.longitude;
            },
            () => {
                console.log("Geolocation failed, defaulting to Greenwich, London.");
            }
        );

        function drawClock(timeObject, faceColor, canvasId) {
            const canvas = document.getElementById(canvasId);
            const ctx = canvas.getContext('2d');
            const centerX = canvas.width / 2;
            const centerY = canvas.height / 2;
            const radius = 80;

            // Clear the canvas
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Draw face
            ctx.beginPath();
            ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI);
            ctx.lineWidth = 4;
            ctx.strokeStyle = 'black';
            ctx.stroke();

            ctx.beginPath();
            ctx.arc(centerX, centerY, radius - 4, 0, 2 * Math.PI);
            ctx.fillStyle = faceColor;
            ctx.fill();

            // Draw markers
            for (let i = 0; i < 60; i++) {
                const angle = (i / 60) * 2 * Math.PI - Math.PI / 2;
                const x1 = centerX + (radius - 8) * Math.cos(angle);
                const y1 = centerY + (radius - 8) * Math.sin(angle);
                const x2 = centerX + (radius - 2) * Math.cos(angle);
                const y2 = centerY + (radius - 2) * Math.sin(angle);
                ctx.beginPath();
                ctx.moveTo(x1, y1);
                ctx.lineTo(x2, y2);
                ctx.strokeStyle = 'black';
                ctx.lineWidth = i % 5 === 0 ? 3 : 1;
                ctx.stroke();
            }

            // Draw numbers
            ctx.font = '14px Arial';
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            for (let i = 1; i <= 12; i++) {
                const angle = (i / 12) * 2 * Math.PI - Math.PI / 2;
                const x = centerX + (radius - 20) * Math.cos(angle);
                const y = centerY + (radius - 20) * Math.sin(angle);
                ctx.fillText(i, x, y);
            }

            // Draw hands
            const hourAngle = ((timeObject.getHours() % 12) / 12) * 2 * Math.PI + (timeObject.getMinutes() / 60) * (2 * Math.PI / 12) - Math.PI / 2;
            const minuteAngle = (timeObject.getMinutes() / 60) * 2 * Math.PI - Math.PI / 2;
            const secondAngle = (timeObject.getSeconds() / 60) * 2 * Math.PI - Math.PI / 2;

            // Hour hand
            ctx.beginPath();
            ctx.moveTo(centerX, centerY);
            ctx.lineTo(centerX + 40 * Math.cos(hourAngle), centerY + 40 * Math.sin(hourAngle));
            ctx.strokeStyle = 'black';
            ctx.lineWidth = 6;
            ctx.stroke();

            // Minute hand
            ctx.beginPath();
            ctx.moveTo(centerX, centerY);
            ctx.lineTo(centerX + 70 * Math.cos(minuteAngle), centerY + 70 * Math.sin(minuteAngle));
            ctx.strokeStyle = 'black';
            ctx.lineWidth = 4;
            ctx.stroke();

            // Second hand
            ctx.beginPath();
            ctx.moveTo(centerX, centerY);
            ctx.lineTo(centerX + 70 * Math.cos(secondAngle), centerY + 70 * Math.sin(secondAngle));
            ctx.strokeStyle = 'red';
            ctx.lineWidth = 2;
            ctx.stroke();

            // Center pin
            ctx.beginPath();
            ctx.arc(centerX, centerY, 5, 0, 2 * Math.PI);
            ctx.fillStyle = 'black';
            ctx.fill();
        }

        function updateClocks() {
            const localTime = new Date();
            const utcTime = new Date(localTime.getTime() + localTime.getTimezoneOffset() * 60000);
            const dayOfYear = parseInt(dateSlider.value);
            const B = (360 / 365.24) * (dayOfYear - 81);
            const EoT = 9.87 * Math.sin(2 * B * Math.PI / 180) - 7.53 * Math.cos(B * Math.PI / 180) - 1.5 * Math.sin(B * Math.PI / 180);
            const timeOffsetMinutes = EoT + 4 * userLongitude;
            const solarTimeMilliseconds = utcTime.getTime() + timeOffsetMinutes * 60000;
            const solarTime = new Date(solarTimeMilliseconds);

            drawClock(localTime, '#E6E6FA', 'local-clock-canvas');
            drawClock(solarTime, '#FAFAD2', 'solar-clock-canvas');

            localTimeDigital.textContent = localTime.toLocaleTimeString();
            solarTimeDigital.textContent = solarTime.toLocaleTimeString();

            const timeDifference = Math.round((solarTime.getTime() - localTime.getTime()) / 60000);
            offsetDisplay.textContent = `offset of solar time from local time: ${timeDifference} minutes`;
        }

        function updateDateDisplay() {
            const dayOfYear = parseInt(dateSlider.value);
            const year = new Date().getFullYear();
            const startDate = new Date(year, 0, 1);
            const currentDate = new Date(startDate.getTime() + dayOfYear * 24 * 60 * 60 * 1000);
            dateDisplay.textContent = currentDate.toLocaleDateString('en-US', { weekday: 'long', day: 'numeric', month: 'long' });
        }

        dateSlider.addEventListener('input', () => {
            updateDateDisplay();
        });

        todayButton.addEventListener('click', () => {
            const now = new Date();
            const startOfYear = new Date(now.getFullYear(), 0, 1);
            const dayOfYear = Math.floor((now - startOfYear) / (24 * 60 * 60 * 1000));
            dateSlider.value = dayOfYear;
            updateDateDisplay();
        });

        setInterval(updateClocks, 1000);
        updateDateDisplay();
        updateClocks();
    </script>
</body>
</html>
