    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <!-- Main container for the demo -->
    <div id="demo-container">
        <!-- Control panel section -->
        <div id="control-panel">
            <div>
                <label for="input-steps">steps</label>
                <input type="number" id="input-steps" min="0" max="6" step="1" value="2">
            </div>
            <div>
                <label for="checkbox-zoom">zoom</label>
                <input type="checkbox" id="checkbox-zoom">
            </div>
            <div>
                <label for="select-color">color</label>
                <select id="select-color">
                    <option value="DarkRainbow">DarkRainbow</option>
                    <option value="SiennaTones">SiennaTones</option>
                    <option value="RustTones">RustTones</option>
                    <option value="FuchsiaTones">FuchsiaTones</option>
                    <option value="Grays">Grays</option>
                </select>
            </div>
        </div>
        <!-- Visualization canvas container -->
        <div id="canvas-container"></div>
    </div>

    <script>
        const colorPalettes = {
            DarkRainbow: ['#1E4363', '#2E6341'],
            SiennaTones: ['#A0522D', '#CD853F', '#D2B48C'],
            RustTones: ['#B7410E', '#000033', '#FFFFFF'],
            FuchsiaTones: ['#C71585', '#000000', '#FFFFFF'],
            Grays: ['#404040', '#808080', '#C0C0C0']
        };

        let steps = 2;
        let zoom = false;
        let colorPalette = colorPalettes.DarkRainbow;

        function setup() {
            createCanvas(document.getElementById('canvas-container').clientWidth, 600);
            angleMode(DEGREES);
            noLoop();

            document.getElementById('input-steps').addEventListener('input', updateSteps);
            document.getElementById('checkbox-zoom').addEventListener('change', updateZoom);
            document.getElementById('select-color').addEventListener('change', updateColor);
        }

        function draw() {
            background(255);
            translate(width / 2, height / 2);

            const scaleFactor = zoom ? pow(sqrt(7), steps) : 1;
            scale(scaleFactor);

            if (!zoom) {
                const baseSize = 100 / scaleFactor;
                drawHexagon(0, 0, baseSize);
            }

            drawTiling(steps, 0, 0, 100, 0);
        }

        function updateSteps(event) {
            steps = parseInt(event.target.value, 10);
            redraw();
        }

        function updateZoom(event) {
            zoom = event.target.checked;
            redraw();
        }

        function updateColor(event) {
            colorPalette = colorPalettes[event.target.value];
            redraw();
        }

        function drawHexagon(centerX, centerY, size) {
            const angleStep = 60;
            beginShape();
            for (let angle = 0; angle < 360; angle += angleStep) {
                const x = centerX + size * cos(angle);
                const y = centerY + size * sin(angle);
                vertex(x, y);
            }
            endShape(CLOSE);
        }

        function drawRhombus(centerX, centerY, size, angleRad) {
            const halfDiag1 = size;
            const halfDiag2 = size / sqrt(3);
            const angleOffset = angleRad - 30;
            const vertices = [
                { x: centerX + halfDiag1 * cos(angleOffset), y: centerY + halfDiag1 * sin(angleOffset) },
                { x: centerX + halfDiag2 * cos(angleOffset + 60), y: centerY + halfDiag2 * sin(angleOffset + 60) },
                { x: centerX - halfDiag1 * cos(angleOffset), y: centerY - halfDiag1 * sin(angleOffset) },
                { x: centerX - halfDiag2 * cos(angleOffset + 60), y: centerY - halfDiag2 * sin(angleOffset + 60) }
            ];

            fill(colorPalette[floor(angleRad / 120) % colorPalette.length]);
            quad(vertices[0].x, vertices[0].y, vertices[1].x, vertices[1].y, vertices[2].x, vertices[2].y, vertices[3].x, vertices[3].y);

            // Draw faint diagonal lines
            stroke(255, 150);
            line(vertices[0].x, vertices[0].y, vertices[2].x, vertices[2].y);
            line(vertices[1].x, vertices[1].y, vertices[3].x, vertices[3].y);
            noStroke();
        }

        function drawTiling(level, centerX, centerY, size, angleRad) {
            if (level === 0) {
                drawRhombus(centerX, centerY, size, angleRad);
                return;
            }

            const newSize = size / sqrt(7);
            const rotationAngle = atan(sqrt(3) / 5);

            const positions = [
                { x: 0, y: 0, angle: angleRad },
                { x: newSize * cos(angleRad), y: newSize * sin(angleRad), angle: angleRad },
                { x: newSize * cos(angleRad + 120), y: newSize * sin(angleRad + 120), angle: angleRad + 120 },
                { x: newSize * cos(angleRad - 120), y: newSize * sin(angleRad - 120), angle: angleRad - 120 },
                { x: newSize * cos(angleRad + 120 - rotationAngle), y: newSize * sin(angleRad + 120 - rotationAngle), angle: angleRad + 120 - rotationAngle },
                { x: newSize * cos(angleRad - 120 + rotationAngle), y: newSize * sin(angleRad - 120 + rotationAngle), angle: angleRad - 120 + rotationAngle },
                { x: newSize * cos(angleRad - rotationAngle), y: newSize * sin(angleRad - rotationAngle), angle: angleRad - rotationAngle }
            ];

            positions.forEach(pos => drawTiling(level - 1, centerX + pos.x, centerY + pos.y, newSize, pos.angle));
        }
    </script>
</body>
</html>
