    <script>
        const magnetOrientations = [3, 2, 4, 5, 0];
        const orientationSymbols = ['→', '←', '↑', '↓', '⊙', '⊗'];
        let plotType = 'stream';
        let strength = 5;

        function setup() {
            createCanvas(800, 800);
            translate(width / 2, height / 2);
            redrawCanvas();
        }

        function draw() {}

        function redrawCanvas() {
            clear();
            background(255);
            translate(-width / 2, -height / 2);
            drawMagneticField();
            drawMagnets();
        }

        function drawMagneticField() {
            if (plotType === 'stream') {
                drawStreamPlot();
            } else if (plotType === 'density') {
                drawDensityPlot();
            }
        }

        function drawStreamPlot() {
            const numStreams = 20;
            const stepSize = 10;
            const maxSteps = 100;

            for (let i = -numStreams; i <= numStreams; i++) {
                for (let j = -numStreams; j <= numStreams; j++) {
                    const startX = map(i, -numStreams, numStreams, -400, 400);
                    const startY = map(j, -numStreams, numStreams, -400, 400);
                    drawStreamline(startX, startY, stepSize, maxSteps);
                }
            }
        }

        function drawStreamline(x, y, stepSize, maxSteps) {
            stroke(0, 0, 255);
            noFill();

            let px = x;
            let py = y;

            for (let i = 0; i < maxSteps; i++) {
                const field = calculateField(px, py);
                if (field.mag() < 0.01) break;

                const nx = px + field.x * stepSize;
                const ny = py + field.y * stepSize;

                if (nx < -400 || nx > 400 || ny < -400 || ny > 400) break;

                line(px, py, nx, ny);

                if (i % 10 === 0) {
                    drawArrowhead(nx, ny, field.heading());
                }

                px = nx;
                py = ny;
            }
        }

        function drawArrowhead(x, y, angle) {
            push();
            translate(x, y);
            rotate(angle);
            triangle(0, 0, -5, -3, -5, 3);
            pop();
        }

        function drawDensityPlot() {
            const gridSize = 50;
            const minMag = Infinity;
            const maxMag = -Infinity;
            const magValues = [];

            for (let i = 0; i < gridSize; i++) {
                const row = [];
                for (let j = 0; j < gridSize; j++) {
                    const x = map(i, 0, gridSize, -400, 400);
                    const y = map(j, 0, gridSize, -400, 400);
                    const field = calculateField(x, y);
                    const mag = field.mag();
                    row.push(mag);
                    if (mag < minMag) minMag = mag;
                    if (mag > maxMag) maxMag = mag;
                }
                magValues.push(row);
            }

            for (let i = 0; i < gridSize; i++) {
                for (let j = 0; j < gridSize; j++) {
                    const x = map(i, 0, gridSize, 0, width);
                    const y = map(j, 0, gridSize, 0, height);
                    const mag = magValues[i][j];
                    const normMag = map(mag, minMag, maxMag, 0, 1);
                    const col = lerpColor(color(255, 255, 204), color(0, 0, 150), normMag);
                    fill(col);
                    noStroke();
                    rect(x, y, width / gridSize, height / gridSize);
                }
            }
        }

        function calculateField(x, y) {
            const L = 100; // Scaled size of a magnet
            let totalField = createVector(0, 0);

            for (let i = 0; i < 5; i++) {
                const rx = ((i - 2) * L) - x;
                const ry = -y;
                const rSquared = rx * rx + ry * ry;
                const rCubed = rSquared * sqrt(rSquared);
                const rFifth = rCubed * rSquared;

                const orientation = magnetOrientations[i];
                const mx = [1, -1, 0, 0, 0, 0][orientation];
                const my = [0, 0, 1, -1, 0, 0][orientation];
                const mz = [0, 0, 0, 0, 1, -1][orientation];

                const bx = (3 * rx * (mx * rx + my * ry) / rFifth) - (mx / rCubed);
                const by = (3 * ry * (mx * rx + my * ry) / rFifth) - (my / rCubed);
                const bz = (3 * rz * (mx * rx + my * ry) / rFifth) - (mz / rCubed);

                totalField.add(createVector(bx, by).mult(strength));
            }

            return totalField;
        }

        function drawMagnets() {
            const L = 100; // Scaled size of a magnet

            for (let i = 0; i < 5; i++) {
                const x = (i - 2) * L;
                const y = 0;
                const w = 50;
                const h = 100;

                // Draw magnet body
                fill(192);
                stroke(0);
                quad(x - w / 2, y - h / 2, x + w / 2, y - h / 2, x + w / 2, y + h / 2, x - w / 2, y + h / 2);
                line(x - w / 2, y - h / 2, x - w / 2, y + h / 2);
                line(x + w / 2, y - h / 2, x + w / 2, y + h / 2);

                // Draw orientation symbol
                fill(0, 0, 255);
                noStroke();
                const orientation = magnetOrientations[i];
                const symbol = orientationSymbols[orientation];
                textSize(20);
                textAlign(CENTER, CENTER);
                text(symbol, x, y - h / 2 + 20);
            }
        }

        function updateOrientation(index, delta) {
            magnetOrientations[index] = (magnetOrientations[index] + delta + 6) % 6;
            document.getElementById(`magnet-orientation-display-${index}`).textContent = orientationSymbols[magnetOrientations[index]];
            redrawCanvas();
        }

        function updateStrength(value) {
            strength = value;
            document.getElementById('slider-strength-value').textContent = value;
            redrawCanvas();
        }

        function updatePlotType(type) {
            plotType = type;
            document.getElementById('btn-stream-plot').classList.toggle('active', type === 'stream');
            document.getElementById('btn-density-plot').classList.toggle('active', type === 'density');
            redrawCanvas();
        }

        document.querySelectorAll('.orientation-control button').forEach(button => {
            button.addEventListener('click', () => {
                const index = parseInt(button.id.split('-')[3]);
                const delta = button.id.includes('up') ? 1 : -1;
                updateOrientation(index, delta);
            });
        });

        document.getElementById('slider-strength').addEventListener('input', event => {
            updateStrength(parseInt(event.target.value));
        });

        document.getElementById('btn-stream-plot').addEventListener('click', () => {
            updatePlotType('stream');
        });

        document.getElementById('btn-density-plot').addEventListener('click', () => {
            updatePlotType('density');
        });
    </script>
</body>
