        let phaseEnvelope = []; // Placeholder for phase envelope points
        let tieLines = []; // Placeholder for tie lines
        let canvas;

        const PA = createVector(400, 50);
        const PB = createVector(100, 700);
        const PC = createVector(700, 700);

        function setup() {
            canvas = createCanvas(800, 800);
            canvas.parent('canvas-container');
            canvas.mouseClicked(handleCanvasClick);
            phaseEnvelope = generatePhaseEnvelope();
            tieLines = generateTieLines();
            updateInfoBoxes(userPoint);
