        function updateInfoBoxes(point) {
            document.getElementById('xa-main').textContent = point.xA.toFixed(2);
            document.getElementById('xb-main').textContent = point.xB.toFixed(2);
            document.getElementById('xc-main').textContent = point.xC.toFixed(2);
            MathJax.typeset();
        }

        function drawTriangle() {
            strokeWeight(2);
            stroke(0);
            fill(255);
            triangle(PA.x, PA.y, PB.x, PB.y, PC.x, PC.y);
            noStroke();
            fill('#008800');
            text('A', PA.x - 10, PA.y + 20);
            fill('#0000FF');
            text('B', PB.x - 10, PB.y + 20);
            fill('#FF0000');
            text('C', PC.x - 10, PC.y + 20);
        }

        function drawAxes() {
            noFill();
            strokeWeight(1);
            stroke(0);
            for (let i = 0.1; i <= 0.9; i += 0.1) {
                let P1 = lerpVector(PA, PB, i);
                let P2 = lerpVector(PA, PC, i);
                let P3 = lerpVector(PB, PC, i);
                line(P1.x, P1.y, P2.x, P2.y);
                line(P1.x, P1.y, P3.x, P3.y);
                line(P2.x, P2.y, P3.x, P3.y);
                fill(0);
                textSize(12);
                textAlign(CENTER, CENTER);
                text(i.toFixed(1), P1.x, P1.y - 5);
                text(i.toFixed(1), P2.x + 5, P2.y + 5);
                text(i.toFixed(1), P3.x - 5, P3.y + 5);
            }
